namespace MenoPreDieta.Data
{
    public class BoyNamesCatalog : IEnumerable<NameEntity>
    {
        private readonly List<NameEntity> list;

        public BoyNamesCatalog()
        {
            list = new List<NameEntity>
            {
                new NameEntity {Value = "Adam", NameDay = "24. december", Description = "Podľa starozákonnej knihy Genessis je Adam prvý človek, muž stvorený Bohom v raji, telo z hliny. Z tohto spojenia je odvodený aj pôvod krstného mena Adam z hebrejčiny, čo v preklade znamená \"človek z hliny, pozemšťan\". Ľahko nájde cestu nahor aj keď je v neviem akej ťažkej situácii. Získa si podporu iných ľudí. Uskutoční svoje sny a plány, nie vždy si vyberie tú správnu partnerku. Všetko mu ide ľahšie, ako iným mužom. Vie sa rýchlo zorientovať aj v neprehľadnej situácii a nevzdáva sa bez boja. Neznáša kritiku a pochybnosti o sebe a svojich výsledkoch. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú jaspis a nefrit."},
                new NameEntity {Value = "Adolf", NameDay = "17. jún", Description = "Mužské meno Adolf má germánsky pôvod a jeho význam je \"ušľachtilý vlk\". Osobným číslom tohto mena je 7 - Tajomnosť. Ich šťastné farby sú zlatá, modrá, hnedá, oranžová. Ochranné rastliny ľubovník, bobkový list a borák, ochranné kamene jantár, topás a olivín. Týchto mužov ovplyvňuje planéta Slnko. Sú dosť nevyrovnaní, panovační a náladoví. Adolfovia radi trávia svoj voľný čas v spoločnosti. Títo muži pôsobia distingovane a vierohodne. Vedia si ľahko získať sympatie ľudí a urobiť nezabudnuteľný dojem. Adolfovia sú prelietaví, majú veľa záľub a nikdy nedokážu robiť iba jednu činnosť. Výbornú majú pamäť a sú zvedaví. Nič nedokončia, do čoho sa pustia. Adolfovia chcú vždy viesť ľudí a radi kritizujú druhých. Ale ak vycítia neoprávnené obvinenie, budú sa snažiť vždy to vyriešiť. Majú dobré myšlienky, majú pozitívny postoj k veciam, teda nebudete ľutovať ak mu pomôžete. Majú úžasnú životaschopnosť a napriek tomu, že vedú nepravidelný život a pracujú nárazovito, zachovávajú si vzácnu rovnováhu. Toto meno je vhodné pre Rakov, Býkov, Levov a Blížencov."},
                new NameEntity {Value = "Adrián", NameDay = "23. marec", Description = "Mužské meno Adrián má latinský pôvod a v preklade znamená \"muž od mora, z talianskeho mesta (H)adrie\". Ovplyvňuje ich planéta Venuša. Ich šťastné farby sú modrá, zelená a biela. Ochranné rastliny nechtík, ligurček, kamene smaragd, zafír. Toto meno má osobné číslo 9 - Génius. Adriánovia majú veľkú trpezlivosť a sú to ľudia s veľkým intelektom. Nikdy nevieme, čo môžeme čakať od týchto mužov. Vedia so svojimi financiami veľmi dobre zaobchádzať. Konajú nepredvídane, nikto nevie, čo môže od nich čakať. Adriánovia sú nestáli, nevedia sa sústrediť iba na jednú vec. Pre nich je najdôležitejšie, aby si splnili svoj cieľ. Niekedy vedia byť veľmi usilovní a úspešní a v niekedy nedokáže zaostávajú. Potrebujú pri sebe silnú osobnosť, aby zrýchlili svoje tempo. Títo muži majú výbornú životaschopnosť a zdravie nezlomné. Mali by si obohatiť jedalníček o vitamín E a B. Toto meno je vhodné pre znamenia : Býk, Rak, Panna, Škorpión."},
                new NameEntity {Value = "Alan", NameDay = "8. marec", Description = "Krstné meno Alan má keltský pôvod a význam mena v preklade je \"súlad, svornosť\". Alanov ovplyvňujú planéty Saturn a Urán. Ich obľúbená farba je tmavomodrá, ochranné rastliny náprstník, rozmarín, ochranné kamene perla a rubín. Nemali by sa správať samoľúbo. Toto meno má číslo 1 - Vodca. Títo muži sa vždy správajú nadšene. Sú stále v pohybe a zároveň pracovití. Nehladia iba na seba, ale pomáhajú aj ostatným. Na Alanov je vždy spoľahnutie a majú v sebe veľa energie, ktorú rozdávajú okolo nich. Sú to dobrí ľudia a snažia sa byť ľuďom oporou. Ak nebudú títo muži dostatočne silní, budú nespoľahliví a nebudú obozretní. Nič nemajú potom naplánované a celú prácu si nechávajú na poslednú chvíľu. Sú to šarmantní muži, čo vedia aj zneužiť pre svoje dobro. Budú sa pretvarovať, ak by im to malo pomôcť v ich kariére. Toto meno je teda vhodné pre silné znamenia : Baran, Lev, Býk, Kozorožec a Škorpión."},
                new NameEntity {Value = "Albert", NameDay = "8. apríl", Description = "Mužské meno Albert má germánsky pôvod, je ojedinelé a vo voľnom preklade znamená \"vznešený\". Jeho osobným číslom je 7 – Tajomnosť. Albertovia majú ťažkú povahu. Raz sú takí a raz iní. Nechajú sa ľahko ovplyvniť a na druhej strane vedia byť tvrdohlaví. Ako deti sú veľmi zlatí a jemní, ale stále niečo musia tajiť. Nikto nevie, čo môže od tohto človeka čakať, ako bude reagovať. Keďže všetko tají pred ostatnými, stáva sa z neho introvert a to mu nepomáha dosiahnuť rýchlo úspech. Toto meno priťahuje k sebe dosť chorôb, je teda dosť riskantné. Vhodné je pre znamenia : Vodnár, Býk, Lev, Panna, Škorpión a vhodnou farbou je modrá, zelená a čierna. Ochranné rastliny medovka, baza a divozel, ochranné kamene ametyst, perla a achát."},
                new NameEntity {Value = "Albín", NameDay = "1. marec", Description = "Mužské meno Albín má latinský pôvod a v preklade znamená \"biely\". Ich šťastné farby sú čierna a tmavomodrá. Ochranné rastliny medovka a nezábudka, ochranné kamene ametyst a jaspis. Jeho osobným číslom je 6 – Nežnosť. Aj napriek svojmu osobnému číslu majú títo muži v sebe dosť strnulosti. Ale ináč Albínovia sú milí a občas tajuplní. O svojich súkromných veciach veľa nehovoria, radšej si ich nechávajú pre seba. Títo muži vždy vedia, čo chcú, i keď majú niekedy so sebou problémy. Ak nebudú dosahovať úspechy, budú smutní a sťažovať sa. Ale ak využijú svoje príležitosti a budú tvrdo bojovať, budú mať výborne výsledky. Toto meno je vhodné pre znamenia : Vodnár, Strelec, Baran, Býk a Blíženci."},
                new NameEntity {Value = "Aleš", NameDay = "13. apríl", Description = "Mužské meno Aleš je českou domácou podobou Alexej, ktoré je gréckeho pôvodu a znamená \"obranca\". Osobným číslom tohto mena je 7 – Tajomnosť. Alešovia keď chcú, dokážu byť tvrdí ľudia, ale vedia byť aj citliví. Nie sú nároční, ale vedia ľudí šokovať. Veľa krát sa správajú na základe podrazov. Čo sa týka ich pamäti, je výborna a môžu sa na ňu spoľahnúť. Preto to občas využívajú a chvíľami sú dosť zvedaví. V oblasti práce majú Alešovia celkom úspech, ktorý však nemusí byť stály. Preto je to u týchto mužov dosť kolísavé. Toto meno je vhodné pre znamenia : Baran, Vodnár, Strelec, Blíženci a vhodnou farbou je žltá. Ich šťastné farby sú ružová a červená. Ochranné rastliny cesnak, chmeľ, horčica, ochranné kamene chalcedón a karneol."},
                new NameEntity {Value = "Alexander", NameDay = "27. február", Description = "Krstné meno Alexander má grécky pôvod a v preklade jeho význam znamená \"obranca mužov\". Jeho osobným číslom je 7 - Tajomnosť.Ich šťastné farby sú biela, zelená, modrá a strieborná. Ochranné rastliny rumanček, rozmarín, ochranné kamene perla a rubín. Alexandrovia bývajú aktívny, atraktívni, aj keď ich schopnosť myslenia je často založená na intrigách. Na ich pamäť sa dá spoľahnúť. Ich myšlienky a city sa dajú ťažko rozpoznať, nepovie o nich ani svojim blízkym, bývajú uzavretí. Život Alexandrov je založený na hľadaní. Často vycíti, čo sa môže udiať. Keď nenájde to, po čom túžil, stáva sa zatrpknutým a sebeckým mužom, ktorý sa nakoniec o všetko stratí záujem. Ako deti by ich rodičia mali mať dostatočný dohľad nad nimi a nemali by ich viesť k pyšnému správaniu. V citovom živote na jednej strane chcú byť voľní, ale na druhej strane hľadajú lásku a podporu. Alexandrovia sú úprimní muži, preto sa netreba báť o ich city – povedia, čo si myslia. Ich zdraviu škodí dosť jedlo s veľkým obsahom cukru. Toto meno je vhodné pre znamenia : Rak, Panna, Vodnár, Váhy."},
                new NameEntity {Value = "Alexej", NameDay = "9. január", Description = "Mužské meno Alexej je ruské meno gréckeho pôvodu a v preklade znamená \"obranca\". Osobným číslom tohto mena je 8 – Peniaze. Ich obľúbené farby sú biela, zelená, hnedá a strieborná, ochranná rastlina rumanček, ochranný kameň nefrit. Títo muži patria skôr k ostýchavým ľuďom. Majú občas obavy zo svojho správania a hlavne z úsudku ostatných. Nechajú sa ľahko ovplyvniť druhými a nepovedia si svoj názor. Ale na druhej strane sú veľmi pracovitými. K tomu však potrebujú spolupracovníkov, aby dosiahli úspech. Dokážu v práci druhým pomôcť a správne ich aj ohodnotiť. Alexejovia sú vždy zanietení do práce a stále niečo riešia, čo nevplýva na nich dobre. Oni od vás budú vždy žiadať veľa, no aj veľa od nich dostanete. Alexejovia si vždy idú za svojimi plánmi a cieľmi a len veľké sklamanie ho môže spomaliť. Títo muži sa od malička radšej sami učili. Orientujú sa často na oblasť umenia. Alexejovia majú premenlivé zdravie. Mávajú problémy so žalúdkom. Preto by si mali vyhýbať a dávať pozor na mastné a nezdravé jedlá. Vhodné je pre znamenia Býka, Kozorožca, Panny a Raka."},
                new NameEntity {Value = "Alfonz", NameDay = "28. január", Description = "Mužské krstné meno Alfonz má nemecký pôvod a v preklade znamená \"pripravený na boj\". Obľúbené farby Alfonzov sú zlatožltá, modrá a medená. Ochranné rastliny sú mak a nechtík, ochranné kamene jantár a olivín. Osobným číslom tohto mena je 1 - Vodca. Títo muži sú stále v pohybe. Alfonzovia sú milí, šarmantní, plní energie a nad ťažkosťami sa netrápia dlho. Radi trávia svoj čas v spoločnosti medzi ľuďmi. No niekedy žijú rozdvojení život. Jedna časť ich osobnosti je otvorená a druhá osobná. Sebadôvera týchto mužov je dosť vysoká, čo im niekedy neprospieva. Ich problémom je, že nevedia byť občas nestranní. Keďže Alfonzovia sú veľmi aktívni ľudia, sa veľmi chcú prepracovať k úspechu. No málokto vie využiť svoj talent. Už odmalička vedia, v akej oblasti chcú byť zamestnaní. V práci sú veľmi usilovní a vždy si plnia svoje povinnosti. Alfonzovia sú kreatívni muži, preto sa často z nich stávajú bádatelia, výskumníci, projektanti. Ich zdravie je priemerné. Ale ak budú si dávať pozor na svoj tráviaci systém, budú mať zdravie v poriadku. Toto meno v spojení znamení Váh, Strelca alebo Ryby zapríčiní, že Alfonzovia nič príznačné a stále nedosiahnu, kvôli ich ľahkomyseľnosti a neposednosti. Preto by sa malo toto meno skombinovať so silným znamením - Baranom, Býkom, Pannou, alebo Kozorožcom."},
                new NameEntity {Value = "Alfréd", NameDay = "19. jún", Description = "Mužské meno Alfréd má anglický pôvod a jeho význam je \"múdry radca\". Jeho osobným číslom je 2 – Dobrota. Ovplyvňuje ich planéta Merkúr. Ich šťastné farby sú biela, modrá a zelená. Ochranné rastliny podbeľ, levanduľa, ochranné kamene tigrie oko a citrín. Alfrédovia sú citlivými mužmi a pôsobia navonok ako uzavretí a hanbliví. Dá sa na neho spoľahnúť a v spoločnosti sa vedia zdvorilo správať. Ak niekto potrebuje pomôcť, sú vždy ochotní. Alfrédovia sú milí a úprimní ľudia. Nemali by sa konať proti svojmu vnútru. Vo vzdelávaní sú veľmi zodpovední, aj keď svojím spôsobom. V krízových situáciach si vedia vždy nájsť cestu ako vyriešiť nejaký problém. Sú pripravení aj na najhoršie situácie. Títo muži si vždy idú za svojím cieľom a nakoniec ho aj dosiahnú, ale až keď sa poučia na svojích chybách. Čo sa týka ich zdravia, býva v celku prijateľné. Ich fyzická únava vychádza z ich lenivosti. Bude lepšie ak sa budú stravovať skôr pravidelnejšie a mali by konzumovať viac ovocia a kyslomliečne výrobky. Toto meno je vhodné pre znamenia : Váhy, Kozorožec, Panna a Vodnár."},
                new NameEntity {Value = "Alojz", NameDay = "21. jún", Description = "Mužské meno Alojz sa k nám dostalo zo starej francúzštiny a označuje slávneho bojovníka. Osobným číslom tohto mena je 4 – Sila. Ovplyvňuje ich planéta Mesiac. Ich šťastné farby sú biela, svetlozelená, modrá. Ochranné rastliny rumanček, jazmín, ochranné kamene nefrit a perla. Títo muži majú radi, keď trávia svoj čas v spoločnosti. Stále majú niečo na práci a neustále nad niečím rozmýšľajú. Ale ich slabou stránkou je niečo dokončiť. Vždy vie, ako a čo má robiť a hlavne ako získať iných na svoju stranu. Majú dobré reakcie a sú energickí. Dobre vedia využiť svoju predtuchu. Niektorí ľudia s týmito mužmi si nerozumejú. Alojzovia nemajú radi, ak sa majú deliť o svoj úspech. Sú občas lakomí, ale nemusí to byť za každých okolností, dokáže šetriť, ale iba po nejaké hranice. Sú prebojnými mužmi, idú si pevne za svojim cieľom aj prekážky vedia dobre zdolávať. Ich životaschopnosť nie je vždy dobrá. Sú precízni pri stravovaní a to ich brzdí v ich životnej sile. Zdravie majú celkom dobré, ale aby bolo symetrické, mali by mať pokojnejší život. Toto meno je teda dobré, prijateľné pre každé znamenie zverokruhu."},
                new NameEntity {Value = "Ambróz", NameDay = "7. december", Description = "Mužské meno Ambróz má grécky pôvod a v preklade znamená \"nesmrteľný\", \"nebeský\". Nie vždy má geniálne myšlienky. Občas šokuje svoje okolie, no zväčša svoju genialitu používa nesprávne. Často mení názory. Má rád spoločnosť ľudí a vyhľadáva dobrodružstvá. Nedá sa na neho spoľahnúť, ani ho brať vážne. Nikomu nie je jasné, aký bude jeho ďalší krok a najmenej on sám vie povedať niečo viac o svojich zámeroch. Je to človek, ktorý ak nemá pri sebe dobrého priateľa, svoj život prežije len v záhaľke a zábavách. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene sú ametyst a achát."},
                new NameEntity {Value = "Andrej", NameDay = "30. november", Description = "Mužské mená Ondrej a Andrej majú grécky pôvod a v preklade znamenajú \"statný\", \"mužný\" \"odvážny\". Zdajú sa byť trochu leniví, kým oni iba čakajú na vhodnú chvíľu. Ak sa do niečoho pustia, rýchlo dosiahnu úspech. Milujú dobrodružstvá a sú nezávislí. Sú roztržití a chodia poza školu, hlavne ak sa im nepáči nejaký profesor. Láka ich technika, aj keď niektorí inklinujú k umeniu. Sú citovo založení a túžia po tom, aby ich ľudia mali radi a aby im to aj dokazovali. Nemajú radi samotu, a preto často organizujú večierky. Sú priateľskí voči mužom aj ženám. Majú šťastie naklonené na svojej strane, no oni si to len málo všímajú. Vyrazia do života, kráčajú ním a väčšinou im zázračne všetko vychádza. Ich ochranný kameň je achát."},
                new NameEntity {Value = "Anton", NameDay = "13. jún", Description = "Mužské meno Anton má latinský pôvod a jeho význam je \"popredný\", \"vynikajúci\". Jeho osobným číslom je 6 – Nežnosť. Obľúbené farby Antonov sú hnedá, čierna, žltá a tmavomodrá. Ochranné rastliny kostihoj, kokorník, ochranné kamene granát a achát. Antonovia sú plní energie a sú to emotívni muži. V živote nemajú problém vyriešiť si záležitosti, aj keď to nemajú v obľube. Takže najprv si dobre zvážia do čoho sa púšťajú. Antonovia nemajú radi reálny svet, v ktorom by mali žiť. Títo muži majú pevnú vôlu a vedú seriózny život. Majú radi komunikáciu s ľuďmi v úzkom okruhu. Bývajú zlý a podráždení, ak nemajú okolo seba dobre prostredie. Zaujímajú sa o štúdium filozofie, psychológie a medicíny. Antonovia nebývajú často unavení a sú plní vitálnosti. Hlavne čo potrebujú je dostatočný spánok a čerstvý vzduch. Ich slabou stránkou je zrak. Mali by predovšetkým dbať na duševnú rovnováhu. Toto meno je vhodné pre znamenia : Kozorožec, Lev, Strelec, Panna a Rak"},
                new NameEntity {Value = "Arnold", NameDay = "1. október", Description = "Mužské meno Arnold má nemecký pôvod a vo voľnom preklade jeho význam je \"orlí vládca, silný ako orol\". Na Arnolda nikdy nezabudnete. Spočiatku vás nadchne, podporujete ho, pomáhate mu, požičiavate mu a tešíte sa z jeho pozornosti. Zúčtovania, alebo vrátenia svojej pôžičky, či presných odpovedí sa nedočkáte. Prekvapí vás - nič nedostanete späť a zistíte, že ste boli šikovne oklamaní. Arnoldovi je manipulovanie, klamanie a zavádzanie vlastné. Má to v sebe od narodenia. Je rodeným obchodníkom s ilúziami a dokáže presvedčiť kohokoľvek o čomkoľvek. Ťažko mu uniknete. Ak si vás vyberie za svoju obeť a keď vás celkom vyžmýka, bez výčitiek vás nechá na dlažbe. On sa nikdy nedostane do ťažkostí, vždy si vie poradiť a o svojich blízkych sa vždy vzorne postará. Ovplyvňuje ho planéta Saturn. Jeho ochranné kamene sú zafír a opál."},
                new NameEntity {Value = "Arpád", NameDay = "13. február", Description = "Arpád je meno maďarského kráľovského rodu a v preklade znamená \"jačmeň\". Obľúbené farby týchto mužov sú čierna, zelená a modrá. Ochranné rastliny medovka, zvonček a modrý hyacint, ochranné kamene ametyst, zirkón a sokolie oko. Osobným číslom tohto mena je 8 - Peniaze. Sú presní ľudia s veľkou inteligenciou. Všetko čo do čoho sa pustia, urobia správne. Žijú vo svojom svete a keď v niečo veria, dokážu pre to všetko obetovať. Ako chlapci majú radi obyčajné štúdium, ale je im blízka aj matematika a cudzie jazyky. Arpádovia sa dokážu presadiť v práve a v oblasti médii. I keď síce pomaly, ale isto sa dokážu presadiť a zabezpečiť si finančné prostriedky pre seba a hlavne svoju rodinu. Sú nežní a pre lásku dokážu obetovať všetko, ale nenechajú sa zlomiť. Nemali by mať ženu, ktorá sa bude snažiť byť lepšia ako on, ale takú partnerku, čo si ich bude vážiť a dokáže im pomôcť. Ak ich partnerka zradí, dokážu sa neskutočne trápiť.V oblasti zdravia by sa mali zamerať na správne stravovanie a na dostatočný a pokojný spánok. Ich citlivým miestom je sluch. Toto meno je vhodné pre znamenia Býk, Blíženci, Rak, Panna, Kozorožec."},
                new NameEntity {Value = "Augustín", NameDay = "28. august", Description = "Mužské meno Augustín má latinský pôvod a v preklade znamená \"majestátny\". Osobným číslom je 8 – Peniaze. Ovplyvňuje ich planéta Mars. Šťastné farby sú červená a fialová. Ochranné rastliny púpava, chmeľ a horčica, ochranné kamene diamant a ametyst. Títo muži sú odvážni, rozhodní a oddaní. Vedia pochopiť myšlienky a pocity druhých ľudí. Neboja sa vzdelávať po celý život. Ako budúce zamestnanie preferujú medicínu. Ale môžu byť aj dobrými biznismenmi. Majú výbornú pamäť, ktorú patrične využívajú. Zapamätajú si ľudí, ktorí im pomohli, ale aj tých, ktorí ich potopili. Nikomu nechcú zostať nič dlžní. Vždy si stoja za svojim slovom. Neúspech neberú ako prehru. Radi chodia do spoločnosti.V oblasti zdravia sú odolní. Majú veľkú vitalitu. Toto meno je vhodné pre znamenia: Baran, Býk, Lev a Panna."},
                new NameEntity {Value = "Aurel", NameDay = "25. október", Description = "Mužské meno Aurel má latinský pôvod a v preklade znamená \"zlatý\", \"zlatučký\". Nevie byť nikomu oporou a nemá silný charakter. Vždy je jemný, milý, ovládateľný a zneužívaný. Jeho povaha mu prekáža v snahách stať sa skutočným mužom. Chce dosiahnuť vysoký cieľ, ale nikdy sa mu to celkom nepodarí. Sklamania, nedôvera okolia v jeho schopnosti, jeho komplexy menejcennosti a podceňovanie samého seba sa podpíšu pod jeho neúspechy. Až keď si uvedomí, že nie je a nikdy nebude taký istý, ako ostatní jeho známi, potom dokáže využívať svoje sily na to, aby dosiahol úspech - po svojom a nie podľa očakávaní iných. Ovplyvňuje ho planéta Slnko. Jeho ochranné kamene sú jantár, topás a olivín."},
                new NameEntity {Value = "Bartolomej", NameDay = "24. august", Description = "Bartolomej je novozákonné meno. Má hebrejský pôvod a v preklade znamená \"Talmajov syn\". Osobným číslom tohto mena je 9 – Génius. Ovplyvňuje ich Slnko. Šťastné farby sú žltá a medená. Ochranné rastliny nechtík a rumanček, ochranné kamene granát a jantár. Títo muži sú silné osobnosti. Majú vysoké nároky na seba aj ostatných. Neboja sa povedať otvorene, čo si myslia. Oplývajú vysokou sebadôverou. Ich školské roky sú väčšinou búrlivé, pretože majú veľké problémy s neposednosťou. V spoločnosti sú veľmi obľúbení a žiadaní. Ľudia ich radi vyhľadávajú. Zdravie majú celkom dobré. Len by si mali dávať pozor na ich stravovacie návyky. Toto meno je vhodné pre akékoľvek znamenie."},
                new NameEntity {Value = "Belo", NameDay = "3. september", Description = "Mužské meno Belo je odvodené z mena Vojtech, ktoré má slovanský pôvod a v preklade znamená \"tešiteľ vojska\". Má pozoruhodne rýchle reakcie a je to muž činu. Nohami stojí pevne na zemi, hlavu však má stále v oblakoch. Zvyčajne si vyberá povolania, ktoré mu umožňujú byť v styku s realitou a kde vidí plody svojho úsilia. Rád sa oddáva pôžitkárskemu spôsobu života a má dosť sebecký pohľad na svet. Má vlastnícke správanie. Túži byť pochopený, a to oveľa viac, ako on chápe iných. Jeho zdravie je väčšinou znamenité a je veľmi vitálny. Ovplyvňuje ho planéta Saturn. Ochranné kamene sú achát, zafír a opál."},
                new NameEntity {Value = "Beňadik", NameDay = "22. marec", Description = "Mužské meno Beňadik má latinský pôvod a význam mena je \"požehnaný\". Ovplyvňuje ich planéta Saturn. Ich šťastné farby sú hnedá, zelená a žltá. Ochranné rastliny kostihoj, kokorík, kamene zafír, achát. Osobným číslom je 2 - Dobrota. Beňadikovia majú nevyrovnanú osobnosť. Buď sú veľmi citliví alebo veľmi aktívni s veľkou pohotovosťou. Títo muži milujú pocit slobody a majú veľmi dobrú schopnosť predvídať. Nie sú založení materialisticky ale skôr duchovne. Kreatívnosť a intuícia je ich silná stránka. Ak nebude mať dobré rodinné zázemie, je možné, že sa z nich stanú nespokojní a konfliktní ľudia. Bude teda stále musieť niečo obetovať, aby dosiahol pochopenie a pomoc. Ich zdravie je celkom v poriadku. Jediné čo ich môže trápiť je psychická záťaž, ale telesnej únave vedia dobre odolávať. Toto meno je dobré spojiť s hociakým znamením, ktoré pomôže tomuto menu zvládať ťažké situácie."},
                new NameEntity {Value = "Benjamín", NameDay = "31. marec", Description = "Mužské meno Benjamín je starozákonné, má hebrejský pôvod a jeho význam je \"milovaný syn\". Ovplyvňuje ich planéta Mars. Ich šťastné farby sú zelená, červená. Ochranné rastliny púpava, rozmarín a divá čerešňa, ochranné kamene perla a rubín. Osobným číslom tohto mena je 5 – Riziko. Benjamínovia nevedia nájsť pokoj vo svojom živote. Sú to ale muži, ktorí za každých okolností budú bojovať. Sú citliví, ale ak im niekto ublíži, sú schopní sa dobre pomstiť. Nedokážu sa na veci pozerať objektívne, preto sa často dostanú so svojimi názormi do konfliktu s ostatnými, ale aj napriek tomu vedia s takými ľuďmi spolupracovať. Ich aktívnosť je striedavá, raz sú nenápadní a niekedy zas majú chuť za všetko bojovať. Títo muži majú dobrú intuíciu, ktorú vedia využiť. To im dopomáha si vybrať správny smer a dosiahnuť všetko, po čom túžia. Toto meno je vhodné pre znamenia : Ryby, Škorpión, Strelec, Panna, Býk."},
                new NameEntity {Value = "Bernard", NameDay = "20. máj", Description = "Mužské meno Bernard má nemecký pôvod a význam mena je \"silný medveď\". Ovplyvňuje ich planéta Mars. Ich šťastné farby sú červená, modrá a fialová. Ochranné rastliny púpava, chmeľ, ochranné kamene diamant a ametyst. Toto meno má osobné číslo 3 – Schopnosť. Títo muži od detstva bývajú veľmi tvrdí. Preto by sa im mala ukazovať jemnosť, ale nemali by sa rozmaznávať. Bernardovia si za svojmi názormi pevne stoja, nedajú sa ovplyvniť, iba ak by išlo o silné argumenty. V práci majú šťastie, vždy sa im všetko vydarí, do čoho sa pustia. Ak od malička sú šetrní a dlhšie im to vydrží, budú dobre zabezpečení. Vedia so svojimi peňažnými úspormi dobre zaobchádzať. V zamestnaní majú najlepšie predpoklady na vedúcich pracovníkov. Obyčajne sa z nich stávajú politici alebo diplomati, lebo majú radi, keď sa môžu pred ostatnými predviesť. Ľudia v ich blízkosti by sa ich mali držať lebo Bernardovia sa radi podelia s ostatnými. Na svoje zdravie by si mali títo muži dávať pozor, aj keď ich vitalita je výborná. Niekedy to vedia zneužiť a preto nehladia na svoje zdravie. Je vhodné pre znamenia : Baran, Blíženci, Panna, Škorpión a Vodnár."},
                new NameEntity {Value = "Blahoslav", NameDay = "21. marec", Description = "Mužské meno Blahoslav má slovanský pôvod a význam mena je \"blahoslavený\". Ovplyvňujú ich planéty Saturn a Urán. Šťastné farby Blahoslavov sú tmavomodrá, čierna a sivá. Ochranné rastliny medovka a baza, kamene achát a sokolie oko. Osobným číslom je 6 – Nežnosť. Títo muži musia mať vždy svoje myšlienky usporiadané. Blahoslavovia nie sú ovplyvniteľní ľudia a nikto ich nemôže presvedčiť o opaku, o ktorom sú presvedčení. Nebývajú nadšení, ak majú tráviť svoj čas v spoločnosti. No niekedy potrebujú, aby ich niekto vypočul a ohodnotil ich úsilie. Ich slabosťou je, že sa nedokážu naplno sústrediť na veci a venovať sa jednej činnosti. Blahoslavovia by si mali nájsť nejakú aktivitu a prácu, v ktorej budú vynikať a bude ich baviť. Toto meno je dosť riskantné a mali by sme ho dávať len osobám, narodeným v silných znameniach : Baran, Býk, Lev, Škorpión."},
                new NameEntity {Value = "Blažej", NameDay = "3. február", Description = "Mužské meno Blažej je má grécky pôvod, je svätecké a v preklade znamená \"nezrozumiteľne hovoriaci\". Osobným číslom tohto mena je 4 - Sila. Obľúbené farby Blažejov sú zlatá, zelená a žltá. Ochranné rastliny majú ľubovník a mak, ochranné kamene jantár a olivín. Blažejovia vedia, kde majú svoje hranice, ako sa správať, aby dosiahli po čom túžia aj keď dokážu byť rozrušení a ostýchaví ľudia. Stoja pevne nohami na zemi, dokážu sa prispôsobiť iným a byť ochotní a tolerantní. Vyberá si postupne okruh ľudí, i keď naozaj blízkych ľudí si určí až po dlhom čase a to do konca života. Blažej sa správa ako otvorený a úprimný človek, veselý spoločník a dobrý kamarát. Je trpezlivý a tak si hľadá aj lásku. Trvá mu dlhší čas, kým nájde tu správnu a je nerozvážny ukázať niekomu svoju citovú stránku. Nositelia tohto mena majú určenú skôr manuálnu prácu ako remeselník, i keď niektorí si nájdu svoju oblasť skôr ako právnici, učitelia alebo novinári. Títo muži sú náchylní na nervový systém a na vírusy, ale dokážu dobre odolať pocitu únavy. Toto meno je vhodné pre znamenia Býka, Váh, Strelec a Baran."},
                new NameEntity {Value = "Bohdan", NameDay = "21. december", Description = "Mužské meno Bohdan má slovanský pôvod a znamená \"dar boží, daný Bohom\". Nie je nejaká cintľavka, alebo citlivka. Ale vie používať aj svoju citovú stránku a pomáha iným, aj keď ho o to nepožiadajú. Má jemný a nevtieravý spôsob komunikácie a získava ním veľa priateľov. Môže sa spoľahnúť na každého, kto je dlhší čas v jeho blízkosti. Je veľmi obľúbeným spoločníkom, kamarátom a partnerom. Je výhodné žiť s ním a pomáhať mu, ak je to potrebné. Nie je neverný, ani neintriguje. Potrebuje pevnejšie vedenie a podporu, aby sa nepoddal lenivosti. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú citrín a zafír."},
                new NameEntity {Value = "Bohumil", NameDay = "3. marec", Description = "Krstné meno Bohumil má slovanský pôvod a ich význam je \"milý bohu, milá bohu\". Bohumilove šťastné farby sú čierna, fialová, hnedá a zelená. Ochranné rastliny kostihoj, myší chvost, ochranné kamene achát a granát. Osobným číslom pre toto meno je 2 - Dobrota Títo muži sú veľmi opatrní a jednoduchí ľudia. Pri svojich záležitostiach sa rýchlo rozhodnú, nikdy si nič neplánujú dlho. Bohumilovia sú dobrí spoločníci a majú radi návštevy. Svoju prácu nikdy nerobia len kvôli peniazom, snažia sa pomôcť a aby aj druhí mali dobre prostredie v zamestnaní a aby sa mali dobre. Prospeje im, ak budú svoju stravu doplňovať o zeleninu, ryby a potraviny zo sóje. Toto meno je vhodné pre znamenia : Strelec, Vodnár, Lev, Kozorožec, Panna. Bohumil aj Bohumila sú introverti. Sú objektívni, náramne struční, veľmi opatrní a niekedy aj úzkostliví. Ich vôľa je striedavá. Ak je všetko v poriadku, je pevná, ak sa veci skomplikujú, oslabená. Nositelia týchto mien sú spoločenskí. Radi prijímajú návštevy a milujú priateľské posedenia. Úprimne túžia byť užitoční a pomáhať iným. Mali by viac dôverovať ľuďom, jesť veľa zeleniny, rýb a výrobkov zo sóje."},
                new NameEntity {Value = "Bohumír", NameDay = "8. november", Description = "Mužské meno Bohumír má slovanský pôvod a znamená \"Bohu mier\", \"zmieruj sa s Bohom\". Má v živote vždy jasný cieľ a málokedy odbočí z priamej cesty. City nie sú pre neho nejako veľmi dôležité, aj keď ku svojim deťom je vždy štedrý. Pokiaľ ide o vzťahy k ľuďom, presne vie, čo vyžaduje a kto mu môže pomôcť v jeho snažení. Dôkladne si vyberá spolupracovníkov a dovedie ich vždy k úspechu, aj keď on sám neznáša priveľkú pozornosť. Je typickou \"šedou eminenciou\", človekom v pozadí, bez ktorého by však nemohlo existovať ani jedno úspešné podujatie. Ovplyvňuje ho planéta Slnko. Jeho ochranné kamene sú krištáľ, granát a ónyx."},
                new NameEntity {Value = "Bohuš", NameDay = "27. január", Description = "Mužské meno Bohuš je domácou podobou mena Bohuslav, ktoré je slovanského pôvodu a v preklade znamená \"Bohu sláva\". Ovplyvňuje ich planéta Mesiac. Ich šťastné farby sú fialová, červená,žltá, modrá. Ochranné rastliny žihľava, rimbaba, ochranné kamene ametyst a tyrkys. Osobným číslom tohto mena je 8 - Peniaze. Bohušovia vo svojom živote veľa bojujú, aby niečo dosiahli. Úspech vždy dosiahnu, aj keď to bude trošku zložitejšie, ale ak sa nezdajú, budú mať šťastie. Medzi ľuďmi sú títo muži obľúbení a populárni, ale niektorí to zneužívajú a nepomôžu Bohušom, keď to potrebujú. Ich vôľa nie je niekedy pevná na to, aby splnili každú úlohu, ktorú chcú dosiahnuť. Sú silnými osobnosťami, preto ak sa budú veľmi snažiť, nakoniec sa vypracujú vysoko. Ak budú trpezliví, ich usilovnosť bude ocenená. Ak budú Bohušovia zaťažení a nebudú mať vyrovnanú psychiku, ich zdravie nebude uspokojivé. Vhodné je pre znamenia Panna, Kozorožec, Vodnár a Býk."},
                new NameEntity {Value = "Bohuslav", NameDay = "17. júl", Description = "Mužské meno Bohuslav má slovanský pôvod a jeho význam je \"Bohu sláva\", \"Boha oslavuj\". Osobné číslo tohto mena je 8 – Peniaze. Ovplyvňuje ich Mesiac. Šťastné farby sú fialová, žltá, modrá. Ochranné rastliny žihľava, rimbaba, ochranné kamene ametyst a tyrkys. Títo muži sú plní dôvtipu a inteligencie, ale chýba im vytrvalosť. Preto nemajú veľmi ľahký život. Musia prekonávať mnoho prekážok a problémov, kým sa dostanú k tomu, po čom túžili. V škole sa cítia dobre, preto sa z nich stávajú aj dobrí odborníci. Majú zmysel pre profesionalitu a zodpovednosť. V spoločnosti bývajú uvoľnení, pretože sú slušne vychovaní. Sú plní nápadov a plánov v spojení so silnými znameniami, no pri slabých znameniach dochádza k problémom a ťažkostiam. Zdravie majú dobré, lenže by si mali dávať pozor na psychiku. Toto meno je vhodné znamenia: Baran, Býk, Lev, Panna."},
                new NameEntity {Value = "Boleslav", NameDay = "16. marec", Description = "Mužské meno Boleslav má slovanský pôvod a význam mena je \"viac slávy\", \"slávny\". Ovplyvňuje ich planéta Mesiac. Šťastná farba Slávkov je červená, ochranné rastliny buxus, červený klinček, ochranný kameň rubín. Jeho osobným číslom je 1 - Vodca. Muži s týmto menom sú dosť aktívni a zároveň citliví. Sú to silné osobnosti, vždy vedia správne reagovať na dané situácie. Boleslavovia sú celkom tvrdohlaví, nikto ich nepresvedčí o opaku. Ak máte strach z riskovania alebo bojovať o niečo, hľadajte pri sebe takého človeka ako je Boleslav. Oni vás podporia a budú vám pomáhať, keď to budete potrebovať. Títo muži nemajú radi kritiku od druhých alebo iní názor, preto ak s nimi nesúhlasite, vyhýbajte sa im. Odmalička sa Boleslavovia vzdelávajú najradšej sami a ťažko sa zameriavajú na jednu oblasť zamestnania. Vyberajú si takú prácu, kde môžu viesť kolektív oni, lebo nikdy sa nevzdávajú a radi riskujú. Ak sa im v kariére práve nedarí, vôbec ich to neodradí, práve naopak budú silnejšie motivovaní. V zdraví môžu mať problémy s nervovou sústavou alebo s očami. Vhodnými znameniami pre toto meno sú : Baran, Blíženci, Škorpión a Strelec."},
                new NameEntity {Value = "Bonifác", NameDay = "14. máj", Description = "Mužské meno Bonifác má latinský pôvod a význam mena je \"človek dobrého osudu\". Ich osobné je číslo - 6 – Nežnosť. Ovplyvňuje ich planéta Merkúr. Ich obľúbené farby sú červená, modrá a biela. Ochranné rastliny podbeľ, kôpor, ochranné kamene tigrie oko a topás. Rastlinný totem majú citrón. Títo muži sú veľmi milí a zdvorilí. Aj keď radšej žijú vo svojom svete a sú tajuplní, sú starostlivými mužmi. Bonifácovia sa svojich príležitosti celkom boja. Nemajú radi, keď sú v spoločnosti, radšej sa trávia nenápadne. Vedia byť tiež neistí a nesmelí. Tito ľudia veľa neriskujú , radšej budú v pozadí a nebudú veľa investovať. No v súkromnom živote radi vedú ostatných a snažia sa, aby boli aspoň príbuzným autoritou. Ak budú Bonifácovia pravidelne jesť, nebude ich trápiť žiadna zdravotný problém. Mali by jesť veľa ovocia, cibule a chrenu. Toto meno je vhodné pre znamenia : Blíženci, Baran, Strelec, Lev, pretože tieto ho dokážu viac dostať do popredia a podporia v ňom chuť do boja a riskovať."},
                new NameEntity {Value = "Boris", NameDay = "14. október", Description = "Mužské meno Boris má ruský pôvod a je odvodené z mongolského slova bogori, ktoré znamená \"malý\". Má v sebe iskru a chuť do boja s akýmkoľvek problémom. Potrebuje stálu aktivitu a rýchly spôsob života. Nevyhovujú mu žiadne istoty, pokoj, pohoda alebo normálny rodinný život. On musí stále niečo robiť, riskovať a budovať, aby mal pocit, že žije. Občas si oddýchne a vtedy sa mení na milého, príjemného a šarmantného spoločníka. Vie si urobiť čas pre svojich blízkych a vypočuť si ich problémy. Pôsobí tak, akoby v ňom boli spojené dve osobnosti, ktoré každého mätú. Na neho neplatia obvyklé pravidlá. Všetko si robí po svojom a nakoniec sa stane uznávaným a bohatým. Napriek minimálnemu pochopeniu jeho činov sa s ním oplatí žiť a starať sa o neho. Nikdy nezabudne na to, čo mu človek urobí - či už dobré, alebo zlé. Ovplyvňuje ho planéta Mars. Jeho ochranné kamene sú diamant, ametyst a jaspis."},
                new NameEntity {Value = "Branislav", NameDay = "10. marec", Description = "Mužské meno Branislav má slovanský pôvod a význam mena je \"bráň slávu\". Ovplyvňuje ich planéta Mars. Šťastné farby sú ružová, hnedá a červená. Ochranné rastliny cesnak a chmeľ, ochranné kamene chalcedón a karneol. Toto meno má osobné číslo 4 - Sila. Nositelia tohto mena sa držia svojho presvedčenia a konajú s rozumom. Branislavovia sú obľúbení medzi ľuďmi. Sú to bojovníci, ktorí vždy vedia konať a odolávať nepríjemnostiam. Títo muži vedia vždy šokovať so svojim správaním a keď im to vyhovuje, vedia aj ostatných podraziť. Nikdy nie sú ohľaduplní, vždy povedia úprimne čo si myslia, aj keby to malo niekomu ublížiť. Branislavovia sú už od malička inteligentní s výbornou pamäťou. V zamestnaní sú vytrvalí a plní sily a keď budú trpezliví dopracujú sa postupne k úspechu. V zdraví môže týchto mužov trápiť bolesť hlavy a menšia psychická nevyrovnanosť. Prospeje im, ak budú chodiť zavčasu spať a nebudú jesť sladké jedlá. Toto meno je vhodné pre znamenia : Škorpión, Panna, Býk."},
                new NameEntity {Value = "Bruno", NameDay = "10. marec", Description = "Mužské meno Bruno je svätecké, má nemecký pôvod a význam mena je \"hnedý vládca\". Ovplyvňuje ich Mesiac. Ich obľúbená farba je strieborná a modrá. Ochranné rastliny rumanček, zvonček, ochranné kamene nefrit, perla. Osobným číslom je 4 – Sila. Brunovia radi fantazírujú a neprijímajú realitu. Vždy premýšľajú ako si veci vyriešia a naplánujú. Títo muži sú dosť plachí a nechajú sa od druhých ovplyvniť a zneužívať. Vždy vopred rozmýšľajú ako sa ospravedlnia za svoje činy, aby ich ostatní neodmietali. Sú dobrými priateľmi, ale ak si ich niekto znepriatelí, ich pomsta býva krutá. Veľa ľudí ich neberie vážne, ale Brunovia sa nakoniec presadia a dostanú sa do cieľa. V ich zdraví im neprospieva konzumácia mastných jedál. Toto meno je vhodné pre znamenia : Rak, Panna, Lev, Baran."},
                new NameEntity {Value = "Bystrík", NameDay = "11. september", Description = "Bystrík je nové slovenské meno odvodené zo slova \"bystrý\". Je veľmi priateľský a dá sa mu dôverovať. Vie o čo ide v živote a čo je dôležité. Má rád jasno vo vzťahoch a nerád klame, pretože si nepamätá nič pridlho. Je to výhoda, lebo zabúda aj na zlé a vie odpustiť skoro všetky hriechy svojim priateľom. V láske je nerozhodný, za mlada veľmi prelietavý, no keď sa rozhodne, potom niet nikoho a ničoho, čo by ho zviedlo zo správnej cesty. Ak mu jeho žena bude verná a nedá mu možnosť pochybovať o nej, potom sa aj on bude správať vzorne a bezchybne. Ovplyvňuje ho planéta Venuša. Jeho ochranné kamene sú smaragd, ruženín a avanturín."},
                new NameEntity {Value = "Ctibor", NameDay = "13. september", Description = "Mužské meno Ctibor má slovanský pôvod a jeho voľný preklad znie \"bojuj za česť\". Ctibor je geniálny a silný muž. Mnoho priateľov v ňom nájde oporu a pomoc aj v tých najťažších chvíľach. Medzi mužmi je uznávaný a medzi ženami obletovaný. Je verný zo zásady a jeho manželka môže s dôverou žiť po jeho boku. Má síce veľa obdivovateliek a aj veľa priateliek, ale nikdy nebude konať nečestne. Bol by tým najbohatším človekom na svete, ak by mu na tom záležalo. Je to však v jeho povahe - deliť sa s inými a nehnevať sa na ľudské slabosti. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú zafír a citrín."},
                new NameEntity {Value = "Cyprián", NameDay = "27. september", Description = "Mužské meno Cyprián má latinský pôvod a v preklade znamená \"muž z ostrova Cypru\". Zdá sa byť pokojný, až ťažkopádny, ale jeho vnútorný svet je plný vzrušenia a plánov, ktoré sú často nereálne. Je dosť ľahkomyseľný a aj v citoch sa málokedy vie ustáliť. Každá žena je pre neho niečím zaujímavá a on sa snaží nájsť si ku každej cestu. Nemá čas na budovanie dlhodobých vzťahov a keď sa ocitne v závažnej a ťažkej situácii, tak sa s radosťou obráti chrbtom a nechá svojich spoločníkov, aby si poradili sami. Nie je spoľahlivý, ani dôveryhodný, ale občas vie získať pre seba výnimočné podmienky a dosiahnuť úspech. Ten však nikdy nie je trvalý, pretože rád riskuje a skoro vždy potom stratí všetko, čo dosiahol. Nikdy sa nevzdáva a k svojim deťom je veľmi zodpovedný, takže im nechá dostatok prostriedkov na dobrý štart do života. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú citrín a nefrit."},
                new NameEntity {Value = "Cyril", NameDay = "5. júl", Description = "Mužské meno Cyril má grécky pôvod a význam mena je \"pán\". Tí, čo vládnu na zemi, tak sa dajú v stručnosti charakterizovať. Osobným číslom tohto mena je 7 – Tajomnosť. Šťastnou farbou je modrá a tmavé odtiene všetkých farieb. Títo muži sú citliví, spoločenskí, intuitívni a dynamickí. Občas, ale trochu leniví. Milujú dobrodružstvá a svoju nezávislosť. Ich vnútorný svet je málokomu prístupný. Sú stále pod vplyvom očakávania nejakej závažnej udalosti. Ak sa nič nestane, môžu sa zmeniť na zatrpknutých, necitlivých a egoistických ľudí. Na druhej strane si vedia i priznať chyby. Dokážu byť objektívni a spravodliví. Sú málo ovplyvniteľní, ale potrebujú niekoho po svojom boku, aby im stále opakovali, že mu veria, pomôžu a nakoniec sa aj presvedčí o svojich schopnostiach. S obľubou chodia do školy. Zameriavajú sa na techniku a vedecké štúdium. Stávajú sa z nich prevažne inžinieri, vojaci či zverolekári. Cyrilovia majú obdivuhodnú vitalitu, vynikajúcu odolnosť pred chorobami, ale potrebujú veľa spánku. Toto meno je vhodné pre znamenia : Rak, Panna, Vodnár, Váhy."},
                new NameEntity {Value = "Dalibor", NameDay = "20. január", Description = "Mužské meno Dalibor má slovanský pôvod a znamená \"vzďaľuj boj\". Jeho osobným číslom je 1 – Vodca. Ich obľúbená farba je oranžová, ochranné rastliny sú rozmarín, čakanka, ochranná kamene zafír a jaspis. Daliborovia sú šikovní muži. Sú stále v pohybe a aj keď tak nepôsobia, vedia byť aj citliví. Títo muži vždy vedia, čo robiť a ako sa správať. Sú vždy ohľaduplní a šarmantní voči druhým a vždy budú bojovať za pravdu. Daliborovia vedia dlho snívať a majú dobrú predstavivosť. Často unikajú pred realitou, v ktorej žijú. Ich vôľa nie je vždy na rovnakej hladine,ale predsa vedia po čom túžia. Občas majú náklonosť k lenivosti a hlavne v práci. Radi by sa zviezli na iných úspechoch. Veľmi sa im nepáči tvrdá práca a zarabanie peňazí. Ich zámerom je objaviť nejaké miesto, kde by mohli niekoho viesť, a aby hlavne oni sa nenamáhali. Je radšej sprievodcom ako vykonávateľom. Daliborovia majú v obľube štúdiu. Už od mála sú dobrými a usilovnými žiakmi a veľmi obľúbenými u učiteľov. Radi spolupracujú, preto s nimi nie je nikdy problém. Obrácajú sa ku klasickému štúdiu a hlavne k cudzím jazykom. Hodia sa k ním povolania profesorov, odborníkov v oblasti financií alebo obchodníkov. Ak chcú mať Daliborovia dobré zdravie, nemali by sa veľmi prejedať a dbať na svoju stravu. Toto meno je vhodné pre znamenia Váhy, Panna, Kozorožec a Býk."},
                new NameEntity {Value = "Daniel", NameDay = "21. júl", Description = "Mužské meno Daniel je starozákonné, má hebrejský pôvod a význam mena je \"Boh je sudca\". Osobným číslom tohto mena je 9 – Génius. Šťastná farba je fialová. Títo muži sú veľmi inteligentní a citliví. V spoločnosti sa cítia skvelo, pretože rozdávajú okolo seba pohodu. Neznášajú, keď sa v ich spoločnosti sa niekto nevhodne správa. Niekedy sa môže zdať, že sú leniví. Ale len čakajú na svoju príležitosť, aby mohli konať. Keď sa do niečoho pustia, dosahujú prekvapivé výsledky. Sú veľmi zvedaví a nezávislí. Majú dobrodružnú povahu. V živote majú presne stanovené pravidlá, ktoré aj dodržujú. Svoje názory nemenia bezdôvodne. Ich zdravie nie je veľmi odolné, ale to je zapríčinené nesprávnym spôsobom života. Toto meno je vhodné pre Rakov, Ryby a Panny."},
                new NameEntity {Value = "Dávid", NameDay = "30. december", Description = "Mužské meno Dávid má hebrejský pôvod a v preklade znamená \"miláčik\". Nemá sa čoho báť. Jeho budúcnosť bude vždy dobrá, len sa musí zo začiatku veľa učiť. Ak má dostatočné vzdelanie, potom je jeho život ľahkou cestou nahor, bez závažnejších prekážok. Ak nemá dostatok vedomostí, potom mu potrvá dlhšie, kým dosiahne svoje ciele. Tými sú peniaze a čím viac, tým lepšie. Záleží mu na vonkajšom lesku, blahobyte a luxuse. Musí mať okolo seba vždy dostatok všetkého a kvôli vlastnému pohodliu je ochotný aj riskovať, klamať, či podvádzať. Nezabúda na svoju rodinu a vždy sa snaží zabezpečiť aj budúcnosť svojich blízkych. Ovplyvňuje ho planéta Mesiac. Jeho ochranné kamene sú nefrit a rubín."},
                new NameEntity {Value = "Demeter", NameDay = "26. október", Description = "Mužské meno Demeter je gréckeho pôvodu a v preklade znamená \"patriaci bohyni úrody Daméter\". Je mužom, ktorý vie čo chce. Neznáša spôsob boja, ktorým by svoj cieľ mal získať. Nerád ničí, alebo ubližuje, iba ak ide vyslovene o ohrozenie jeho životných potrieb. V láske je veľmi náročný, žiarlivý a majetnícky. Spája lásku s predstavami z minulosti a neuznáva žiadne ženské práva. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú zafír, citrín a sardonix."},
                new NameEntity {Value = "Denis", NameDay = "1. november", Description = "Krstné mená Denisa, Denis majú grécky pôvod. V preklade znamenajú \"zasvätení bohu vína, úrody a plodnosti Dionýzovi\". V jednom momente dokážu riskovať, strácať, získavať, dávať aj prijímať. Nikdy nikomu nič neodoprú, stačí ak ide o nejakého blízkeho človeka a už premýšľajú o pomoci, aj na vlastný úkor. Nikto im nie je cudzí a aj keď sa sklamú, vedia odpúšťať. V práci nevedia dodržať žiadnu disciplínu, ani nemajú vytrvalosť k boju s prekážkami. Nedokážu sa veľmi angažovať v ničom jednotvárnom, alebo nudnom. Musia mať pocit nebezpečenstva, riskovať a nikdy ich nezaujíma výsledok. Ak im partner dokáže svoju lásku, potom mu budú verne stáť po boku aj dlhé roky. Ovplyvňuje ich planéta Merkúr. Ich ochranné kamene sú jaspis, nefrit a citrín."},
                new NameEntity {Value = "Dezider", NameDay = "11. február", Description = "Mužské meno Dezider má latinský pôvod a jeho význam v preklade znie ako \"žiadajúci\", \"vyžiadaný\". Jeho osobným číslom je 8- Peniaze. Ich obľúbené farby sú hnedá a čierna, ochranné kamene zafír, achát a granát. Ich totemové zviera ja kobra a rastlina brečtan. Deziderovia dokážu byť tvrdý, majú úctu k tradíciám, sú citliví a ich zmyslom je rodina. Sú priateľský a radi študujú, aby mohli byť lepší ako iní. Dezider rýchlo dosiahne svoje úspech ale ak sa nechá ovládnuť citmi, láskou a zmyslami, tak sa úspechu dočká až v starobe. Sú spoločenský a opatrní, vedia si presne rozplánovať svoju budúcnosť. Dokáže sa sťažovať a plakať nad sebou a svojimi neúspechmi, no keď zacíti možnosť na úspech, naraz sa zmení na tvrdého bojovníka a nájde v sebe mnoho skrytých zásob energie. Svoje názory dokážu vyjadrovať nahlas – čo na srdci, to na jazyku – aj keď to môže občas pôsobiť hrubo. Toto meno je vhodné pre znamenia : Vodnár, Strelec, Baran, Býk a Blíženci. Jeho farbou je modrá."},
                new NameEntity {Value = "Dionýz", NameDay = "9. október", Description = "Mužské meno Dionýz je gréckeho pôvodu a v preklade znamená \"zasvätený bohu Dionýzovi\" (gréckemu bohu vína, úrody a plodnosti). Aj keď sa toto meno nepoužíva veľmi často, je pre svojho nositeľa výhodné. Prináša energiu, ktorá pomáha mužovi presadiť sa. Riskovanie, šarm, čestnosť a rýchle rozhodovanie - to sú typické črty nositeľa tohto mena. Dionýz je veľkým priateľom žien a dokáže pochopiť všetky ich nálady. Je aj dobrým milencom, manželom a otcom pre svoje deti. Snaží sa, aby v jeho okolí boli ľudia spokojní. Aj keď túži zbohatnúť, nesnaží sa dosiahnuť úspech za každú cenu. Sklamania v ľuďoch často lieči alkoholom a potom sa jeho povaha zmení k horšiemu. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú jaspis a nefrit."},
                new NameEntity {Value = "Dobroslav", NameDay = "15. január", Description = "Mužské meno Dobroslav má slovanský pôvod s významom \"dobro sláv\".Osobným číslom tohto mena je 9 – Génius. Ich obľúbená farba je žltá, fialová a rastlinný totem mak. Títo muži bývajú zvyčajne znepokojení. V živote sú prelietaví a neustále v napätí. Ale snažia sa pôsobiť pokojne a v ťažkých situáciach zachovať chladnú hlavu. Dobroslavovia chcú vždy všetko vedieť a niekedy sú až veľmi zvedaví. Sebavedomie nemajú vysoké a preto niekedy bývajú dosť nesmelí. Ale predovšetkým sa spoliehajú na svoju predtuchu a fantáziu. Čo sa týka ich zamestnania, nebývajú zvyčajne aktívni. Aj keď sú leniví, nakoniec dosiahnú to, čo chceli. Preto im veľa ľudí nepraje, ale Dobroslavov to nevyvedie z miery. Faloš a ohováranie neberú vážne, pre nich sú to bezvýznamné veci. Nemusia sa príliš snažiť ani v práci, vždy niekto iný im vyhovie a pomôže im dosiahnuť to, po čom túžili. Dobroslavovia majú zdravie celkom v poriadku. Nemali by sa intelektuálne preťažovať a mali by si chrániť pokožku, žalúdok, kolená a nervový systém. Toto meno sa hodí pre znamenia : Blíženci, Váhy, Ryby a Škorpióna."},
                new NameEntity {Value = "Dominik", NameDay = "4. august", Description = "Meno Dominik je svätecké meno, latinského pôvodu a jeho význam je \"pánov\". Je rýchly a má vždy veľa záujmov. Nevie sa sústrediť len na jednu prácu, potrebuje mať plný stôl problémov a všetko riešiť naraz. Vyhovuje mu žiť v zhone a vzrušení. Jeho aktivita, rýchlosť a prudkosť mu spôsobujú veľa problémov, ale on všetko zvládne. Ak sa zamiluje do správnej ženy, potom bude jeho život cestou nahor, všetko sa mu vydarí a dosiahne svoj cieľ. Aj keď si to nerád priznáva, potrebuje oporu v rodine, citovú istotu a stabilitu. Vie byť verný a milujúci. Postará sa o svoju rodinu, o svoje deti, ale len ak má pocit, že to má význam a partnerka je mu verná a chce s ním žiť celý život, nie iba chvíľu. Vzájomná dôvera je pre neho veľmi podstatná. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene sú ametyst, achát a perla."},
                new NameEntity {Value = "Drahomír", NameDay = "16. júl", Description = "Mužské meno Drahomír má slovanský pôvod a význam mena je \"komu je drahý mier\". Ovplyvňuje ich Mesiac. Šťastné farby sú biela, zelená a svetlomodrá. Ochranné rastliny rumanček, jazmín, medovka, ochranné kamene nefrit a rubín. Títo muži sú dobrosrdeční, ale aj naivní. Dokážu sa správne rozhodovať. V kolektíve ich obľubujú, pretože sa vedia nadchnúť pre ušľachtilé ciele. Ich zdravie nie je práve odolné. Často ich bolieva hlava a mali by si dávať veľký pozor aj na hrdlo, obličky a pľúca."},
                new NameEntity {Value = "Drahoslav", NameDay = "4. január", Description = "Mužské meno Drahoslav má slovanský pôvod, ktorého význam je \"nech mu je drahá sláva\". Osobným číslom tohto mena je 8 – Peniaze. Týchto mužov najviac ovplyvňuje planéta Saturn. Ich totemová rastlina je kostihoj, ochranný kameň granát a obľúbená farba hnedá a zelená. Drahoslav vie veľmi rýchlo odhadnúť človeka a málokedy sa mýli v úsudku o iných. Je ohľaduplný a empatický, pretože má zmysel pochopiť chyby a omyly druhých. Nerád koná proti svojmu presvedčeniu, ale iných ľudí rešpektuje a necháva im voľnú ruku, aby sa sami slobodne realizovali. Snaží sa neovplyvňovať druhých, a preto je v rodine považovaný za skvelého človeka, aj jeho deti ho preto zbožňujú. Nič nie je pre neho nemožné, vrhá sa do všetkého s tým, že všetko sa dá vyriešiť. Ide si za svojím cieľom a popri ani nezabúda na svoju rodinu či svoju lásku. Vie všetko skĺbiť a dokáže naraz riešiť množstvo problémov. Nositelia s týmto menom sú vhodné pre znamenia Barana, Býka, Kozorožca, Škorpióna a Raka."},
                new NameEntity {Value = "Dušan", NameDay = "26. máj", Description = "Mužské meno Dušan je južnoslovanskou skrátenou podobou mien Duchoslav a Dušislav, a v preklade znamená \"slávny duchom\". Ovplyvňujú ich planéty Saturn a Urán. Ich šťastné farby sú čierna, oranžová, tmavomodrá. Ochranné rastliny medovka, baza, divozel, ochranné kamene achát a perla. Osobným číslom tohto mena je 7 - Tajomnosť. Toto meno je veľmi pozitívne a pre svojho nositeľa výhodné. Človek s týmto menom sa vždy dokáže dohodnúť, nájsť spoločnú reč a komunikovať s inými, žiť podľa vlastných predstáv. Vie byť však dosť sebecký a lakomý. Nerád sa delí o nič, čo dosiahne, ani o úspech, ani o peniaze. Ale ak sa zamiluje do správnej ženy, dokáže sa zmeniť a štedro obdarúva svoju rodinu a blízkych. Aj napriek osobnému číslu tohoto mena, ktorým je  7 čiže „tajomnosť“, má takýto človek len málo tajomstiev. S prehľadom a bez  veľkých sa pretĺka životom a ráta aj s menšími stratami. Necháva si vždy zadné dvierka pre prípad, že by mu nevyšli nejaké veci, ktoré si naplánoval. Dušanovia sa vedia dosť rýchlo unaviť. To im bráni v ich dobrom zdraví.Keďže sa radi dobre najedia, potrebovali by k svojej vitalite pravidelný pohyb a stravou by si mali dopĺňať najmä vitamíny C, E a vápnik. Dušan je vhodné meno pre znamenia : Strelec, Ryby, Kozorožec, Škorpión, Lev."},
                new NameEntity {Value = "Edmund", NameDay = "1. december", Description = "Mužské meno Edmund má anglický pôvod a v preklade znamená \"mocný ochranca\". Je v živote veľmi úspešný. Vynikne v právnej sfére, ako vyšetrovateľ, či daňový úradník. Svoje klady ukáže, ak má zodpovedné miesto, ktoré je dobre ocenené. V láske je povrchný a svoj záujem mení z hodiny na hodinu. Nemá výčitky kvôli nevere. Má svoje pravidlá a podľa nich žije. V súkromí je život po jeho boku dosť ťažký, najmä ak jeho partnerke ide o stály vzťah. Ku svojim deťom je výnimočne starostlivý a snaží sa im byť vo všetkých smeroch vzorom. Kvôli nim je ochotný zmierniť svoje správanie v oblasti lásky a sexu. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene sú chalcedón a jaspis." },
                new NameEntity {Value = "Eduard", NameDay = "18. marec", Description = "Mužské meno Eduard má anglický pôvod a význam mena je \"mocný, bohatý strážca\". Šťastné farby Eduardov sú tmavomodrá, oranžová a červená. Ochranná rastlina puškvorec, kamene rubín, achát a opál. Osobným číslom tohto mena je 9 - Génius. Eduardovia sú intuitívni muži. Vždy vedia nájsť správnu cestu, ktorou majú kráčať. Buď sa zamerajú na materialistickú stránku života alebo na duchovnú. Do svojich záležitosti sa púšťajú s radosťou aj keď občas pôsobia ako flegmatici. Mali by sme ich vedieť pochopiť v každej situácií a kritizovať by ich mali takí ľudia, ktorých si vážia a rešpektujú. Ak si ujasnia, čo vlastne chcú a využijú všetky svoje prednosti a vedomosti, budú rozhodní, stanú sa z nich úspešní ľudia. Eduardovia sú plní energie, veľmi si dôverujú a len málokto ich dokáže ovplyvniť. Toto meno je vhodné pre znamenia : Škorpión, Panna, Rak, Ryby." },
                new NameEntity {Value = "Eliáš", NameDay = "20. júl", Description = "Mužské meno Eliáš má hebrejský pôvod a znamená \"môj Boh\". Najčastejšie je zaužívané v rusky hovoriacich krajinách. Osobným číslom tohto mena je 2 – Dobrota. Ovplyvňuje ich planéta Jupiter a Mesiac. Šťastné farby sú azúrovomodrá, biela a krémová. Ochranné rastliny slez, sedmokráska, ochranné kamene avanturín, granát. Títo muži sú činorodí, zodpovední, láskaví a inteligentní. Vedia sa vrhnúť do problémov, ktoré sa zdajú byť neriešiteľné. Aj keď sa im nepodarí vyriešiť nejaké problémy, nemajú z toho ťažkú hlavu. Sú zodpovední už od školských rokov. Vyhľadávajú povolania, v ktorých je nevyhnutný kontakt s ľuďmi. Svoje skúsenosti a schopnosti využívajú pre druhých. Pomáhajú svojmu okoliu, ale keď sa náhodou sklamú, nezanevrú na svoje okolie. Zdravie je celkom dobré. Pretože, ak sa už pre niečo rozhodli, nemôžu byť chorí. Veľmi dobre vedia odolávať únave. Toto meno je vhodné pre Býkov, Kozorožcov, Panny a Baranov." },
                new NameEntity {Value = "Emanuel", NameDay = "26. marec", Description = "Mužské meno Emanuel je starozákonné, má hebrejský pôvod a v preklade znamená \"s nami je Boh\". Ovplyvňuje ich planéta Venuša. Šťastné farby Emanuelov sú zelená, biela, oranžová a žltá. Ochranné rastliny nechtík a ligurček, ochranné kamene smaragd a zafír. Jeho osobným číslom je 1 – Vodca. Títo muži to vedia dosiahnuť vo svojom živote dosiahnuť ďaleko. Majú radi, ak môžu niekoho viesť a byť v popredí. Emanuelovia sú dosť neústupčiví. Bývajú často na rozhraní – raz pôsobia ako dobré a raz ako zlé osobnosti. Nevedia často, čo cítia a je ťažké sa na tieto osoby spoľahnúť. Oni sú veľmi energickí a nikdy sa neunavia. Cítia sa ako výstredni ľudia, lebo dokážu odmietať ponuky od druhých a aj kritike. Je to meno vhodné pre znamenia : Býk, Kozorožec, Škorpión, Panna."},
                new NameEntity {Value = "Emil", NameDay = "31. január", Description = "Mužské meno Emil má latinský pôvod a v preklade znamená \"horlivý\", \"pracovitý\". Osobným číslom tohto mena je 1 – Vodca. Ovplyvňujú ich planéty Mars a Pluto. Ich šťastné farby sú modrá a ružová. Ochranné rastliny bazalka a puškvorec, ochranné kamene rubín a diamant. Títo muži žijú v uzavretom svete. Svoje myšlienky a city sú málokomu prístupné, ani dokonca svojim najbližším. Emilovia sú skôr plachý a preto potrebujú mať niekoho pri sebe, aby im pomohol v ich sebadôvere. Majú dobrú fantáziu a predtuchu, ktorá im pomáha pri neustálom hľadaní nových udalostí. Ak nenájdu, čo hľadali, stávajú sa z nich zatrpnutí ľudia, ktorí myslia pesimisticky. Svoj úspech dosiahnu vtedy, ak budú mať pri sebe niekoho, kto ich bude podporovať v každej situácií. Štúdium ich nikdy nelákalo. Zameriavajú sa na oblasť herectva a publicity. Nájdeme však medzi nimi aj osamelých cestovateľov, ktorí odmietajú žiť v civilizovanom svete. Ako ešte deti treba Emilov dobre tvarovať ich osobnosť a dodať im potrebnú vyrovnanosť. Zdravie majú v poriadku, ale mali by si dávať pozor na žalúdok. Toto meno je vhodné pre znamenia : Rak, Panna, Vodnár, Váhy."},
                new NameEntity {Value = "Erik", NameDay = "2. február", Description = "Mužské meno Erik má škandinávsky pôvod a v preklade znamená \"ctihodný vládca\". Osobným číslom tohto mena je 7 - Tajomnosť. Medzi obľúbené farby týchto mužov sú hnedá, zelená a čierna. Ochranné rastliny majú určené kostihoj a štiav, kamene zafír a opál. Erikovia sú prefíkaní, lebo niektoré ich výbuchy sú predstierané, čo im umožňuje pripraviť sa na vykrútenie sa zo situácie. Nikdy nie sú sebeckí, dokážu za každých okolností pomôcť ľudom, ale nikdy nepomáha bez vopred premyslenej návratnosti. Od mladosti sú Erikovia veľmi zruční a uvedomujú si svoje hodnoty v oblasti profesionality. Sú agresívni, ak si vezmú do hlavy, že budú úspešní. Nositelia tohto mena sa dokážu prispôsobiť rôznym okolnostiam v ich živote. Bývajú málokedy unavení, ale niekedy ich premôže bolesť hlavy. Precízne si vyberá svojich priateľov alebo známych, vytvára si dobre svoje teritórium, aby sa mohol vyhnúť nevďačnosti a výsmechu. Ak ste Erikovi blízky, nemusíte sa báť, že vás zradí, opustí alebo ublíži, a však ak budete robiť veci podľa jeho predstáv. Dokáže vytvoriť výborne okolie pre všetkých, ktorých miluje. Meno Erik je vhodné pre Býkov, Strelcov, Baranov a Vodnárov."},
                new NameEntity {Value = "Ernest", NameDay = "12. január", Description = "Mužské meno Ernest má nemecký pôvod a v preklade znamená \"vážny\", \"čestný\". Toto meno má osobné číslo 5 - Riziko. Ich obľúbená farba je modrozelená, biela a žltá, ochranný kameň akvamarín. Ernestovia majú pokojnú povahu a nič ich nevyvedie z miery. Žijú pomalým životom, nikde sa neponáhľajú. Nevedia pochopiť ostatných, ktorí sú v neustálom zhone. Toto meno nesie v sebe pozitívne veci. Zvyčajne títo muži sú v bezpečí a obklopuje ich dobro. Ak niekto Ernestov ovplyvní, prestanú sa snažiť a nedarí sa im. Neznesú, ak ich niekto blízky sa nevhodne správa. Radi sú v spoločnosti, v ktorej môžu ukázať svoje nadanie. Potrpia si na dobrý štýl oblečenia. Ak sa Ernestovia budú spoliehať na svoju predtuchu, budú dosahovať úspechy aj keď to nebude najjednoduchšie. Vždy majú niekoho po boku, na ktorého sa môžu obrátiť. V oblasti štúdia sa Ernestovia obracajú na viacej predmetov. Vynikajú v literatúre, hudbe, v exaktných vedách alebo v divadelníctve. Zvlášť ich lákajú zamestnania, v ktorých majú čas na čítanie a písanie. Ernestovia majú dobrú predstavivosť a preto sa k ním hodia všetky umelecké povolania. Zdravie týchto mužov je normálne. Ich zdravotné problémy pramenia z ich nesprávneho spôsobu života a nepravidelnej stravy. Mali by si dávať pozor na nohy a nemali by ich zaťažovať. Toto meno je vhodné pre znamenia Lev, Váhy, Baran a Strelec."},
                new NameEntity {Value = "Ervín", NameDay = "21. apríl", Description = "Mužské meno Ervín patrí k zriedkavejším menám, má nemecký pôvod a znamená \"priateľ cti\". Osobným číslom tohto mena je 4 - Sila. U týchto ľudí nevieme, čo môžeme čakať. Ervínovia sú tvrdohlaví a v celku aktívni ľudia. Vždy vedia, čo chcú a čo chcú dosiahnuť, ale uvažuje tak, aby sa pri tom veľmi nenadrel. Ervínovia vám vždy pomôžu a odporučia správne chodníčky v živote. Niekedy sú viac introvertmi, ťažko sa rozhodujú a nevedia si vybrať. Ich životaschopnosť celkom dobrá, kým svoju silu neprecenia. Mali by byť striedmejší v dôverčivosti a utrácaní peňazí. Toto meno je vhodné pre znamenia : Býk, Ryby, Váhy, Panna. Ich šťastné farby sú zelená, biela, červená, žltá a ich ochranné rastliny nechtík, ligurček a podbeľ, ochranné kamene smaragd a zafír."},
                new NameEntity {Value = "Eugen", NameDay = "18. november", Description = "Mužské meno Eugen má grécky pôvod a význam mena je \"blahorodý, urodzený\". Je to schopný, rozhodný, tvrdohlavý a nadaný muž, ktorý neplýtva slovami, ale snaží sa konať. Dlho premýšľa a váha, no ak sa rozhodne, potom ho už nič nepresvedčí o opaku jeho záveru. V láske je verný a stály, ale ak v detstve vyrastal v spoločnosti ľudí, ktorí brali morálku na ľahkú váhu, potom sa jeho vernosť občas stratí a oklame svoju partnerku. No nie je neúprimný, ani odmietavý. Snaží sa hrať otvorene, aspoň čo sa týka citov. V kariére je to tvrdý obchodník, ktorý vie presne, aká je cena a čo musí obetovať, aby bol úspešný. Je dobrým partnerom, či už ide o city, alebo obchody. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene sú ametyst a perla."},
                new NameEntity {Value = "Fedor", NameDay = "15. apríl", Description = "Mužské meno Fedor je slovenskou podobou ruského mena Fjodor, ktoré je vytvorené z gréckeho mena Theodóros a znamená \"boží dar\". Toto meno má osobné číslo 6 – Nežnosť. Títo muži sú činní a citliví, ale občas vedia prejaviť agresívne. Fedorovia priťahujú dosť problémy aj keď sám o sebe pôsobia milo a sebadôverne. Od malička potrebuje, aby sa rodičia o nich dosť zaujímali a venovali mu pozornosť, aby sa vedel neskôr v živote dostatočne obrácať. Fedorovia nie sú zodpovední, preto najradšej by „ťahal“ s niekým úspešným. Ťažko sa adaptujú okoliu a ich odvaha nie je často taká, ako vyzerá na povrchu. Aj keď bývajú Fedorovia nepokojní, ich životaschopnosť je odolná. Môžu mať problémy so zrakom. Prospeje im veľa surovej zeleniny, ryby a výrobky so sóje. Toto meno je vhodné pre znamenia : Blíženci, Býk, Panna, Váhy. Vhodnou farbou je hnedá. Obľúbené farby týchto mužov sú fialová, červená a ružová. Ochranné rastliny púpava, chmeľ a prvosienka, ochranné kamene diamant a jaspis."},
                new NameEntity {Value = "Félix", NameDay = "20. november", Description = "Mužské meno Félix má latinský pôvod a v preklade znamená \"šťastný\". Má dosť energie a sily, aby sa postavil na nohy po akomkoľvek údere. Strieda úspechy a neúspechy. Je povestný svojimi výkonmi a nikdy nezostáva v anonymite. Snaží sa konať v súlade so svojím svedomím, ale nie vždy je to svedomie v súlade s platnými zákonmi, teda niekedy koná dosť rozporuplne. Celkovo je ochotný, milý a jemný, najmä k tým, ktorých miluje. Ak ste na jeho strane, potom sa nemáte čoho báť, s ním sa dostanete na to najlepšie miesto, kde sa vám bude určite dariť. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene sú sokolie oko a perla."},
                new NameEntity {Value = "Ferdinand", NameDay = "30. máj", Description = "Mužské meno Ferdinand má germánsky pôvod a význam mena je \"smelý ochranca\". Ovplyvňuje ich planéta Saturn. Ich obľúbené farby sú tmavomodrá, žltá, sivá, ochranná rastlina kokorík, ochranné kamene opál a granát. Osobné číslo tohto mena 6 – Nežnosť. Muži s týmto menom sú nestáli a nerozhodní, ale zato zvedaví. Ferdinandovia si nevedia naplánovať svoje povinnosti a nevedia prene čo chcú, aby dosiahli úspech. Chýba im sebavedomie a nestranný postoj k skutočnostiam. Dobre sa cítia v spoločnosti, ale niekedy im viac vyhovuje, keď môžu byť sami. Títo muži nevedia dobre využiť príležitosti, ktoré sa im ponúkajú. Mali by využiť svoju intuíciu a neutekať od svojich povinností. Nemajú radi, keď majú niečo organizovať, preto by mal niekto pri nich stáť, kto by im pomohol v ich rozhodnutí a naviedol ich na správnu cestu. Keď budú Ferdinandovia chcieť, tak dosiahnu úspech. Títo muži sa orientujú hlavne na zamestnania intelektuálnej a manuálnej činnosti. Zdravie majú Ferdinandovia vyrovnané. Zle odolávajú psychickej preťaženosti, ale naopak dobre odolávajú fyzickej únave. Prospeje im, ak budú jesť viac karfiolu, uhoriek, mandlí a jahôd. Toto meno je vhodné pre znamenia Váhy, Ryby, Blíženci a Vodnár."},
                new NameEntity {Value = "Filip", NameDay = "23. august", Description = "Mužské meno Filip má grécky pôvod a v preklade znamená \"milovník koní\". Ovplyvňuje ich planéta Slnko. Ich šťastné farby sú oranžová a žltá. Ochranné rastliny borák a mak, ochranné kamene olivín a krištáľ. Toto meno má osobné číslo 2 – Dobrota. Filipovia sa vedia slušne správať a pôsobia ako vyrovnané osobnosti. Vždy vedia, čo chcú v živote dosiahnuť a to im aj dopomáha stať sa úspešnými. Títo muži sa vedia prispôsobiť v každej situácií a to veľa ľudí zneužíva. Väčšinou v živote veľmi nemajú šťastie, ale napriek tomu Filipovia vždy zostávajú v dobre nálade a stále optimistickí. Snažia sa vždy pomôcť druhým a potrebujú, aby ich niekto usmerňoval, aby získal väčšie sebavedomie. Filipovia často nasledujú takých ľudí, ktorí ich budú viesť a brať za nich zodpovednosť. Ale zato sú iniciatívni a rozhodní, preto si aj vyberajú také zamestnania. Uplatnia sa ako ako režiséri, redaktori, alebo bádatelia. Toto meno je vhodné pre znamenia : Blíženci, Býk, Panna, Váhy."},
                new NameEntity {Value = "Florián", NameDay = "4. máj", Description = "Mužské meno Florián je latinského pôvodu a v preklade znamená \"kvet\". Ich obľúbené farby sú zelená, biela, oranžová a žltá, ochranné rastliny nechtík a podbeľ, ochranné kamene smaragd, zafír a avanturín. Osobným číslom tohto mena je 5 - Riziko. Títo muži sú dosť konzervatívni. Pôsobia ako veľmi pokojní ľudia, ale vo vnútri to niekedy v nich vrie. Majú vždy premyslené, čo a ako chcú konať, I keď niekedy to nie je možné zrealizovať. Občas sú pomalí, to preto lebo starostlivo uvažujú ako budú konať a svoje problémy riešiť. Nedá sa na nich veľmi spoľahnúť a to ani v rámci kariéry. Floriánovia sú veľmi pracovití v svojom zamestnaní. Vždy vedia dobre riešiť svoje pracovné záležitosti a nakoniec dosiahnu výborne výsledky. Problémy neberie na seba, radšej nechá sa s nimi trápiť svojich spolupracovníkov. Títo muži radi riskujú, čo nemusí byť pre nich výhodne, lebo môžu stratiť všetko, čo získali. Floriánovia sa často realizujú v oblasti medicíny. Ich zdravie je celkom v poriadku. Keďže radi využijú každú príležitosť v ich živote, nemali by sa veľmi náhliť. Toto meno je vhodné pre znamenia : Baran, Býk, Rak, Lev a Ryby."},
                new NameEntity {Value = "František", NameDay = "4. október", Description = "Krstné meno František je talianskeho pôvodu, bolo odvodené od mena svätého Františka z Assisi a znamená \"malý Francúz\". Pôsobí ako ľahkomyseľný a veselý človek. Často premýšľa nad filozofickými a duchovnými otázkami. Ak jeho okolie nemá záujem o rozvíjanie takýchto \"nadzemských\" teórií, on sa prispôsobí a svoj tajný svet si nechá pre seba. Ak sa dá zviesť na alkohol, stratí svoju intuíciu a stane sa z neho len obyčajný, povrchný a vypočítavý človek, sklamaný svetom. Je to len na ňom, ako si poradí so svojím osudom, je silný a precízny. Ak má motív a presný cieľ, stačí malý neúspech a už o sebe pochybuje. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú tigrie oko a topás."},
                new NameEntity {Value = "Frederik", NameDay = "25. február", Description = "Meno Frederik je románskou podobou mena Fridrich, ktoré je nemeckého pôvodu a v preklade znamená \"mierumilovný vládca\". Často má stálu výdrž svojho rastlinného totemu duba. Frederikova šťastná farba je žltá a červená. Ochranné rastliny sú púpava, rozmarín, ochranné kamene jaspis a karneol. Jeho osobné číslo je 1 – Vodca. Má výbornú vlastnosť viesť druhých, hlavne na poznávanie okolia. Frederik je nezávislý a dosť tvrdohlavý. Ak budeme mu venovať viac pozornosť a budeme mu nablízko, dostatočne to ocení a obetuje nám silné priateľstvo. Frederikovia sa do práce púšťaju rýchlo, ale neskôr na nejaké skutočnosti rýchlo zabudnú. Pre fyzickú prácu nie sú stvorení. V práci potrebujú pomoc a ochotných spolupracovníkov. Ak bude v kolektíve so zlými ľuďmi, je tu problém a nijakých úspechov sa nedočká, lepšie vždy bude, keď sa radšej spoľahne na seba samého. V citovom živote nie je oddaný rodinnému životu. Frederik je síce vášnivý muž, ale aj dosť nestáli a prelietavý v citoch. Toto meno je vhodné pre znamenia : Baran, Vodnár, Strelec, Blíženci, Lev."},
                new NameEntity {Value = "Fridrich", NameDay = "5. marec", Description = "Mužské meno Fridrich má germánsky pôvod a jeho význam v preklade je \"mierny vladár\". Ovplyvňuje ich planéta Mars, obľúbenú farbu majú žltú a červenú. Ich ochranné rastliny sú púpava a žltá chryzantéma, ochranné kamene diamant a avanturista. Osobné číslo pre toto meno je 5 – Riziko. Nositelia tohto mena sú samostatní a plní energie. Sú to šarmantní a pôvabní muži a dá sa na nich spoľahnúť. Medzi ľuďmi sú veľmi roztržití, potrebujú pohyb, nejaké vzrušenie a rýchle situácie. Pre nich pokoj neexistuje. Aj v práci musí stále niečo robiť, nudu neznášajú. Fridrichovia konajú vždy pohotovostne, aby sa stali úspešnými. Vždy budú niečo riešiť, stavať a prekonávať ťažkosti. Ak dosiahnu, to po čom túžil, trochu si oddýchnu, ale nie nadlho. Toto meno je teda vhodné pre znamenia, ktoré majú rady zmenu a vedia sa prispôsobovať : Strelec, Vodnár, Blíženci."},
                new NameEntity {Value = "Gabriel", NameDay = "24. marec", Description = "Mužské meno Gabriel je starozákonné, má hebrejský pôvod a v preklade znamená \"Boh je silný, muž boží\". Ovplyvňuje ich planéta Mars. Šťastné farby týchto mužov sú biela, oranžová, zelená, krémová. Ochranné rastliny rumanček, sedmokráska, kamene nefrit, perla a rubín. Osobným číslom tohto mena je 4 - Sila. Gabrielovia sú aktívni a silní ľudia. Vždy vedia ako konať, aby dosiahli svoj cieľ. Sú vytrvalí, no ľahko ich ľudia dokážu ovplyvniť. Pôsobia veľmi povýšenecky a vždy majú potrebu sa predvádzať sa pred ostatnými. Títo muži majú v obľube niekde unikať a vždy potrebujú meniť svoje doterajšie záležitosti. Ak sa Gabrielovia budú o seba starať, dosiahnu úspech, no ak sa vyhnú alkoholu, ktorí im vždy zničí ich túžby. Ich dobré zdravie občas naruší únava, sú citliví na duševné prepracovanie. Toto meno je vhodné pre znamenia : Vodnár, Baran, Strelec, Blíženci, Lev, Panna."},
                new NameEntity {Value = "Gašpar", NameDay = "29. január", Description = "S menom Gašpar sa stretávame už v Novom Zákone, ide o mužské meno z Orientu a s pôvodným významom \"nesúci poklad\". Osobným číslom tohto mena je 4, čiže Sila. Ich obľúbené farby sú zlatá, žltá a hnedá, ochranné rastliny púpava a prvosienka, ochranné kamene krištáľ a ónyx. Tento muž vzbudzuje v iných ľuďoch podnet nasledovať ho a dávať mu za pravdu. Je síce milý, navonok otvorený a príjemný, ale nedá nikomu znať o svojich najvnútornejších citoch a plánoch. Gašparovia vedia byť aj dobre \"prefíkaní šibali\". Prejavia sa vtedy, keď chcú niekoho nanešťastie zneužiť, alebo zbohatnúť na úkor niekoho. Vtedy to robia všetko tak jemne a nenápadne, s akýmsi \"šarmom\", takže aj keď iných podvedú, nikto im nedokáže ublížiť. Nedokážu všetko urobiť samostatne, preto potrebujú dobrých spolupracovníkov. Lenže ak sa dajú dokopy so zlými ľuďmi, tak sa s trvalým úspechom musia rozlúčiť. Sú ľahkým terčom manipulácie. Čiže len ak sami zostanú pri vážnom rozhodovaní a aj pri výbere ľudí okolo seba a spoľahnú sa tiež na svoju intuíciu, tak z nich budú ľudia hodní úcty a ocenenia. Aj keď sú Gašparovia nenároční ľudia, na svoje zdravie veľmi nedbá. Medzi ich slabosti v oblasti životaschopnosti patrí citlivosť kostí a pečene. Toto meno je vhodné pre znamenia Baran, Blíženci, Býk, Vodnár, Ryby a Strelec."},
                new NameEntity {Value = "Gejza", NameDay = "25. január", Description = "Mužské meno Gejza má turecký pôvod a význam tohto mena je odvodený zo slova \"jeg\", označujúceho vojenské hodnosti. Ovplyvňuje ich planéta Saturn, obľúbenú farbu majú hnedú a zelenú, ochranné rastliny hyacint a kostihoj, ochranný kameň opál. Osobným číslom tohto mena je 5 - Riziko. Títo muži sú aktívni a nadaní ľudia. Na ľudí pôsobia dosť neprístupne a zdržanlivo. Gejzovia majú silnú rozhodnosť a veľa energie. Ich nálada býva často premenlivá a nevrlá a to keď nemá úspech. Čo sa týka ich kariéry, sú veľmi usilovní a pevne si idú za svojim cieľom a dôverujú iba sebe a svojim najbližším. Ich vytrvalosť im umožní za každých okolností zvíťaziť a všetko dať do poriadku. Ak sa im bude dariť, budú veľmi úspešní a nič im nezničí ich plány. Ale ak podľahnú alkoholu, môžu sa s celým úspechom rozlúčiť. K spokojnému životu potrebujú dostatok spánku. Sú citlivejší na kĺby a chrbticu. Je vhodné pre znamenia Býk, Panna, Kozorožec, Baran a Lev."},
                new NameEntity {Value = "Gregor", NameDay = "12. marec", Description = "Mužské meno Gregor je gréckeho pôvodu a v preklade znamená \"ostražitý\". Ovplyvňuje ich planéta Saturn. Ich šťastná farba je zelená, fialová a hnedá. Ochranné rastliny agát a kostihoj, ochranný kameň ametyst. Osobným číslom je 1 - Vodca. Títo muži sú plní energie. Pôsobia ako silne a rýchle osobnosti. Aby sa vyhli neúspechom, mali by byť stále v pohybe a konať. Vždy vedia, čo majú robiť, aby sa vyhli nepríjemnostiam a tak sa dobre presadia. Gregorovia nikdy nedokážu uznať, že je niekto lepší ako oni. Veľakrát musia oni riadiť rozkazovať druhým, aby boli pokojní. Aj napriek tomu, vedia pracovať v kolektíve. Ak nebudeme Gregorom odvrávať a uznáme, že oni majú pravdu, bude sa nám žiť po ich boku výborne – v blahobyte. Ich vitalita je vyvážená. Aj keď sú neustálom pohybe, v strese a žijú nepravidelný život, ich životaschopnosť je výborná. Potrebujú slnko, preto radi dovolenkujú pri mori. Toto meno je vhodné pre silné znamenia : Baran, Býk, Škorpión, Lev."},
                new NameEntity {Value = "Gustáv", NameDay = "2. august", Description = "Mužské meno Gustáv má švédsky pôvod a význam je \"opora Gotlanďanov\". Je náladový, ale vie sa prinútiť k výkonom, ak je za to dobre zaplatené. V láske je u neho na prvom mieste snaha získať čo najlepšie finančné výhody. Niet sa čo diviť, ak uprednostní pred citmi peniaze. U neho každý vie, na čom je, lebo on sa svojimi snami o zbohatnutí a získaní dobrého postavenia netají. Zabezpečuje si svoje pozície a pomáha aj iným, ale nie zadarmo. Každý, kto mu môže byť užitočný, je jeho priateľom. Je to jednoduchý človek, s jednoduchými životnými túžbami, ale aj so silou na ich dosiahnutie. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú sardonyx a citrín."},
                new NameEntity {Value = "Henrich", NameDay = "15. júl", Description = "Mužské meno Henrich má germánsky pôvod a jeho význam je \"vládca domu\". Osobné číslo je 2 – Dobrota. Ovplyvňuje ich planéta Saturn. Šťastné farby sú hnedá, čierna a tmavomodrá. Ochranné rastliny kostihoj a štiav, ochranné kamene granát a opál. Títo muži sú starostliví, nadaní, jemní a príjemní. Ďalšími pozitívami ich osobností sú aj zručnosť, šikovnosť a inteligencia. Na druhej strane bývajú aj dosť nevyspytateľní. Neboja sa povedať, čo si skutočne myslia, a preto sa niekedy stáva, že niekoho urazia. Často skrývajú svoje reakcie, aby nemuseli zaťažovať ostatných ľudí svojimi náladami. Ľudia si na nich väčšinou cenia zábavnosť a iróniu, ktorou vedia potrápiť nejedného človeka. V živote sa vedia správne zorientovať a málokedy si vyberú zlú stranu, no často ich stretne sklamanie.V oblasti zdravia sú veľmi odolní, ale mali by si dávať pozor na stravovanie. Toto meno je vhodné pre znamenia Kozorožec, Panna, Váhy, Blíženci."},
                new NameEntity {Value = "Hubert", NameDay = "3. november", Description = "Mužské meno Hubert má nemecký pôvod a v preklade znamená \"duchaplný\". Je talentovaný a ak nepodľahne alkoholu, dopracuje sa k úspechu vo veľmi krátkom čase. Vie, kde a ako sa dá dosiahnuť úspech, zisk, alebo osobná prestíž. Koná premyslene a rázne. V práci aj v láske sa drží jedného smeru a neodpočíva, kým nemá to, po čom túži. Na jeho náklonnosť sa môžete spoľahnúť a obrátiť sa na neho v akejkoľvek situácii. Neuteká pred zodpovednosťou, ani kritikou, je teda skoro ideálny. Má len jednu slabosť, a to sklon k alkoholizmu. Ak sa dá na pitie, nikdy sa mu nič nepodarí. Ovplyvňuje ho planéta Venuša. Jeho ochranné kamene sú karneol a avanturín."},
                new NameEntity {Value = "Hugo", NameDay = "1. apríl", Description = "Mužské meno Hugo má nemecký pôvod a jeho význam je \"duchaplný\". Toto meno má osobné číslo 9 – Génius. Muži menom Hugo síce pôsobia nedostupne, ale sú aj vytrvalí. Radi žijú v spoločnosti a hlavne radi prijímajú návštevy. Sú priateľskí, preto je ľahké s nimi nadviazať kontakt. Často bývajú v strese, nedokážu sa uvoľniť bez dôvodu. Hugovia potrebujú vedieť, že je všetko v poriadku, aby boli spokojní a nežili v strachu. Vo veciach vidí niekedy problémy, preto ho ľudia považujú za paranoidného. Čo sa týka ich citov, ťažko ich dávajú najavo a málokomu o nich povedia. Žijú s nedôverou a ironickým postojom k ostaným. Pre toto meno je vhodné znamenie : Kozorožec, Vodnár, Býk, Panna. Ich šťastné farby sú hnedá, fialová, čierna a tmavomodrá. Ochranné rastliny kostihoj a kokorík, ochranné kamene zafír, opál a granát."},
                new NameEntity {Value = "Ignác", NameDay = "31. júl", Description = "Mužské meno Ignác má latinský pôvod a jeho význam je \"neskôr narodený\", \"ohnivý\". Osobným číslom tohto mena je 2 - Dobrota. Ovplyvňuje ich planéta Mars. Šťastné farby sú fialová a svetlomodrá. Ochranné rastliny horčica a cesnak, kamene chalcedón a karneol. Títo muži sú otvorení. Dokážeme predvídať ich skutky. Na druhej strane bývajú aj dosť nesústredení. To sa prenáša do školy, zamestnania a života. Nevedia dokončiť jednu činnosť a hneď sa púšťajú do druhej. Sú stále nervózni a prenášajú to na svojich blízkych. Nedokážu prijať kritiku. Sú tvrdohlaví, neústupčiví a nezabúdajú na urážky. Zdravie majú dobré. Napriek tomu, že bývajú často nervózni, sú neobyčajne odolní. Toto meno je vhodné pre znamenia : Škorpión, Kozorožec, Váhy, Ryby a Vodnár."},
                new NameEntity {Value = "Igor", NameDay = "10. apríl", Description = "Mužské meno Igor je ruské meno islandského pôvodu a význam mena je \"boh Ingvio ochraňuje\". Osobným číslom pre toto meno je 6 – Nežnosť. To značí, že sú to veľmi zlatí, príjemní ľudia a plní radosti. Usilujú sa, aby vytvorili príjemnú spoločnosť aj pre druhých. Chcú, aby všetky veci dosiahli spravodliví, čo nie je vždy možné. Igorovia sú dosť emotívni, čo môže byť niekedy na ich škodu. Ak sa Igorom niečo nedarí, dokážu byť impulzívni a mrzutí. Toto meno je vhodné pre znamenia : Baran, Lev, Blíženci a Býk. Šťastné farby týchto mužov sú červená, fialová a oranžová."},
                new NameEntity {Value = "Iľja", NameDay = "20. júl", Description = "Mužské meno Iľja má hebrejský pôvod a znamená \"môj Boh\". Najčastejšie je zaužívané v rusky hovoriacich krajinách. Osobným číslom tohto mena je 9 – Génius. Ovplyvňuje ich planéta Jupiter a Mesiac. Šťastné farby sú azúrovomodrá, biela a krémová. Ochranné rastliny slez, sedmokráska, ochranné kamene avanturín, granát. Títo muži sú činorodí, zodpovední, trpezliví, vytrvalí a snaživí. Neboja sa vrhnúť aj do problémov, ktoré sa zdajú navonok neriešiteľné. Aj keď sa im nepodarí vyriešiť nejaké problémy, nemajú z toho ťažkú hlavu. Veľmi ťažko sa dajú ovplyvniť. Ak majú od prírody vlastníckeho ducha, nechcú si všetko privlastniť za každú cenu. Majú primeranú sebadôveru. Zdravie je celkom dobré. Pretože, ak sa už pre niečo rozhodli, nemôžu byť chorí. Veľmi dobre vedia odolávať únave. Toto meno je vhodné pre všetky znamenia."},
                new NameEntity {Value = "Imrich", NameDay = "5. november", Description = "Mužské meno Imrich je nemeckého pôvodu a v preklade znamená \"pracovitý vládca\". Snaží sa vždy konať dobre, citlivo, ale neznáša ak mu niekto odporuje. To aký je záleží od spoločnosti, v ktorej sa pohybuje, pretože je dosť ovplyvniteľný a snaží sa čím menej iným odporovať. Medzi priateľmi má dobré meno a vždy sa môže na niekoho obrátiť so žiadosťou o pomoc. V láske je prelietavý, nestály, ale tiež sa snaží zanechať svojim láskam len tie najlepšie spomienky. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene sú ametyst, achát a perla."},
                new NameEntity {Value = "Ivan", NameDay = "8. júl", Description = "Mužské meno Ivan má slovanský pôvod a význam mena je \"Boh je milostivý\". Osobným číslom tohto mena je 8 – Peniaze. Ich obľúbené farby sú zelená, modrá a žltá. Ochranné rastliny nechtík a podbeľ, ochranné kamene zafír, smaragd. Títo muži sú aktívni a majú železnú vôľu. Sú veľmi výbušní, statní, ale aj verní. V živote nedostanú nič zadarmo, všetko si musia vybojovať. V škole sa cítia dobre. Dokážu študovať dlhé roky a keď sú pripravení, tak začnú konať. Uplatnia sa v každom povolaní, no prevažne preferujú technické zamerania. Sú obdarení veľkým zmyslom pre zodpovednosť. Aby dosiahli svoj cieľ, často sa musia zriecť nejakého iného plánu. Ich vitalita je veľká, no majú sklon preceňovať svoje sily. Mali by byť opatrní hlavne na chrbticu a vyhýbať sa preťažovaniu svalov. Toto meno je vhodné pre znamenia: Býk, Blíženci, Rak, Panna, Kozorožec."},
                new NameEntity {Value = "Izidor", NameDay = "4. apríl", Description = "Mužské meno Izidor má grécky pôvod a význam mena je \"dar egyptskej bohyne slnka Izis\". Toto meno má osobné číslo 1 - Vodca. Izidorovia sa vždy snažia, aby konali správne. Podľa toho si aj vyberajú priateľov. Izidor nie je problémový typ muža, všetko si dopredu premyslí. Väčšinou je spokojný ako sa správa. Priateľstvo s Izodorom patrí k dôležitému, lebo vám môže veľmi pomôcť k úspechu. Ale pozor, nikdy nespochybňujte jeho skutky a činy, lebo on vie vždy, čo sa má daný čas, na danom mieste konať. Nemyslí len na seba a dokáže sa obetovať najprv pre druhých. Izidorovia sú veľmi zodpovední ľudia aj čo sa týka ich práce. Rýchlo sa prispôsobí daným podmienkam a má dobrú fantáziu, ale potrebujú byť dostatočne motivovaní. Pre toto meno je vhodné silné znamenie ako: Baran, Lev, Vodnár, Kozorožec, alebo Býk. Ich šťastné farby sú tyrkysová, biela, zelená a hnedozelená. Ochranné rastliny valeriána, čakanka, ochranné kamene citrín a jaspis."},
                new NameEntity {Value = "Jakub", NameDay = "25. júl", Description = "Mužské meno Jakub je starozákonné, má hebrejský pôvod a jeho význam je \"držiaci sa za pätu\", \"druhorodený\". Ovplyvňuje ich planéta Mars. Ich šťastná farba je červená. Ochranné rastliny púpava, chmeľ, kamene diamant, ametyst. Osobným číslom tohto mena je 6 - Nežnosť. Títo muži sú výkonní, presvedčiví a uvážliví. Majú výbornú pamäť, ktorú vedia využiť vo svoj prospech vo vhodnej chvíli. Na prvý pohľad vyzerajú, že sú menej schopní, ale zdanie klame. Ich reakcie sú tiché, ale môžu sa stať veľmi obávané. Dokážu sa ovládať a kontrolovať. Majú pevnú vôľu, čo využívajú v práci, a preto sú určite na čele tímu. V živote nie sú veľmi opatrní, ale vynahrádzajú si to špekulatívnosťou. Často žijú dvojakým životom. Jeden je verejný, veľmi plodný, druhý je tajný. Toto meno sa hodí pre znamenia Ryby, Váhy, Panna."},
                new NameEntity {Value = "Ján", NameDay = "24. jún", Description = "Krstné meno Ján má hebrejský aj nemecký pôvod a v preklade znamená \"Boh je milostivý\". Osobným číslom tohto mena je 7 – Tajomnosť. Ich obľúbené farby sú modrá a červená, ochranné rastliny púpava a chmeľ, ochranné kamene diamant a ametyst. Títo muži sú dosť aktívni a plní pohybu. Stále niečo chcú spoznávať a keď sa do niečoho pustia, vedia to dokončiť. Jánovia bývajú často nerozhodný a ich nálady sú dosť premenlivé. Nevedia si uceliť myšlienky o svojich názoroch, nevie si o sebe vytvoriť istý názor. Na jednej strane sú plní energie, extrovertní a niekedy sa úplne zmenia a správajú sa negatívne a pesimisticky. Vždy si idú za svojim cieľom, ale veľa krát im to dlho trvá. Týchto mužov je ťažké rozpoznať a nie je ľahké k nim nájsť cestu. Ale po dlhšom čase v nich uvidíte milých a šarmantných mužov a priateľov, ktorí sa za vás obetujú. Aj keď budú pôsobiť navonok tvrdo, v skutočnosti vo vnútri sú to oddaní a zraniteľní muži. Janovia pôsobia v celku ako inteligentní, ktorí logicky myslia a hľadajú skryté súvislosti. Životaschopnosť Jánov je dobrá, no stáva sa, že prejavujú isté obavy o svoje zdravie. Je to prijateľné meno pre každé znamenie, len jeho nositeľ musí odolať alkoholu a všetko bude v poriadku."},
                new NameEntity {Value = "Jarolím", NameDay = "30. september", Description = "Mužské meno Jarolím má grécky pôvod a v preklade znamená \"sväté meno\". V spojení s mužským charakterom vytvára nepokojnú osobnosť. Je nerozhodný, čomu sa má venovať, ako sa má správať,čo má uprednostniť, aby dosiahol úspech. Musí mať pri sebe organizačne nadaného človeka, priateľa, či priateľku, osobu, ktorá mu bude pomáhať pri rozhodovaní a nasmeruje ho správnym a výhodným smerom. Ak je riadený, dokáže neuveriteľné veci. Dosiahne všetko, čo potrebuje pre seba a svojich blízkych. Len nerád vymýšľa, plánuje a rozhoduje. Ovplyvňuje ho planéta Slnko. Jeho ochranné kamene sú jantár a topás."},
                new NameEntity {Value = "Jaromír", NameDay = "18. február", Description = "Mužské meno Jaromír má slovanský pôvod a význam mena je \"slávny silou\", \"jarý\". Jeho osobným číslom je 7 - Tajomnosť. Osobnosť s takýmto číslom je nadaná, ale záhadná a málo prístupná. Ich šťastné farby sú biela, zelená a modrá. Ochranné rastliny rumanček, biely hyacint a zvonček, ochranné kamene nefrit, perla a rubín. Ich rastlinným totemom je pŕhľava. Jaromírovia sú skôr rozdvojené osobnosti. Na jednej strane dokážu byť veľmi šťastnými, ale na druhej strane sa správajú zúrivo. Svojimi pocitmi sa len tak hocikomu nezverí. Dokonca, ich práve „ja“ dokážu dať najavo až po dlhom čase, ak sa dostane do kritických okolností. Preto by sa im ľudia mali vyhýbať v takýchto situáciach, ak sa nechcú dostať s nimi do rozporu. Jaromírovia sú ťažko ovládateľní. Čo sa týka ich citového života, sú panovační, ale vedia si zabezpečiť rodinu. V láske sú nestáli, ale ak ide o ich manželku, je pre nich jediná na svete, pokiaľ nemá dôvod na žiarlivosť. Pokiaľ ide o ich zamestnanie, v ktorom sa im nedarí, vzdajú sa ho a budú ho zamieňať. Ich snom je hrať v divadle, pretože sa radi predvádzajú. Záľubu majú aj v cestovaní. Jaromírovia by mali si dávať pozor na jedlo, ktoré jedia. Pre týchto mužov sú najvhodnejšie znamenia: Rak, Vodnár, Ryby, Panna a Kozorožec."},
                new NameEntity {Value = "Jaroslav", NameDay = "27. apríl", Description = "Mužské meno Jaroslav má slovanský pôvod a jeho význam je \"slávny silou\". Osobným číslom tohto mena je 5 - Riziko. Jaroslavovia pôsobia ako dokonalí a rozumní ľudia. Majú radosť žiť a vždy si idú za svojimi cieľmi, aj keď napohľad nemusia byť reálne, avšak oni ich aj tak dosiahnú. Jarovia potrebujú, aby ich ľudia riadili a pomáhali im. Títo muži sú skôr materialista a rozmyšľajú tak, aby všetko poporiadku rozobrali a zanalyzovali. Životaschopnosť Jaroslavov je v poriadku, sú plní vitalitnej sily. Mali by však konať uvážlivejšie, neodkladať povinnosti a vystríhať sa ponocovania. Toto meno je vhodné pre znamenia : Váhy, Panna, Býk, Kozorožec, Strelec, Vodnár a ich obľúbené farby sú žltá a biela, ochranné rastliny prvosienka a rebarbora, ochranné kamene opál a citrín."},
                new NameEntity {Value = "Jerguš", NameDay = "3. august", Description = "Mužské meno Jerguš je slovenskou podobou mena Gregor, ktoré je gréckeho pôvodu a v preklade znamená \"ostražitý\". Je nadaný, bystrý, pohotový a neváha, ak sa má rozhodnúť, či už v láske, alebo práci. Nedá sa nikým ovplyvniť, ani zmanipulovať. Ide vpred a aj keď to nie je za každú cenu, predsa dosiahne svoje. V jeho spoločnosti sa všetci cítia uvoľnene a príjemne, pretože má ľudí okolo seba rád. Ak potrebuje niekto z jeho známych pomoc, vždy mu pomôže. Niekedy kvôli svojim kamarátom zanedbáva rodinu a často sa kvôli iným odcudzí svojim blízkym. Vtedy je potrebné s ním \"zatriasť\", upozorniť ho na jeho omyl. Nikto mu nikdy nepomôže, iba jeho pokrvná rodina a práve na nej by mu malo čím viac záležať. Ovplyvňuje ho planéta Mars. Jeho ochranné kamene sú jaspis a diamant."},
                new NameEntity {Value = "Jozef", NameDay = "19. marec", Description = "Mužské meno Jozef má hebrejský pôvod a význam mena je \"Boh pridá, rozhojní\". Ovplyvňuje ich planéta Jupiter. Obľúbené farby týchto mužov sú modrá, žltá, fialová, ochranné rastliny žihľava, šalvia, kamene ametyst, smaragd a granát. Osobným číslom je 6 – Nežnosť. Títo muži sú vždy presvedčení o tom, čo v živote chcú. Vedia sa v každej situácií ovládať a byť pokojní. Jozefovia majú v živote šťastie a veľký predpoklad využiť všetky svoje prednosti k úspechu. Sú to starostlivé osoby, vždy pomôžu svojim blízkym. V práci sa vedia vždy uplatniť, získať si druhých na svoju stranu, ktorí ich budú vedieť podporiť v každej situácií. Mali by sa vyhýbať riskantným situáciám a viac odpočívať. Toto meno by nemalo byť spojené so slabšími znameniami, aby mohli bojovať s každou ťažkosťou a aby ich neúspech nezlomil. Preto toto meno patrí k znameniam : Baran, Býk, Lev, Vodnár, Strelec."},
                new NameEntity {Value = "Július", NameDay = "11. apríl", Description = "Mužské meno Július má latinský pôvod a význam mena je \"vlasatý\", \"mladý\". Osobným číslom tohto mena je 7 - Tajomnosť. Títo muži sú dosť tvrdí a rozvážní, aby odolávali ťažkým situáciam v živote. Aj keď navonok pôsobia ako úprimní ľudia, majú pred okolím tajomstva. Dokážu dosť ovplyňovať druhých, dokonca aj svojich blízkych. Na ľudí pôsobia veľmi milo a inteligentne, čo niekedy môžu zneužiť voči iným. Júliusovia si dobre pamätajú informácie a majú dobré myšlienky. Toto meno je vhodné pre znamenia : Škorpión, Váhy, Baran, Panna, Býk a Kozorožec. Ich šťastné farby sú tmavomodrá, ružová a červená."},
                new NameEntity {Value = "Juraj", NameDay = "24. apríl", Description = "Mužské meno Juraj je svätecké, má grécky pôvod a význam mena je \"roľník\". Osobným číslom tohto mena je 9 – Génius. Jurajovia sú výreční, ale aj dosť pokojní a citliví. Niekedy potrebujú, aby boli sami a inokedy chcú stráviť svoj čas v spoločnosti. Majú dosť originálne a pútavé nápady, ktoré im nakoniec prinesú úspech. Jurajovia chcú veľmi pomáhať aj druhým aj v oblasti kariéry a to je u nich chyba. Preto ich veľa ľudí využíva a ich myšlienky pre vlastné ciele. Lenže Jurajovia sú leniví a nemajú záujem si stáť za svojimi nápadmi. Iba prijímajú kritiku, ale nakoniec sa dočkajú všetkého, čo im patrí. Toto meno je vhodné pre znamenia : Baran, Býk, Strelec, Vodnár, Kozorožec. Obľúbené farby Jurajov sú žltá,červená, fialová a zelená, ochranné rastliny púpava, prvosienka a rozmarín, ochranné kamene ametyst a jaspis."},
                new NameEntity {Value = "Kamil", NameDay = "14. júl", Description = "Mužské meno Kamil na nás dýcha ďalekou orientálnou exotikou. Význam mena je \"chrámový sluha\". Osobným číslom tohto mena je 9 – Génius. Šťastné farby sú hnedá, tmavomodrá, zelená. Ochranné rastliny kostihoj, štiav, ochranné kamene zafír, achát a opál. Títo muži sú veľmi galantní a romantickí. Musia mať svoj život pevne v rukách, inak sa cítia nepríjemne. Sú to otvorení a spoločenskí ľudia, ktorí sa neboja rozprávať na rôzne témy. Majú veľa originálnych nápadov a predstáv, ktoré dokážu aj uskutočniť. Lenže nesmú podľahnúť alkoholu a iným návykovým látkam. Majú celkom odolné zdravie, ale mali by sa vyhýbať intelektuálnej preťaženosti a veľa spávať. Do jedálneho lístka by si mali zaradiť karfiol, kaleráb, mandle a hydinu. Toto meno je vhodné pre všetky znamenia."},
                new NameEntity {Value = "Karol", NameDay = "4. november", Description = "Meno Karol pochádza zo staronemčiny a v preklade znamená \"muž, manžel\" a vzniklo z neho aj slovo kráľ. Je rodeným mužom činu. Pracuje na tom, aby bol úspešný a aj keď sa mu to niekedy nedarí, nikdy sa nevzdáva. Má jednu slabosť - priveľa mudruje, premýšľa, kombinuje a plánuje. Preto sa mu občas stáva, že nevyužije možnosti, ktoré mu osud prináša. V láske je stály a aj keď rád riskuje, v dáva si pozor a snaží sa vždy budovať harmonický a stály vzťah. Ovplyvňuje ho planéta Jupiter. Jeho ochranné kamene sú ametyst, smaragd a tyrkys."},
                new NameEntity {Value = "Kazimír", NameDay = "4. marec", Description = "Mužské meno Kazimír má slovanský pôvod a v preklade znamená \"ten, kto kazí mier\". Osobné číslo tohto mena je 9 - Génius. Ich obľúbená farba je modrá a zlatohnedá. Ochranné rastliny ľubovník, zvonček, ochranné kamene jantár a achát. Títo muži sú silnými osobnosťami. Ak si niečo zaumienia, tak to aj dosiahnu. Bývajú zvyčajne veľmi pokojní, ale ak sa rozčúlia tak poriadne. V živote to ale nemajú jednoduché, budú musieť prejsť ťažkým období, aby sa dostali k tomu, po čom túžia. Niekedy sa im podarí získať úspech veľmi ľahko, no postupne sa im do cesty stavajú problémy a ťažkosti. Ak nebudú Kazimírovia dostatočne silní a precízni, tak to nebudú mať v prekonávaní prekážok ľahké a úspech nezískajú. Ak je toto meno spojené so slabým znamením, budú to mať Kazimírovia v živote náročné. Preto je vhodné pre znamenia : Baran, Býk, Lev, Panna."},
                new NameEntity {Value = "Klaudius", NameDay = "20. marec", Description = "Toto meno je vytvorené z mena rímskeho rodu \"Claudius\" a v preklade znamená \"chromý, krivý\". Ovplyvňuje ich planéta Merkúr. Ich šťastné farby sú červená, biela a zelená. Ochranné rastliny podbeľ, kôpor, kamene tyrkys, chalcedón. Títo muži si vždy musia povedať svoj názor. Klaudiovia sú buď najradšej na takom mieste, kde žijú a neradi ho opúšťajú, alebo sú odvážnejšími a stále utekajú. Sú to tvori ľudia s veľkou predstavou, preto sa často zameriavajú na módne návrhárstvo alebo na oblasť herectva, kde majú aj úspech. Muži s týmto menom musia vždy cítiť voľnosť vo svojom živote, ale na druhej strane, musia mať nejakú oporu, u ktorej sa môžu uchýliť. Neúspech v ich práci či v živote neznášajú dobre – vtedy potrebujú pomoc od svojich blízkych. No ak sa im začne dariť, bez akéhokoľvek rozlúčenia sa vytratia."},
                new NameEntity {Value = "Klement", NameDay = "23. november", Description = "Mužské meno Klement má latinský pôvod a v preklade znamená \"prívetivý\", \"láskavý\". Je muž činu, najprv koná a potom premýšľa. Jeho okolie ho pokladá za prudkého a neovládateľného človeka. V mladosti je búrlivý a nezvládateľný. Postupne si ujasní čo a ako by mal robiť, aby dosiahol úspech. Nič z toho, čo robí, nie je náhodné. Vždy to má nejaký háčik, nejaký zmysel, ktorý ostatným ostáva skrytý. Málokto sa v ňom vyzná. Je veľmi uzavretý a nerád odhaľuje svoje vnútro. V láske je opatrný a ťažko prístupný, preto s ním treba jednať v rukavičkách. Oplatí sa to, pretože pokiaľ ide o stálosť, je vzorom cností. Ovplyvňujú ho planéty Mars a Pluto. Jeho ochranné kamene sú opál a jaspis."},
                new NameEntity {Value = "Koloman", NameDay = "13. október", Description = "Mužské meno Koloman má keltský pôvod a v preklade znamená \"pustovník\". Toto meno je veľmi výhodné. Jeho nositeľ s ním skutočne nemá žiadne problémy. Dokáže sa postarať o stály prísun energie, pomáha svoju \"pánovi\" presadiť sa a získať si dobrých priateľov. Rodič, ktorý dá svojmu dieťaťu toto meno mu veľmi pomôže. Jeho dieťa dosiahne v živote vysoké miesto. Koloman vidí možnosti ostatným skryté a tak je vždy o krok vpredu. V láske je veľmi povrchný, najprv hľadá dobrodružstvo a až v neskoršom veku stabilitu. Nie je teda dobré byť jeho prvou láskou, radšej poslednou. Celkovo sa mu darí a jeho život je sledom malých úspechov a postupného bohatnutia. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú citrín, zafír a jaspis."},
                new NameEntity {Value = "Konštantín", NameDay = "19. september", Description = "Mužské meno Konštantín má latinský pôvod a v preklade znamená \"stály, pevný\". Tento muž je introvertný, uzavretý do seba, dáva prednosť vnútornému svetu pred stykom s vonkajším. Je nepriebojný, zato dosť ovplyvniteľný a zväčša precitlivený a pomstychtivý. Od detstva ho treba poúčať, čo značí \"tvoj blížny\", aké sú sociálne väzby a nevyhnutné hranice individuálnej slobody, ktorej prekročenie značí pošliapavanie slobody iného. Rád ničí a využíva svoje okolie, ale málokto sa v ňom vyzná, teda je ťažké sa im ubrániť. Ovplyvňuje ho planéta Slnko."},
                new NameEntity {Value = "Kornel", NameDay = "26. november", Description = "Mužské meno Kornel má latinský pôvod a je odvodené zo slova cornu, ktoré znamená \"roh\". Nemá to v živote ľahké. Je dosť kolísavý, ponáhľa sa s vyriekaním úsudkov o svojom okolí a nikdy nevydrží dlho pri jednom názore. Život s ním je ťažký, pretože sa mení ako aprílové počasie. Ak si v mladosti nájde vhodnú partnerku, ktorá mu pomôže nájsť rovnováhu a dodá mu sebavedomie, tak sa dočká úspechu raz dva. Ovplyvňuje ho planéta Mesiac. Jeho ochranné kamene sú nefrit a perla."},
                new NameEntity {Value = "Kristián", NameDay = "19. október", Description = "Mužské meno Kristián má grécky pôvod a jeho význam je \"kresťan, Kristovi zasvätený\". Je dosť inteligentný, ale nemá ani vytrvalosť, ani snahu niečo obetovať. Snaží sa ísť cestou čo najmenšieho odporu, využívať iných na to, aby sa dostal do popredia. Neváha ani pred zneužitím tajomstiev iných ľudí, takže by sme si na neho mali dávať pozor. Nemá čas čakať na postupný úspech a najmenej sa spolieha na svoju vlastnú pracovnú výkonnosť. Nerád ubližuje iným, no ak sa dostane do úzkych, tak na všetko zabudne. Kristián vie byť skvelý, ak má všetkého dostatok, no ak mu niečo chýba, stane sa z neho pravý opak. Jeho ochranné kamene sú zafír, sardonix a citrín."},
                new NameEntity {Value = "Krištof", NameDay = "28. júl", Description = "Mužské meno Krištof má grécky pôvod a význam mena je \"nesúci Krista\". Osobným číslom tohto mena je 1 - Vodca. Ovplyvňuje ich planéta Mars. Šťastná farba je fialová. Ochranné rastliny prvosienka a rozmarín, kamene karneol a jaspis. Títo muži sú veľmi otvorení, pracovití a citliví. Dokážu si dobre premyslieť, či to, čo chcú, im stojí za vynaloženie námahy. Ak áno, investujú veľa času a námahy na realizáciu. Ak by bol nejaký problém v podnikaní, upozorní vás na všetky nebezpečenstvá, ktoré môžu nastať. V práci chcú byť tí najlepší. Nie sú ľahko prístupní a často sa upriamujú len na seba. Zdravie majú pozoruhodné, ale sú náchylní na prechladnutie. Toto meno je vhodné pre znamenia : Býk, Ryby, Váhy, Panna."},
                new NameEntity {Value = "Ladislav", NameDay = "27. jún", Description = "Mužské meno Ladislav má slovanský pôvod a význam mena je \"slávny vládca\". Osobným číslom tohto mena je 7 - Tajomnosť. Ich obľúbenou farbou je fialová a hnedá, ochranné rastliny prvosienka a horčica, ochranné kamene chalcedón a karneol. Ladislavovia sú plní energie a silnými osobnosťami. Do práce sa púšťajú naplno s cieľom dosiahnuť úspech. Sú aktívni, ale niekedy potrebujú viac času na to, aby spoznali sami seba. Musí prekonať svoje prvé neúspechy a ťažkosti, aby sa uplatnil a vedel sa úplne obracať v živote. Vedia sa adaptovať určitým okolnostiam a podmienkam. Sú spoločenskí a komunikatívni a radi sú stredobodom pozornosti. Neriadia sa nejakými zásadami a nedá sa povedať, že sú vernými a dôveryhodnými ľuďmi. Ale aj napriek tomu si vedia užívať a prikrášliť si svoj život. Nie je vhodné, aby sa zaplietli do nejakých podvodoch, lebo nakoniec budú mať z toho veľké problémy a zaplatí za ne. Toto meno je vhodné pre Kozorožca, Pannu, Barana a Blíženca, lebo tieto znamenia ho budú viesť správnym smerom."},
                new NameEntity {Value = "Leonard", NameDay = "16. august", Description = "Mužské meno Leonard má nemecký pôvod a v preklade znamená \"silný (ako) lev\". Je vždy na správnej strane, v spojení so správnymi ľuďmi a so správnymi názormi. Nikdy neurobí nič bez toho, aby to nemal vypočítané. Každý krok je poistený a všetky náklady spočítané. Byť s ním zadobre je veľmi dôležité, lebo jeho pomocou sa môžete dostať vysoko, za hranice svojich možností a bez veľkej námahy. Ak sa bojíte bojov so životom, za jeho chrbtom nájdete pokoj a harmóniu. Lenže zaplatíte za to vysokú cenu. Stratíte celkom svoju osobnosť. Nikdy nesmiete nič kritizovať, ani pochybovať o jeho činoch. Vie vždy najlepšie, čo je treba robiť a ako konať, aby sa každý mohol tešiť z úspechu. Nie je sebecký, ale vie obetovať najskôr iných a až nakoniec niečo zo svojho majetku, keď je v ohrození. Nikto nie je pre neho dôležitý, iba jeho rodina. Je dobrým manželom, no málokedy je doma. Stále sa snaží zarobiť pre rodinu dostatok peňazí a má rád slobodu, takže sa domov vracia vždy len na krátky čas. Ovplyvňuje ho planéta Venuša. Jeho ochranné kamene sú koral a opál."},
                new NameEntity {Value = "Leopold", NameDay = "15. november", Description = "Meno Leopold má latinizovaný nemecký pôvod a v preklade znamená \"v ľude odvážny\". Často je umelecky nadaný. Rád sa učí na chybách iných a tak možno o ňom povedať, že je skoro dokonalým mužom. Naozaj má k dokonalosti najbližšie, ale aj tu je jedno ale. Nevie zniesť odmietnutie, nech je to v akomkoľvek smere. Kvôli tomu sa radšej vzdáva, ak vidí na svojej ceste veľa prekážok. Nerád oslovuje neznáme ženy, nekoná impulzívne a neznáša kreatívne porady, kde sa diskutuje o nápadoch. Vyhýba sa akejkoľvek úlohe, v ktorej by musel kritizovať iných. On sám podľa seba súdi iných a tak uvažuje, že ak nebude on kritizovať iných, aj on sa vyhne kritike. Je však z mužov najbližší k ženskému mysleniu a dokáže sa veľmi citlivo stavať ku problémom svojej vyvolenej. Ovplyvňuje ho planéta Slnko. Jeho ochranné kamene sú jantár a olivín."},
                new NameEntity {Value = "Levoslav", NameDay = "2. október", Description = "Levoslav je novšie slovanské meno a jeho význam je \"leva oslavuj\", \"silný ako lev\". Levoslav je materialista a pôžitkár. Úspešne študuje klasické aj technické predmety. Stáva sa úradníkom, učiteľom, spisovateľom alebo obchodníkom. Je väčšinou \"macher\" vo svojom odbore. Rád sa drží hesla: Každý sám pre seba, iba slnko pre všetkých. Jeho životaschopnosť je vynikajúca, je plný životnej sily. Mal by byť ale viac disciplinovaný pri stravovaní. Ovplyvňuje ho planéta Venuša. Jeho ochranné kamene sú achát a zafír."},
                new NameEntity {Value = "Ľubomír", NameDay = "13. august", Description = "Mužské meno Ľubomír má slovanský pôvod a význam mena je \"mierumilovný\". Je nadaný a talentovaný, ale nie vždy dokáže bystro a rýchlo reagovať. Nevie sa koncentrovať a často zabúda aj dôležité udalosti. Aj keď nepatrí k pozorným ľuďom, je úprimný a priamy. Nie je to z nejakej vrodenej mravnosti, ale preto, že by si žiadne klamstvo nepamätal dlho a tak si šetrí svoju pamäť na iné záležitosti. V láske je prelietavý. Nikdy sa nevenuje jednej partnerke dlhý čas, potrebuje zmenu a nezáväzné vzťahy. Ak sa však rozhodne oženiť sa, bude pár rokov tým najvernejším, hlavne ak ho jeho partnerka bude podporovať v práci. Jeho pracovné nápady sú neuveriteľne originálne a šetria nielen čas, ale aj ľudí. Ovplyvňujú ho planéty Jupiter a Neptún. Jeho ochranné kamene sú ametyst, smaragd a rubín."},
                new NameEntity {Value = "Ľubor", NameDay = "24. september", Description = "Mužské meno Ľubor má český pôvod. Ľubor vo voľnom preklade znie ako \"mierumilovný\". Ľuborova povaha mu neumožňuje sa presadzovať za každú cenu a tak je väčšinou na \"chvoste\". Nevie sa uplatniť v žiadnom povolaní, ktoré si vyžaduje boj, alebo postup, zaplatený slzami a nešťastím iných. Najradšej by bol učiteľom, alebo pracoval v zdravotníctve, kde by mohol využiť svoju dobrotu a snahu pomáhať iným. Ak si však vezme panovačnú ženu, potom môže pod jej vplyvom získať dobrú pozíciu a zarábať pre svoju rodinu. No len vtedy, ak je motivovaný stále a stále pod dozorom niekoho, kto zaňho vyrieši dilemy jeho povahy."},
                new NameEntity {Value = "Ľuboš", NameDay = "24. september", Description = "Mužské meno Ľuboš má český pôvod. Ľuboš vo voľnom preklade znie ako \"ľúbený, milý\". Muž s menom Ľuboš je vítaným a obdivovaným spoločníkom. Má v sebe veľkú príťažlivosť a vitalitu. Nikdy nemá problém presadiť sa, alebo sa stať uznávaným. Jeho priania a túžby sú reálne, dokáže ich splniť a aj keď narazí na prekážku, nevzdáva sa ihneď. Má v sebe niečo neodolateľné, aj keď to nie je badateľné na prvý pohľad. V citoch je stabilný a verný, nikdy sa nezníži k pokrytectvu, alebo pretvárke. Jeho partnerka si teda vždy bude môcť byť istá jeho pravdovravnosťou a vernosťou."},
                new NameEntity {Value = "Ľuboslav", NameDay = "20. september", Description = "Ľuboslav je novšie meno slovanského pôvodu s významom \"ľúbosť sláv\", \"oslavuj\" a \"ľúb slávu\". Keď tento muž niečo chce, dosiahne to, niekedy aj silou. Zriedka sa nahnevá, ale potom to už stojí za to. Od detstva robí rodičom problémy. Potrebuje disciplínu a nesmiete sa nechať ním ovládnuť. Má sklon hneď a zaraz povedať \"nie\", a keďže ho skoro vôbec nemožete ovplyvniť, nie je ľahké prinútiť ho, aby zmenil názor. Ľuboslav všetko vzťahuje na svoju osobu, často je zaujatý a oplýva takmer arogantnou sebadôverou. Rád všetko dramatizuje a neoplatí sa mu príliš dôverovať. Ovplyvňujú ho planéty Mesiac a Merkúr. Jeho ochranné kamene sú nefrit, rubín a achát."},
                new NameEntity {Value = "Ľudomil", NameDay = "14. september", Description = "Mužské meno Ľudomil má slovanský pôvod a voľný preklad mena je \"ľudu milý\". Občas sa správa ako prírodný živel. Ničí a búra všetko, čo mu príde do cesty, ak mu to prekáža v jeho plánoch. Ťažko sa s ním vychádza, ak mu hoci len raz budete oponovať, potom už nikdy nezískate jeho plnú dôveru. Je veľmi citlivý na svoje \"JA\" a svoje výkony, potrebuje stále chválu a lichôtky. Je dosť nevyspytateľný, no jeden kľúč k jeho povahe je úplne zrejmý - je to sebec a vždy stavia na prvé miesto sám seba. Ak sa budete na jeho konanie dívať takto, potom je vám jasný každý jeho krok a budete mu viac rozumieť. V láske je ochotný sa zriecť mnohého, ale nie všetkého. Jeho žena teda bude mať možnosť ho zmeniť, no nie celkom. Ovplyvňuje ho planéta Mesiac. Jeho ochranné kamene sú nefrit, perla a rubín."},
                new NameEntity {Value = "Ľudovít", NameDay = "25. august", Description = "Mužské meno Ľudovít je nemeckého pôvodu a v preklade znamená \"slávny bojovník\", \"preslávený v boji\". Osobným číslom tohto mena je 5 – Riziko. Ovplyvňuje ich Mesiac. Šťastné farby sú modrá a zelená. Ochranné rastliny repík a sedmokráska, ochranné kamene avanturín, karneol a achát. Títo muži sú intuitívni a emotívni. Sú obdarení veľkou tvorivosťou a prestaviteľnosťou. Majú pozoruhodné myšlienky, ktoré dokážu uviesť do reality. Veľkým problémom tohto mena je sebeckosť. Ľudovítovia sa neradi delia o niečo. Ich vystupovanie je na vysokej úrovni, preto sa s nimi ťažko vychádza. Majú sklon spoliehať sa na druhých. Preto ich treba viesť k tomu, aby začaté úlohy dotiahli až do konca. V oblasti zdravia sú odolní. Len by si mali dať pozor na stravovanie. Toto meno je vhodné pre znamenia rak, Lev, Panna a Ryby, vhodnou farbou je zelená."},
                new NameEntity {Value = "Lukáš", NameDay = "18. október", Description = "Mužské meno Lukáš má latinský pôvod a v preklade znamená \"pochádzajúci z Lukánie, kraja v južnom Taliansku\". Nie je typickým predstaviteľom muža, ktorý miluje riziko. Riskuje rád, ale nikdy nie až \"na doraz\". Vždy má v zálohe nejaký tajný zdroj financií, takže nikdy nie je celkom na mizine. Ak má okolo seba tých správnych ľudí, dokáže sa obetovať kvôli blahobytu iných. Býva obľúbencom osudu, hviezdy mu prajú a tak by sa nemal vzdávať, aj keď má pocit, že sa všetko na neho rúti. Na rozdiel od ostatných mien, jeho meno mu prináša vždy viac ziskov, ako strát a pomôže mu dosiahnuť to, čo si predsavzal. Jeho ochranné kamene sú smaragd, zafír a ruženín."},
                new NameEntity {Value = "Marcel", NameDay = "20. apríl", Description = "Mužské meno Marcel má latinský pôvod a jeho význam je \"bojovník\". Osobným číslom pre toto meno je 8 – Peniaze. Marcelovia sú nestáli a rozhodujú sa narýchlo. Sú úprimní a rafinovaní. Aj keď sú naladoví a vedia využiť iných, sú dobrí sprostredkovatelia. Rád dáva najavo, keď sa mu darí a má dobré postavenie v zamestnaní. Dobre si zabezpečuje svoje miesto a pomôže aj druhý, ale samozrejme niečo bude za to chcieť. Je to jednoduchý človek, s jednoduchými životnými túžbami, ale aj so silou na ich dosiahnutie. K ich lepšiemu zdraviu pomôže viac spánku. Toto meno je vhodné pre znamenie : Strelec, Býk, Blíženci a Baran. Ich šťastné farby sú fialová, modrá, oranžová a žltá, ochranné rastliny žihľava, skalica a šalvia, ochranné kamene ametyst a tyrkys."},
                new NameEntity {Value = "Marek", NameDay = "25. apríl", Description = "Mužské meno Marek má latinský pôvod a v preklade znie \"bojovník\". Osobným číslom tohto mena je 4 - Sila. Marekovia majú osobitný charaketer. Sú všímaví a často sledujú správanie okolia. Ak má byť Marek silným mužom, musí nájsť správny smer. No však bude to pre neho veľmi ťažké,kým ho nájde. Má náklonnosť k smútku a často sníva, preto má problém zniesť životnú realitu. Svoj neúspech nesie dosť zlé, čo by sa mu nemalo ako mužovi stávať. Kvôli niektorým veciam alebo utrpeniu sa dokáže trápiť dosť dlhý čas. Tieto skutočnosti by mal odstrániť, ak chce byť silným človekom. Až v pokročilom veku, keď bude mať za sebou rôzne skúsenosti, získa úspech a pochvalu. Marekovia by nemali sľubovať, čo nebudú môcť splniť. Zdravie majú v celku dobré. Znamenia, ktoré sa hodia k tomuto menu sú: Škorpión, Býk, Lev, Panna. Ich obľúbené farby sú tmavomodrá, červená a biela, ochranné rastliny estragón a palina dračia, ochranné kamene achát, karneol a opál."},
                new NameEntity {Value = "Marián", NameDay = "25. marec", Description = "Mužské meno Marián má latinský pôvod a jeho význam je \"patriaci rímskemu rodu Mariovcov\". Ovplyvňuje ich planéta Mars. Šťastné farby sú fialová, modrá, červená. Ochranné rastliny púpava, chmeľ, ochranné kamene diamant a jaspis. Osobným číslom tohto mena je 4 - Sila. Mariánovia potrebujú, aby ľudia v ich okolí boli oddaní. Sú to spôsobilí muži, ktorí si pevne stoja za svojimi názormi. Títo muži dokážu byť aj citliví, vedia pochopiť a pomôcť druhým. Od svojich blízkych požadujú, aby boli úprimní, zábavní a verní a vtedy budú Mariánovia pokojní a chápajúci. Ak sa im prestane dariť, začnú cúvať a byť zatrpknutí. Mariánovia majú výbornú vitalitu. Aby nemali problémy vo svojom zdraví, mali by konzumovať veľa surovej zeleniny, kuracieho mäsa a rýb. Toto meno je vhodné pre znamenia : Rak, Blíženci, Panna, Vodnár, Kozorožec."},
                new NameEntity {Value = "Mário", NameDay = "19. január", Description = "Mužské meno Mário má latinský pôvod a je domácou podobou rímskeho rodového mena Marius, ktoré znamená \"muž\". Ich šťastná farba je modrá. Ochranné rastliny citrónovník, modrý hyacint, ochranné kamene achát a granát. Máriovia, ktorí oslavujú meniny spolu s Drahomírami. Títo muži radi dávajú najavo akí sú si istí a akú majú odvahu. Máriovia vždy musia mať pravdu a posledné slovo. Veľmi často ich zaplaví neistota a istá nesmelosť, ktorú si vyčítajú. Máriovia radi od mála študujú a učia sa. To im aj v živote dlho vydrží. Budú vždy si v škole vždy svedomito plniť svoje úlohy. Hodia sa k ním povolanie pedagóga alebo aj technika. V ich zdraví ich trápi hlavne bolesť hlavy a niekedy nevedia byť pokojní, tak podliehajú nervozite."},
                new NameEntity {Value = "Martin", NameDay = "11. november", Description = "Mužské meno Martin má latinský pôvod a v preklade znamená \"zasvätený bohovi Marsovi\", v prenesenom význame \"bojovný\". Nájde si aj v tom najneprehľadnejšom svete svoju cestičku na vrchol a uspeje aj v tých najťažších podmienkach. Nevidí v ničom závažný problém. Dokáže sa postarať o seba a svojich blízkych. Koná rýchlo, s bystrým úsudkom a prehľadom o tom, čo je momentálne najdôležitejšie. Je rodeným fatalistom. Díva sa na svet s odstupom a v ničom nevidí neprekonateľnú prekážku. Nič si priveľmi nepripúšťa a má veľkú sebadôveru. Už v detstve má jasné svoje ciele a snaží sa ich dosiahnuť. No ak sa to nedá a on vyčerpá všetky možnosti, potom sa svojich snov pokojne vzdá a pustí sa do nových plánov. Platí to pre súkromie, aj pre prácu. Ovplyvňuje ho planéta Saturn. Jeho ochranné kamene sú zafír a opál."},
                new NameEntity {Value = "Matej", NameDay = "24. február", Description = "Mužské meno Matej je novozákonné, má hebrejský pôvod a v preklade znamená \"dar boha\". Toto meno znamená symbolicky znalosť tajov sveta. Ich šťastné farby sú svetlozelená, krémová, strieborná. Ochranné rastliny konvalinka, rumanček, zvonček, biela ľalia, ochranné kamene achát, perla. Ich rastlinný totem je figovník, strom, ktorý bol podľa. Písma od vekov symbolom múdrosti a objavov. Jeho osobným číslom je 6 – Nežnosť. Je to typ, ktorý je milý a dobrý, ktorý sa dokáže aj ospravedlniť za svoje činy a skutky. Matejovia sú výreční a musia byť za každú cenu oboznámení o všetkom. Radi sa starajú do vecí iným a nakoniec každého využije, čo niekedy nemusí dopadnúť dobre. Matej je síce otvorený, ale o svojom citovom živote nedajú nikomu vedieť. V láske je neobozretný. Je mu jedno, v akom vzťahu je žena, o ktorú má záujem. A tak dosť často sa stáva, že Matej narobí problémy v iných harmonických manželstvách. Má vysoké sexuálne požiadavky, preto potrebuje ženu, ktorá bude aktívna. Ak ho budeme trošku sledovať, bude tým najlepším a najdokonalejším mužom. Toto meno je vhodné pre znamenia : Rak, Panna, Vodnár, Ryby, Kozorožec a jeho farbou je žltá."},
                new NameEntity {Value = "Matúš", NameDay = "21. september", Description = "Mužské meno Matúš je novozákonné, má hebrejský pôvod a v preklade znamená \"dar boha\". Je typom muža, ktorý býva najradšej sám. Nemá rád veľkú spoločnosť a hlučných ľudí. Vyhľadáva radšej pokoj a samotu. Jeho myšlienky sa veľmi líšia od myšlienok jeho okolia a tak sa nevie zapájať do príliš obyčajných rozhovorov. Mnohokrát býva považovaný za nudného a nezáživného spoločníka. To však nie je pravda, on sa snaží a je priateľský doslova ku každému, ak len vycíti záujem a úprimnosť od toho druhého. V citoch je spoľahlivý a uvážlivo si vyberá tú pravú. Nikdy by ju nepodviedol, ani nezradil, ak aj ona je k nemu úprimná a je mu verná."},
                new NameEntity {Value = "Maximilián", NameDay = "12. október", Description = "Mužské meno Maximilián má latinský pôvod a v preklade znamená \"najväčší, významný\". Mal by konať až po dôkladnom premyslení svojej situácie, pretože býva dosť naivný a nevidí do budúcnosti. Volí tú najkratšiu cestu k úspechu a keď sa náhodou vyskytnú prekážky, tak sa radšej vzdá. Nerád bojuje, intriguje, či riskuje. Bol by najradšej, ak by sa všetko dialo bez veľkej námahy. Nehodí sa na prácu, ktorá si vyžaduje fyzickú námahu, ale ani prílišné premýšľanie mu nevyhovuje. Je to spoločenský muž, ktorý si rád užíva, no nerád platí svoje útraty. Najlepšie by bolo, ak by sa mohol stať vydržiavaným mužom - gigolom. Život s ním nebude pre nikoho ľahký, najmä ak si vyberie slabšiu partnerku, ktorá ho nedokáže zvládnuť a motivovať k vyšším výkonom. Ovplyvňuje ho planéta Slnko. Jeho ochranné kamene sú kryštál, granát a ónyx."},
                new NameEntity {Value = "Medard", NameDay = "8. jún", Description = "Mužské meno Medard má germánsky pôvod a jeho význam je \"mocný\", \"udatný\". Osobným číslom tohto mena je 8 – Peniaze. Ich obľúbené farby sú fialová, červená a žltá. Ochranné rastliny žihľava, skalica a slez, ochranne kamene ametyst a smaragd. Medardovia vedia čo chcú od života. Sú pracovití a vždy si nájdu prácu, kde by si mohli zarobiť. Málokedy pracujú osamote, najradšej sú, keď im niekto pomáha. Dokážu ľudí ohodnotiť aj pomôcť docieliť im úspech. Títo muži sú pre mnohých príťažlivými. Ich pracovné úsilie na nich dobre nevplýva, mali by v zamestnaní trošku spomaliť. Často ponukám odolávajú lebo sú málo ovplyvniteľní. Medardovia sú silnými osobnosťami a často obdarovaní múdrosťou. Budú od vás vždy žiadať veľa, no aj veľa od nich dostanete. Ich zdravie je obyčajne výborne, lebo raz a navždy sa rozhodli, že ak majú naozaj niečo urobiť, niet času byť chorým. Toto meno je vhodné pre znamenia Strelec, Vodnár, Baran a Býk."},
                new NameEntity {Value = "Metod", NameDay = "5. júl", Description = "Mužské meno Metod má grécky pôvod a význam mena je \"metóda\". Sú ako šípy, poslovia, ich totemovým zvieraťom je kôň. Títo muži sú intuitívni a spoločenskí. Matka príroda ich obdarila veľkou emotívnosťou, obdivuhodnou aktivitou a rýchlymi reakciami. Majú potrebu ľuďom radiť, no ak sa im to nepodarí, tak si myslia, že sú zbytoční. Majú sklon sa predvádzať. Metodovia sú síce veľmi srdeční, ale ich priateľstvo nie je trvalé. Neradi sa dlho stretávajú s tými istými ľuďmi. Často majú pocit, že musia splniť nejaké poslanie. Na jeho dosiahnutie sú ochotní urobiť čokoľvek. V oblasti zdravia sú celkom odolní, aj keď majú občas ťažkosti. Zvyknú mať problémy s krvným tlakom a priberaním. Rýchlo sa dokážu unaviť, preto potrebujú dostatok spánku."},
                new NameEntity {Value = "Michal", NameDay = "29. september", Description = "Mužské meno Michal má hebrejský pôvod a v preklade znamená \"ako Boh\", \"Bohu podobný\". Michal je muž plný rozporov a takmer nikto sa v ňom nedokáže orientovať. Hrá rád divadlo a pôsobí ako tvrdý chlapík. Stačí dlhšia snaha a zistíme, že je to citlivý romantik. Snaží sa učiť z chýb iných a tak je ťažké nájsť v ňom toho pravého a skutočného muža. Je skrytý pod maskou a nerád dáva možnosť iným, aby ho zranili. V láske je opatrný a stály, keď už sa rozhodne, tak to platí na dlhý čas. Platí to aj o jeho vzťahoch k priateľom, správa sa akoby otvorene a veselo, no pritom len skúma reakcie ľudí okolo seba a postupne si vyberá z nich tých, ktorí mu vyhovujú. Ovplyvňujú ho planéty Merkúr a Venuša. Jeho ochranné kamene sú smaragd, ruženín, zafír."},
                new NameEntity {Value = "Mikuláš", NameDay = "6. december", Description = "Mužské meno Mikuláš pochádza z gréčtiny a v preklade znamená \"víťaz\". Často sa stáva obeťou iných. Nevie si sám poradiť so svojimi nápadmi a originálnymi myšlienkami a tak sa obracia o pomoc k iným. Pretože sa málo vyzná v ľuďoch, stáva sa, že ho oklamú, zneužijú a jeho nápady si prisvoja. Jemu je niečo takéto cudzie. Nevie si predstaviť klamstvo a podvod, nikdy by sa k tomu neuchýlil. Kým precitne a začne svoj život brať aj s tienistými stránkami, prejde dlhý čas. Tomu, kto ho klamal nič neodpúšťa a ak sa sklame raz, už nikdy mu nebude dôverovať. Platí to pre prácu, obchodnú sféru, lásku aj city. Má rád priamu hru a ľudí, ktorí ju dokážu hrať. Neznáša \"premenlivých\" ľudí. Najradšej by bol, ak by všetci konali správne. Ovplyvňuje ho planéta Jupiter. Jeho ochranné kamene sú ametyst a smaragd."},
                new NameEntity {Value = "Milan", NameDay = "27. november", Description = "Mužské meno Milan má slovanský pôvod a v preklade znamená \"milý\". Ak sa vyhne alkoholu, nikdy nič nebude pre neho nedosiahnuteľné, či už láska, alebo kariéra. Stačí, ak si všetko premyslí a dokáže čo len bude chcieť. Jeho názory sú dobré a znalosti, ktoré potrebuje, si osvojí veľmi rýchlo. V jeho spoločnosti sa nikto nenudí, vždy sa mu darí a má veľa priateľov. V láske dlho trvá, kým nájde tú pravú. Je obeťou rôznych intríg a klebiet, kým si nájde tú pravú. Svoje rodinné šťastie si vždy žiarlivo strážiť a beda tomu, kto v ňom prebudí zúrivosť. Ovplyvňuje ho planéta Saturn. Jeho ochranné kamene sú zafír a achát."},
                new NameEntity {Value = "Miloš", NameDay = "23. január", Description = "Mužské meno Miloš má slovanský pôvod a v preklade znamená \"milý\". Osobným číslom tohto mena je 4 - Sila. Ochranné rastliny majú ľubovník a kôpor, ochranné kamene olivín, jantár a granát. Obľúbené farby týchto oslnivých mužov sú hnedá, medená a zlatá. Najviac ich ovplyvňuje planéta Slnko. Títo muži pôsobia veľmi suverénne a vyrovnane. Vždy vedia, čo chcú a ako to chcú dosiahnuť. Ľudia, s ktorými trávia čas veľmi dobre poznajú a nemajú problém prijať od nich iniciatívu. Milošovia sú silnými osobnosťami, takže idú si pevne za svojim cieľom. Pred tým ako budú šťastní a úspešní, musia prejsť trápením a zlými skúsenosťami. Ich budúcnosť je kolísavá, raz sú hore, raz dole, ale nakoniec sa dostatnú ďaleko. Ich presvedčenie je silné ako ich osobnosť. Milošovia by sa mali vyhýbať klamstvám a pretvárkam, lebo nakoniec oni zaplatia za svoje činy ale aj za druhých. Ich zdravie je celkom dobré, aj keď priveľmi pracujú a vedú život nepravidelne. Toto meno je vhodné pre Kozorožca, Pannu, Barana a Blíženca, lebo tieto znamenia ho budú viesť správnym smerom."},
                new NameEntity {Value = "Miloslav", NameDay = "3. júl", Description = "Mužské meno Miloslav má slovanský pôvod a význam mena je \"slávny milosťou\" alebo \"milujúci slávu\". Osobným číslom tohto mena je 4 – Sila. Ovplyvňuje ich planéta Mars. Ich šťastné farby sú červená, fialová, žltá a ružová. Ochranné rastliny púpava, prvosienka, ochranné kamene diamant a ametyst. Títo muži sú rozvážnymi a vždy vedia, čo chcú. Pred tým ako sa do niečoho pustia, si najprv všetko dobre premyslia. Miloslavovia sú otvorení muži, vždy povedia úprimne čo si myslia. Ale čo sa týka ich pocitov a zámerov, nikdy o nich nehovoria. Ľudia ich majú radi, lebo pôsobia dôveryhodne. Aj keď nenápadne ale nakoniec ľudí v ich okolí zneužijú, lebo chcú dosiahnuť úspech a zbohatnúť na úkor iných. Na druhej strane Miloslavovia niekedy radšej budú v pozadí a nenápadní. Požadujú mať pri sebe dobrých kolegov, aby vedeli všetko zvládnuť. Ale ak ich niekto bude ovládať a budú spolupracovať so zlými ľuďmi, tak sa nedostanú ďaleko. Miloslavovia sa často venujú rozvíjaniu duševných schopností, ich inteligencia je vysoká. V oblasti zdravia nie sú odolní. Majú problémy s bolesťami hlavy, hlasivkami, čo im môže spôsobovať hlavne nepravidelné stravovanie. Títo muži potrebujú dostatočný spánok. Toto meno je vhodné pre znamenia Baran, Blíženci, Býk, Vodnár, Ryby a Strelec."},
                new NameEntity {Value = "Miroslav", NameDay = "29. marec", Description = "Mužské meno Miroslav má slovanský pôvod a význam mena je \"mier slávi\", \"mierumilovný\". Ovplyvňuje ich planéta Saturn a Urán. Ich šťastné farby sú modrá, čierna, zelená, ochranné rastliny borievka, nezábudka, ochranné kamene olivín a smaragd. Osobným číslom tohto mena je 3 – Schopnosť. Títo muži sú veľmi usilovní a schopní, ktorí dokážu dosiahnuť, po čom túžia. Miroslavovia nesnívajú, vždy žijú v skutočnom svete. Pre nich nie je dôležitý duchovný život, ale materialisticky. Vedia si ho aj užívať naplno. Vždy sa snažia nejaký cieľ dosiahnuť, čo sa im aj podarí. Nikdy sa nezľaknú nejakej prekážky, budú o svoje túžby naďalej a tvrdo bojovať. Títo muži sú správni – vždy sa snažia byť dochvíľni a milí, aj keď tak niekedy nepôsobia. Miroslavovia občas si neveria, preto musia vynaložiť väčšie úsilie na dosiahnutie úspechu, čo im uberá energiu. Mali by sa šetriť, aby ich vitalita bola dobrá. V zdraví majú problémy skôr psychického pôvodu. Mali by si dávať pozor najmä na pečeň a pľúca. Je veľmi podstatné spojiť toto meno so silnými znameniami, ako sú: Lev, Baran, Strelec a Vodnár, aby sa predišlo problémom."},
                new NameEntity {Value = "Mojmír", NameDay = "14. august", Description = "Má slovanský pôvod a jeho význam je \"(buď) môj mier, svet\". Pôsobí veľmi uzavreto a nikto sa k jeho vnútorným pocitom nedostane ľahko. Nedôveruje ľuďom a má veľkú dávku sarkazmu. Nevie sa uvolniť, ani sa bezdôvodne smiať. Vždy vo všetkom hľadá skrytý význam, preto sa aj zdá, že je paranoidný. Každého považuje za útočníka. Celý život dokáže milovať tajne a bez slov. Trvá dlho, kým sa odhodlá prejaviť svoje city navonok. Vie byť aj šarmantným spoločníkom a veselým partnerom, ale len vtedy, ak má zabezpečený pohodlný a spokojný život bez veľkých výkyvov. Ovplyvňuje ho planéta Jupiter. Jeho ochranné kamene sú ametyst a smaragd."},
                new NameEntity {Value = "Móric", NameDay = "22. september", Description = "Mužské meno Móric má latinský pôvod a jeho význam znie \"pochádzajúci z Mauretánie\" (v Severnej Afrike). Je jemným, starostlivým, bojazlivým a nepriebojným človekom. Vie sa občas zavesiť na niekoho vplyvného a potom, za jeho chrbtom sa dokáže zabezpečiť. Je nerád v centre pozornosti, snaží sa byť v ústraní a nepozorovane si \"prihrievať svoju polievočku\". Byť jeho spoločníkom znamená vykonávať jeho nápady a vystavovať svoje postavenie riziku za neho. Nikdy neriskuje, nikdy sa nesnaží priveľa investovať, alebo sa presadiť do centra diania. Zodpovednosť za všetko radšej nechá na svojej partnerke. Pritom sa však rád hrá na vodcu a snaží sa aspoň pred príbuznými vystupovať autoritatívne. Ovplyvňuje ho planéta Jupiter. Jeho ochranné kamene sú ametyst a granát."},
                new NameEntity {Value = "Nikolaj", NameDay = "29. august", Description = "Mužské meno Nikolaj má francúzsky pôvod a v preklade znamená \"víťazstvo\". Ovplyvňuje ich planéta Jupiter a Mesiac. Šťastné farby sú modrá, biela, svetlozelená. Ochranné rastliny slez, šalvia, medovka, ochranné kamene granát, perla a nefrit. Títo muži sú veľmi inteligentní. Majú výbornú pamäť, ktorú neustále využívajú. V detstve si treba dávať pozor na to, aby sa neuzatvárali do seba. Ako budúce zamestnanie si vyhľadávajú vedúce posty v súdnictve, v obchode. V živote musia mať pevný cieľ. Zdravie majú celkom dobré. Ale mali by si dávať pozor na kosti a pečeň. Toto meno je vhodné pre všetky znamenia."},
                new NameEntity {Value = "Norbert", NameDay = "6. jún", Description = "Mužské meno Norbert má nemecký pôvod a jeho význam je \"slávny Severan\". Osobným číslom tohto mena je 9 – Génius. Ich obľúbené farby sú hnedozelená, tyrkysová, fialová a biela. Ochranné rastliny petržlen, rasca, ochranné kamene citrín, sardonyx a achát. Títo muži sú pozorní a ohľaduplní. I keď im veľa ľudí nepraje, Norbertovia si z toho nič nerobia a berú to pokojne a nič ich nerozčúli. Zlo a falošné jednanie si nevšímajú a pokladajú to za bezvýznamné a komické. Všeobecne sú pomalší, ale nakoniec dostanú všetko, po čom túžia, čo im zabezpečí uľahčený a dobrý život. Majú dobrú intuíciu, ktorá im umožní odhadnúť okolnosti. Norbertovia nie sú lakomí, dokážu sa podeliť zo všetkým aj pomôcť ostatným, ak to potrebujú, ale nebudú pri tom vynakladať veľkú námahu. Medzi ich plusy patrí šikovnosť a obratnosť. . Zdravie je vcelku uspokojivé, ale mali by sa prepracovávať. Mali by viac stáť za svojimi názormi, prekonávať sklon k ostýchavosti, a naučiť sa sústrediť. V ich strave by nemal chýbať hlavne vápnik a vitamín C. Toto meno sa hodí pre znamenia : Blíženci, Váhy, Ryby a Škorpióna."},
                new NameEntity {Value = "Oldrich", NameDay = "3. december", Description = "Mužské meno Oldrich má nemecký pôvod a v preklade znamená \"dedičný vládca\". Je síce silnou osobnosťou, no plytvá svojimi silami na nesprávne ciele. Nemal by sa nechať ovplyvniť inými ľuďmi, inak stratí jasný cieľ a bude len slúžiť iným, bez výsledného efektu pre seba. Kým sa rozhodne založiť si rodinu, mal by dobre zvážiť s kým. Nezvykne vycúvať z manželského a rodičovského poslania, aj ak by to bolo neviem ako ťažké vydržať. Je obetavý, súcitný a milý. Jeho povaha je dobrou oporou a pomáha skutočne rád. Ovplyvňuje ho planéta Slnko. Jeho ochranné kamene sú jantár a granát."},
                new NameEntity {Value = "Oleg", NameDay = "10. september", Description = "Mužské meno Oleg má škandinávsky pôvod a v preklade znamená \"zdravý, šťastný\". Aj keď je to zriedkavé meno, má vynikajúci vplyv na svojho nositeľa. Takýto muž v živote ľahko dosiahne svoje ciele. Vie sa dobre orientovať v každej situácii a aj bez pomoci sa presadí. Je veľmi spoločenský. Nikto nemá lepší prístup k deťom, ani k ženám a spoločnosti všeobecne. Ak sa vyhne alkoholu, potom sa určite stane dokonalým mužom. V láske je však veľmi nestály a dlho potrvá, kým sa upokojí a svoje zálety zanechá. Striedanie partneriek však v jeho prípade nie je ukončené zlými vzťahmi a on sa môže na svoje bývalé lásky obrátiť po celý život. Je nezabudnuteľným milencom a všetky ženy, ktoré mu podľahnú, budú spokojné. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú tigrie oko, topás a achát."},
                new NameEntity {Value = "Oliver", NameDay = "7. júl", Description = "Mužské meno Oliver má latinský pôvod a význam mena je \"pestovateľ olív\". Osobným číslom tohto mena je 4 – Sila. Ovplyvňuje ich planéta Saturn. Obľúbené farby sú sivá, gaštanová a zelená. Ochranné rastliny kokorík a štiav, ochranné kamene granát a ametyst. Títo muži stoja väčšinou oboma nohami pevne na zemi. Ich veľká výhoda je inteligencia. Dokážu rýchlo uvažovať a v krátkom čase premyslieť všetky možnosti. Nemajú skoro žiadne zábrany. Okolie rozdeľujú na tých, ktorým už pomohli, teda sú im zaviazaní a na tých, ktorých oni potrebujú. Vedia byť milý a ústretový, ale naozaj blízkymi sú len osoby, ktoré dlho poznajú.Zdravie majú celkom uspokojivé, ale mali by sa vyhýbať intelektuálnej preťaženosti. Prospeje im veľa spánku, pretože veľa energie minú na svoj nestály a rýchly život. Toto meno je vhodné pre znamenia: Býk, Váh, Strelec a Baran."},
                new NameEntity {Value = "Ondrej", NameDay = "30. november", Description = "Mužské mená Ondrej a Andrej majú grécky pôvod a v preklade znamenajú \"statný\", \"mužný\", \"odvážny\". Zdajú sa byť trochu leniví, kým oni iba čakajú na vhodnú chvíľu. Ak sa do niečoho pustia, rýchlo dosiahnu úspech. Milujú dobrodružstvá a sú nezávislí. Sú roztržití a chodia poza školu, hlavne ak sa im nepáči nejaký profesor. Láka ich technika, aj keď niektorí inklinujú k umeniu. Sú citovo založení a túžia po tom, aby ich ľudia mali radi a aby im to aj dokazovali. Nemajú radi samotu, a preto často organizujú večierky. Sú priateľskí voči mužom aj ženám. Majú šťastie naklonené na svojej strane, no oni si to len málo všímajú. Vyrazia do života, kráčajú ním a väčšinou im zázračne všetko vychádza. Ich ochranný kameň je achát."},
                new NameEntity {Value = "Oskár", NameDay = "8. august", Description = "Má germánsky pôvod a jeho význam je \"božská kopija\". Má pred sebou všetky dvere otvorené. Dokáže sa vo všetkom uplatniť a získať podporu pre vlastné plány. Snaží sa dodať sily aj iným. Vie chápať ťažkosti svojich blízkych a dokáže sa postarať o každého, kto ho požiada o pomoc. Vie byť veľmi žiarliví a neznáša kritiku, ani upozorňovanie na vlastné chyby. Snaží sa vytvárať o sebe predstavu ako o dokonalom mužovi, bez chýb a omylov, ale skutočnosť je iná. Má veľa chýb a slabostí, ale svojím šarmom sa mu darí získať si pomoc iných a pochopenie pre svoje slabé stránky. Ovplyvňuje ho planéta Saturn. Jeho ochranné kamene sú zafír, achát a opál."},
                new NameEntity {Value = "Oto", NameDay = "5. december", Description = "Mužské meno Oto má nemecký pôvod a jeho význam je \"ochranca majetku\". Je jemný, citlivý, zraniteľný a obáva sa kritiky. Snaží sa prejavovať sa inak - viac mužsky. Jeho okolie je často zmätené jeho konaním. Podľa niektorých je to \"ostrý chlapík\", podľa iných je zase príliš mäkký. Názory na neho sú prosto rozdielne. On sám sa tiež v sebe nevyzná. V podstate je to dobrý muž, s jasnými predstavami o svojej budúcnosti, o svete a svojom mieste v ňom. Je verný, priamy a stály. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú zafír a sardónyx."},
                new NameEntity {Value = "Pankrác", NameDay = "12. máj", Description = "Mužské meno Pankrác má grécky pôvod a význam mena je \"všemocný\". Spolu so Servácom, a Bonifácom je v pranostikách charakterizovaný ako zmrznutý svätý. Ich šťastné farby sú čierna, modrá a sivá. Ochranné rastliny divozel a baza, ochranné kamene sokolie oko a jaspis. Osobným číslom tohto mena je 4 – Sila. Nositelia tohto mena sú spoločenskí ľudia. Pankrácovia majú extrovertnú povahu. Vždy chcú byť medzi ľuďmi, s ktorými sa môžu porozprávať. Ostatní ich vždy obdivujú, lebo sú pôvabní a plní energie. Vždy vedia ako veci riešiť a vždy sa dopracujú k úspechu a nakoniec sa stanú uznávaným. Prekážky v práci ich nikdy neodradia, vždy budú pevne pracovať, aby si svoj cieľ splnili. Podrobnostiam sa vyhýbajú a problémy ich veľmi nezaujímajú. Pankrácovia sú dosť inteligentní, čo vedia dobre využiť. Stávajú sa z nich dobrí rádiológovia alebo letci. V ich zdraví majú občas problémy s kosťami. Mali by jesť jedlá ako sú ryby, surová kapusta alebo mliečne výrobky. Toto meno sa hodí pre znamenia Vodnár, Ryby, Kozorožec a Škorpión."},
                new NameEntity {Value = "Patrik", NameDay = "6. júl", Description = "Mužské meno Patrik má latinský pôvod a jeho význam je \"urodzený\", \"patricijský\". Osobným číslom je 3 – Schopnosť. Ovplyvňuje ich planéta Venuša. Šťastné farby sú žltá, modrá. Ochranné rastliny podbeľ, mäta, ochranné kamene avanturín a ruženín. Títo muži sú priateľskí a uvážliví. Snažia sa všetko robiť dokonale a precízne. Nevedia odoprieť pomoc iným ľuďom. Na druhej strane sú veľmi tvrdohlaví a spoliehajú sa len na svoje schopnosti. Učia sa viac na chybách iných, ako na svojich, a tak nikomu nedôverujú. Kladú veľký dôraz na svoj vzhľad. Záleží im na tom, aby druhí ocenili ich výzor. Snažia sa zvážiť problémy zo všetkých uhlov pohľadu. Kvôli tomu sa vyhýbajú rôznym nepríjemnostiam. Patrikovia majú veľa priateľov, ale skutočne blízki sú im len rodinní príslušníci. Iba im dokážu plne dôverovať. Toto meno je vhodné pre znamenia: Kozorožec, Býk, Rak, Panna."},
                new NameEntity {Value = "Pavol", NameDay = "29. jún", Description = "Mužské meno Pavol má latinský pôvod a význam mena je \"malý, skromný\". Osobným číslom tohto mena je 7 – Tajomnosť. Pavlovia majú obľúbené farby fialovú, zelenú a modrú, ochranné rastliny žihľavu a slez, ochranné kamene ametyst a smaragd. Pavlovia sú silnými a pôsobivými mužmi. Idú si pevne za svojim cieľom, spoliehajú sa sami na seba a rýchlo získajú úspech. Tiež sú citlivými a láskavými, ale niekedy striedajú svoje nálady, hlavne ak sa im niečo nepodarí. Svoje chyby sa snažia vždy napraviť a nakoniec zvíťazia a dosiahnu to, o čo bojovali. V štúdiu su priemerní, ale najčastejšie sa zameriavajú na matematiku a cudzie jazyky. Mohli by byť z nich dobrí technici, právnici, politici či hovorcovia. Pavlovia sú muži, ktorí si vedia rýchlo získať ľudí na svoju stranu a získať ich náklonnosť. Nemali by sa obrácať k alkoholu, lebo ten im neurobí dobre a prekazí ich plány či v kariére ako aj v láske a priateľstve. Pre Pavlov je vhodné znamenie Býk, Panna, Kozorožec, Baran a Lev."},
                new NameEntity {Value = "Peter", NameDay = "29. jún", Description = "Mužské meno Peter má grécky pôvod a význam mena je \"pevný ako skala\". Petrovia majú obľúbené farby fialovú, modrú a červenú, ochranné rastliny horčicu a rozmarín, ochranné kamene jaspis a diamant. Títo muži sú celkom dynamickí a šarmantní a ktorý rozmýšľajú skôr svojim srdcom. Petrovia sú väčšinou úspešní muži, ale máloktorí vedia využiť svoje možnosti a predpoklady. Keď ich niečo zarmúti alebo majú ťažkosti, trápi ich to iba na krátky čas. Petrovia sú veľmi energickí a vytrvalí s určitou dávkou citlivosti. Radi si poleňošia, preto by mali radšej pracovať skôr duševne, lebo fyzická námaha im nevyhovuje. Uplatnia sa ako herci, podnikatelia, spisovatelia alebo novinári. Sú to muži, ktorí veria na svoj osud a preto radi čakajú na správnu príležitosť. Toto meno by malo byť spojené so silným znamením - Baranom, Býkom, Pannou, alebo Kozorožcom. Ak by sme toto meno dali Váhe, alebo Strelcovi, či Rybe, ich pojašenosť, ľahkomyseľnosť a optimistické očakávanie len dobrých udalostí, spôsobí, že potom nič výrazné a trvalé nedosiahnu."},
                new NameEntity {Value = "Pravoslav", NameDay = "15. február", Description = "Pravoslav je novšie slovanské mužské meno a jeho význam je \"právo sláv\". Ich obľúbené farby sú červená a ružová, ochranné rastliny červená chryzantéma, púpava a fialka, ochranné kamene karneol, jaspis a diamant. Jeho osobným číslom je 6 – Nežnosť, je teda citlivý a vždy sa snaží pomáhať iným. Od malička sa treba k ním správať ako k dospelými a ako k zodpovedným ľuďom. Pravoslavovia sú buď introvertní alebo extrovertní. Vedia zároveň žiť rovnako dobre v spoločnosti ako osamote, uzavrieť sa do seba, alebo sa plne zapojiť do spoločenského života. Nenávidí, keď ho niekto klame alebo podrazí a nedokáže zniesť sklamanie v ľuďoch a taktiež neznáša, keď musí skoro ráno vstávať alebo tvrdo pracovať. Život nemá ľahký, často sa zrieka aj dôležitých vecí, len aby získal aspoň niečo zo svojich snov. V láske dokážu byť veľmi obetaví, vedia dobre podporiť priateľku ale iba v tom prípade, ak sa im darí v pracovných záležitostiach a sú dostatočne finančne zabezpečení, inak vedia byť veľmi nevrlí a neznesiteľní. Ak sa zaľúbia, sú vzornými partnermi a tiež aj otcami. Čo sa týka zdravia, potrebujú dostatok spánku a mali by konzumovať veľa zeleniny, sóju a morské ryby. Neprospieva im priveľká namáha očí pri práci. Pre toto meno je vhodné znamenia : Kozorožec, Lev, Strelec, Panna a Rak, vhodnou farbou je žltá."},
                new NameEntity {Value = "Prokop", NameDay = "4. júl", Description = "Mužské meno Prokop má grécky pôvod a význam mena je \"pripravený na boj\", \"priekopník\". Osobným číslom tohto mena je 8 – Peniaze. Ovplyvňuje ich planéta Saturn. Šťastné farby sú hnedá, čierna, tmavomodrá. Ochranná rastlina kostihoj, ochranné kamene achát a opál. Títo muži si cieľavedomí a vždy vedia, čo chcú a aj za tým idú. Lenže musia stále bojovať, aby si v živote niečo vybojovali. Dôkladne si vyberajú budúce zameranie, v ktorom chcú pôsobiť. No ak si ho už vyberú, dosahujú v ňom výrazný úspech. Ich zmysel pre realitu im nedovolí lietať príliš vysoko. Sú neústupčiví vo svojich názoroch a rozhodnutiach. Ak budú trpezliví, tak sa dočkajú ocenenia. Stojí pri nich šťastná hviezda. Majú silný charakter, ak sa vyhýbajú alkoholu. I keď to nedajú na sebe vidieť, vnútorne sa bránia akejkoľvek náhlej a nepremyslenej zmene. Dokážu sa otvoriť iba pred svojimi blízkymi. Sú veľmi odolní voči únave a obdarení obdivuhodnou vitálnosťou. Ich slabé stránky sú obličky a oči. Vhodné je pre znamenia Panna, Kozorožec, Vodnár a Býk."},
                new NameEntity {Value = "Radomír", NameDay = "29. február", Description = "Mužské meno Radomír má slovanský pôvod a jeho význam je \"má rád mier\". Ich obľúbená farba je zelená a rastlinný totem agát. Radomírovia niekedy pôsobia ako tajuplní ľudia. Ich správanie je niekedy rozdielné. Títo ľudia sú aktívni aj elegantní. Vedia vždy reagovať na každý podnet a chcú zabezpečiť, aby sa ostatní cítili v spoločnosti uvoľnene. Aj keď sa dajú ľahko ovplyvniť, čakajú vždy reakcie od iných ľudí. Zvyčajne majú výborné zdravie, a keď síce majú nepravidelný život a pracujú hekticky, dokážu si zachovať rovnovážny stav. Milujú slnko a dovolenky, mali by sa venovať hlavne dostatku spánku, jesť veľa ovocia a jedlá, ktoré posilňujú kosti, svaly a srdce."},
                new NameEntity {Value = "Radoslav", NameDay = "6. marec", Description = "Mená Radoslav má slovanský pôvod a význam mien je \"radujúci sa, sláviaci radosť\". Obľúbené farby Radoslavov sú fialová a svetlomodrá a ovplyvňuje ich planéta Jupiter. Ochranné rastliny majú žihľavu, nechtík, ochranné kamene ametyst a zafír. Osobným číslom pre toto meno je 5 – Riziko. Radoslavovia nikdy nemajú s ničím problém. Vždy sa vynájde, vie sa s druhými dobre porozprávať a získať ich na svoju stranu, ako si to oni predstavujú. No často títo muži myslia iba na seba a sú dosť skúpi. Sú egoistickí a neradi sa niekym delia o svoj zárobok či úspech. Veľmi neriskujú, iba priemerne. Vždy rátajú s nejakou prehrou, preto si robia aj nejakú zásobu, ak im niečo nevyjde. Toto meno je vhodné pre znamenia : Strelec, Ryby, Kozorožec, Škorpión, Lev."},
                new NameEntity {Value = "Radovan", NameDay = "14. január", Description = "Mužské meno Radovan má slovanský pôvod a jeho význam znamená \"radujúci sa\". Osobným číslom tohto mena je 1 – Vodca. Ich obľúbená farba je zelená a rastlinný totem agát. Títo muži sú tajuplní. Nikdy nevieme, čo si myslia a ako sa zachovajú. Málokto ich ovplyvní ale sú ochotní prijať iniciatívu od okolia. Majú v sebe veľa sily. Radovanovia si vždy vedia získať ľudí na svoju stranu a využiť svoje tušenie. Títo muži radi trávia svoj čas v spoločnosti a hlavne tam, kde môžu zažiť nejaké dobrodružstvo. Radovanovia pôsobia zdvorilo a očarujúco. V ich kariére idú vždy za svojim cieľom a konajú vždy tak, aby čo najrýchlejšie dosiahli úspech. O svoje dosiahnuté výsledky sa neradi delia, bývaju lakomí, ale iba po určitú hranicu. Niekedy jeho nestále hospodárenie niektorí často využívajú. V ich živote a zamestaní ich nič nedokáže zastaviť, vždy sa vynájdu ako zdolať vzniknuté zábrany. Aj keď Radovanovia majú nepravidelný život a priveľa pracujú , majú vynikajúcu vitalitu. Obľubujú, keď môžu byť na slnku, preto väčšinou vyhľadávajú dovolenku pri mori. Mali by si dopriať dostatok spánku. Toto meno je teda dobré, prijateľné pre každé znamenie zverokruhu a farba, ktorá mu vyhovuje, je zelená."},
                new NameEntity {Value = "Radúz", NameDay = "10. december", Description = "Mužské meno Radúz má slovanský pôvod a jeho význam je \"radujúci sa\", \"radostný\". Od narodenia správne tuší, že sú mu určené veľké a vznešené ciele. Ak vyrastá v neprávnom prostredí a nie je správne motivovaný, tak nedokáže toľko, ako by chcel. Má všetky predpoklady na úspech, ale malú vytrvalosť a odvahu k riskovaniu. Sám sa ničí svojím váhaním a pochybnosťami o sebe a svojom nadaní. V láske sa mu darí, vie sa sám o seba postarať a celkom určite si medzi svojimi známymi vyberie najlepšie. Je dobrým manželom a otcom, ak si vezme panovačnejšiu ženu, tak sa pokojne nechá zbaviť vodcovstva a bude radšej \"mamkou\", ako tvrdým otcom. Ovplyvňuje ho planéta Mesiac. Jeho ochranné kamene sú nefrit a perla."},
                new NameEntity {Value = "Rastislav", NameDay = "13. január", Description = "Mužské meno Rastislav má slovanský pôvod a vo voľnom preklade znamená \"ten, kto rastie v sláve\". Jeho osobným číslom je 8 – Peniaze. Ich obľúbená farba je červená, modrá, biela a zelená, ochranná rastlina podbeľ, ochranné kamene tigrie oko, topás a achát. Títo muži sú skôr neprípustní a dobrými pozorovateľmi. Radi pomôžu druhým a dokážu aj všetko obetovať. Sú to milí, príjemní a emotívni ľudia. Rastislavovia majú často rozdvojenú osobnosť. Niekedy veľa snívajú a na druhej strane žijú realisticky a materialisticky. Rasťovia sú schopní muži a vedia ekonomicky rozmýšľať. Vždy sa snažia pôsobiť nedostupne, i keď to nemusí byť pravda. Ich rozpoltenosť im zapríčiňuje trápenie, pretože sa niekedy nespráva dobre práve k tým, čo by mu mohli pomôcť, alebo ho podporiť. Keď si uvedomia, kde schybili, až vtedy môžu mať šťastie. Ľahko sa môžu stať vysokými úradníkmi, či pedantnými priemyselníkmi. V zdraví by si Rastislavovia mali dávať pozor na pečeň a pľúca. Toto meno je vhodné pre znamenia : Váhy, Kozorožec, Panna a Vodnár."},
                new NameEntity {Value = "René", NameDay = "7. november", Description = "Mužské meno René má latinský pôvod a v preklade znamená \"znovuzrodený\". Pôsobí neodolateľne, aj keď väčšinou ide iba o pozlátko. Postupom času sa z neho stane vypočítavec, ktorý svojím šarmom a úsmevmi dokáže využívať doslova každého. Každý je rád v jeho spoločnosti. A aj keď je povrchný a vypočítavý, predsa má svoju úroveň a známosť s ním je prestížnou záležitosťou. Je mužom, ktorý málo dáva, ale veľa berie. Berie s takým šarmom a jemnosťou, že všetci sa radi nechajú obrať aj o to posledné a bez nároku na vrátenie. Je neodolateľný a vždy uspeje. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene sú jaspis a perla."},
                new NameEntity {Value = "Richard", NameDay = "3. apríl", Description = "Mužské meno Richard má germánsky pôvod a význam mena je \"silný vládca\". Ovplyvňuje ich planéta Merkúr. Osobné číslo pre toto meno je 1 - Vodca. Títo ľudia sú veľmi komunikatívny, talentovaní a dokážu využiť každé svoje nadanie. K ich lepším vlastnostiam patrí veľké sebaovládanie. Richardovia sú väčšinou subjektívny a málokedy dokážu pochopiť situáciu druhých. V práci nepracuju za účelom radosti, ale aby získali nejaký cieľ. Môžu byť dobrý v oblasti medicíny. Ak toto meno spojíme so slabším znamením, nemusí to byť najlepšie. V živote to môže mať ťažké. Meno Richard by sme mali spájať so silnými znamenia, aby dokázali využiť svoje príležitosti a šance. Najvhodnejšie sú: Baran, Býk, Panna, Lev, Strelec a Vodnár. Šťastné farby týchto mužov sú hnedá, červená, modrá a zelená. Ochranné rastliny palina, saturejka, ochranné kamene zafír a sardonix."},
                new NameEntity {Value = "Róbert", NameDay = "7. jún", Description = "Mužské meno Róbert má nemecký pôvod a jeho význam je \"slávny\". Osobným číslom tohto mena je 2 – Dobrota. Obľúbené farby Róbertov sú hnedá, čierna, červená, tmavomodrá a zelená. Ochranné rastliny kostihoj, kokorík, ochranné kamene zafír, achát, granát Róbertovia často plánujú svoje veci a dokážu využiť každú príležitosť, ktorá sa im vyskytne. Sú trpezlivý, čo im umožňuje dobre si veci premyslieť a len tak sa nevedia vzdať. Pri budovaní kariéry nemajú hneď šťastie. Ak odmalička budú rodičia na neho prísni a prideľovať mu zložitejšie úlohy, tak z neho vyrastie prosperujúci človek. Pri väčšej námahe bude nakoniec mať po čom sníva odmalička a dokonca bude mať luxusný život. Keďže sú skúsení, po štúdiu môžu byť dobrými obchodníkmi alebo politikmi. Ich vitalita životaschopnosť je nesmierna, sú dobre odolní voči únave. Toto meno je vhodné pre Kozorožca, Pannu, Býka a Raka."},
                new NameEntity {Value = "Roland", NameDay = "9. máj", Description = "Mužské meno Roland má nemecký pôvod a význam mena je \"slávny hrdina\". Ich obľúbené farby sú modrá, zelená a sivá. Ochranné rastliny palina a saturejka, ochranné kamene zafír a sardónyx. Osobným číslom tohto mena je 5 – Riziko. Títo muži pôsobia veľmi nezabudnuteľne. Sú to dostatočne silní ľudia s kvapkou emotívnosti. Ak bude potrebné, Rolandovia využíjú každú situáciu. Ak budete mužov s týmto menom podporovať a spoliehať sa na nich, nemusia sa vám to vrátiť, iba vás v podstate využili. Už domalička majú sklon ku klamaniu a manipulácií druhých. Oni sami sa nikdy nedostanú do ťažkostí, vždy si vedia poradiť a o svojich blízkych sa vždy vzorne postarajú. Majú skvelú intuíciu, ktorú vedia dobre využiť. Najviac si vážia svoju rodinu a na nu nikdy nedajú dopustiť. V oblasti zdravia môžu mať zdravotné ťažkosti s kosťami, štítnou žľazou. Mali by svoju stravu obohatiť o jablká, zeler a biely jogurt. Toto meno je vhodné pre znamenia : Blíženci, Vodnár, Strelec, Panna a Ryby."},
                new NameEntity {Value = "Roman", NameDay = "23. február", Description = "Krstné meno Roman má latinský pôvod a znamená \"pochádzajúci z Ríma\". Od malička je aktívny, vytrvalý a žije si svojský život – teda nie je veľmi spoločenský. Ale napriek tomu, dokáže si ísť za svojím cieľom a je trpezlivý. Roman by sa mal naučiť ovládať svoju zaťatosť a nedôverčivosť v láske. Vhodné sú pre neho vitamíny najmä z citrusových plodov. Jeho osobné číslo je 2 - Dobrota, teda dokáže sa ľahko prispôsobiť a má v sebe pochopenie. Ale ako muž, nemôže byť predsa ku každému srdečný. Romanove šťastné farby sú zelená, červená, svetlofialová a svetlomodrá. Ochranné rastliny nechtík, rozmarín a nezábudka, ochranné kamene smaragd, zafír. Keď sa zaprie, dokáže striedať veľmi nálady a byť odporujúci. Ale tak ho vidia ľudia len navonok, skôr ide o to, že všetko komentuje, hlavne ostatných, uvažuje aj dokáže povoliť, ale kvôli tomu, aby mohol opäť provokovať. Pre mnohých ľudí bude pôsobiť ako výsredný človek, kým nepochopí určité veci a kým sa sám v sebe nenájde. Hodí sa k nemu znamenie Kozorožca, Vodnára a Rýb, je na jeho strane 70 % šťastia a určite sa uplatní v akejkoľvek činnosti."},
                new NameEntity {Value = "Rudolf", NameDay = "17. apríl", Description = "Mužské meno Rudolf má germánsky pôvod a v preklade znamená \"slávny vlk\". Ich osobným číslom je 5 – Riziko. Sú to vytrvalí muži s pevnou voľou. Keď chcú niečo dosiahnuť, idú si pevne za tým. Snažia sa viac získať pre seba ako pre druhých. Svoje výnimočť vedia plne využiť. Rudolfovia sú nadaní a rozvážní ľudia a často priťahujú takých ľudí, ktorí im závidia a ktorí by ich radi využili. O svojich myšlienkach často nehovoria druhým, lebo si uvedomujú ich zvláštnosť a so svojim úspechom sa nebudú deliť. Rudolfovia ale nie sú skúpi a nevďační, nezabudnú na nikoho, kto im pomohol, ale ani neodpustia zradu a klamstvo. Uplatniť sa môžu ako spisovatelia, právnici, alebo pedagógovia. Toto meno je vhodné pre znamenia : Vodnár, Baran, Býk, Kozorožec a Panna. Vhodnou farbou je modrá."},
                new NameEntity {Value = "Samuel", NameDay = "26. august", Description = "Mužské meno Samuel má hebrejský pôvod a v preklade znamená \"meno božie\". Toto biblické starozákonné meno je na Slovensku populárne. Osobné číslo tohto mena je 7 – Tajomnosť. Ovplyvňuje ich planéta Jupiter. Šťastné farby sú žltá a modrá. Ochranné rastliny žihľava a rimbaba, ochranné kamene ametyst, smaragd a tyrkys. Títo muži sú seriózni a láskaví. Vedia presne, čo chcú a za tým si aj idú. Už v mladosti dokážu predvídať, aké povolanie ich bude napĺňať a baviť. Preto sa rozhodujú pre miesto v justícii. Uplatnenie nájdu aj v umeleckej oblasti. Každú krivdu dokážu, ktorú ste im spôsobili v ťažkých časoch, niekoľkonásobne vrátiť. V spoločnosti sú dobrí spoločníci, pretože dokážu pútavo a k veci rozprávať.Zdravie majú zvyčajne dobré. Sú odolní voči únave. Je vhodné len pre silné znamenia, teda pre Barana, Leva, či Býka."},
                new NameEntity {Value = "Sergej", NameDay = "22. október", Description = "Mužské meno Sergej má latinský pôvod a význam mena je \"sluha, strážca\". Je dokonalým príkladom správneho chlapa. Ak sa v detstve nedostane do zlej partie, stane sa neskôr z neho muž, o ktorom snívajú ženy. Dokáže v živote uspieť v čomkoľvek, čo bude pre neho dôležité. Môže to byť obchod, politika, niekedy chce byť dokonalým otcom a manželom, alebo cestovateľom, bádateľom, či vedcom, alebo hoci aj rebelom. Ovplyvňuje ho planéta Mesiac. Jeho ochranné kamene sú nefrit, perla a rubín."},
                new NameEntity {Value = "Servác", NameDay = "13. máj", Description = "Mužské meno Servác má latinský pôvod a v preklade znamená \"záchranca\". Ovplyvňuje ich planéta Saturn. Ich šťastné farby sú hnedá, zelená, čierna a tmavomodrá. Ochranné rastliny kostihoj a štiav, ochranné kamene zafír, opál a granát. Toto meno má osobné číslo 1 – Vodca. Servácovia nie sú veľmi spoločenskými mužmi. Radšej trávia svoj čas osamote a potichu. Ľudia si ich veľmi nevšímajú, lebo pôsobia ako nudní muži, lebo oni nerozmýšľajú ako ostatní. Ale títo muži sa vždy usilujú, aby boli milí a priateľskí. Sú to inteligentní muži a v práci sa vďaka svojmu osobnému číslu stanú uznávanými a vysoko postavenými ľuďmi. Reagujú primerane a vedia byť aj citliví a odvážni. Ich zdravie je v poriadku a životaschopnosť majú výbornú. Mali by konzumovať jedlá ako je: špargľa, kuracie mäso a figy. Je to meno vhodné pre znamenia Kozorožec, Škorpión, Strelce a Baran."},
                new NameEntity {Value = "Severín", NameDay = "8. január", Description = "Mužské meno Severín je svätecké, má latinský pôvod a v preklade znamená \"prísny, vážny\". Osobným číslom tohto mena je 2 – Dobrota. Obľúbené farby týchto mužov sú zlatá, hnedá, modrá a žltá, ochranný kameň jantár. Ich rastlinný symbol je borovica. Severínovia sú muži, ktorí radi si premyslia dopredu ako budú konať. Majú vždy svoje zámery, ktoré chcú v živote dosiahnuť. Sú to priebojní muži, tak vždy docielia, čo chcú. Ale niekedy nemajú šťastie ako by chceli, preto to niekedy na nich zle vplýva. Ak odmalička budete na nich tlačiť a dávať mu dôležité úlohy, sledovať ako sa vyvíja ich zodpovednosť, tak sa z nich stanú vplyvní a úspešní ľudia, ktorí získajú všetko, po čom túžia. Ak sa budú veľmi usilovať, nakoniec budú si žiť v bohatstve, o akom vždy snívali. Už odmála, Servínovia obľubujú výučbu jazykov. Sú z nich výborní prekladatelia a tlmočníci. Tiež majú radi vedecký výskum. Ich pamäť je výnimočná a ťažko ich možno prichytiť pri chybe, ak ide o dátum alebo nejaký citát. Čo sa týka ich zdravia, je v celku dobré. Ale ak nebudú mať v pohode svoju psychiku, tak môžu byť náchylní. Ich zdravotné problémy môžu súvisieť s chrbticou. Vhodné je pre znamenia Váh, Býka, Vodnára, Škorpióna a Rýb."},
                new NameEntity {Value = "Silvester", NameDay = "31. december", Description = "Posledný deň roka je charakteristický zábavou, všeobecnou veselosťou, zábavnou pyrotechnikou a oslavami na každom kroku. Mužské meno Silvester má latinský pôvod a v preklade znamená \"lesný, z lesa\". Berie život, tak, ako prichádza a stavia sa k problémom flegmatickejšie ako jeho okolie. Je nežný a dobre to skrýva vo svojom vnútri. Prejaví ju až po dlhšej dobe, aj to veľmi opatrne. V láske je ochotný zrieknuť sa mnohého, len aby boli s ním spokojné jeho partnerky, ale nemôžu od neho žiadať všetko. Má slony k hádavosti a nerád ustupuje, no stačí milé slovko a už je ochotný sa zriecť hoci aj svojho presvedčenia. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene ametyst a jaspis."},
                new NameEntity {Value = "Šimon", NameDay = "30. október", Description = "Mužské meno Šimon je hebrejského pôvodu a znamená \"počúvajúci, načúvajúci\". Osobným číslom mena je 7 – Tajomnosť. Šimonov ovplyvňuje Venuša a Slnko. Obľúbená farba je zelená, biela, hnedá a oranžová. Ochranné rastliny sú ľubovník, mak a nechtík, ochranné kamene sú smaragd, jantár a granát. Muž s týmto menom má v živote mnoho problémov, pretože sa ťažko prispôsobuje priemeru. Mal by sa snažiť zvoliť si prácu medzi významnými ľudmi, ktorí by ho inšpirovali a medzi ktorými by sa cítil dobre. Je jedno, aký smer si vyberie, uspeje v čomkoľvek. V láske je Šimon otvorený a prístupný všetkému zaujímavému. Žena, ktorá ho zaujme, bude v jeho živote vždy jediná, kým sa v nej nesklame. Je verný a snaží sa vyhovieť svojej láske. Toto meno je vhodné pre znamenie Váh, Panny, Kozorožca a Býka."},
                new NameEntity {Value = "Slavomír", NameDay = "22. apríl", Description = "Mužské meno Slavomír má slovanský pôvod a význam mena je \"sláv mier\". Toto meno má osobné číslo 3 – Schopnosť. Títo muži si málo dôverujú a bývajú málo objektívni. Ale napriek tomu, ak ich niekto vedie a motivuje, majú prosperujúci život. Slavomírovia sú bystrí ľudia a veľakrát vedia o čo sa jedná. Veria na svoje schopnosti a tušenie. Títo ľudia sú úprimní a ochotní pomôcť druhým. Majú dobré nápady, ktoré si všimnú veľakrát aj neznámi ľudia a preto sú obľúbení. Ak sa vyhne alkoholu a spoločnosti, ktorá na neho zlé vplýva, dostane sa so svojimi schopnosťami dostatočne ďaleko. Je vhodné pre znamenia : Blíženci, Rak, Baran, Škorpión a Strelec. Ich šťastné farby sú červená, fialová modrá a biela. Ochranné rastliny podbeľ, kôpor a petržlen, ochranné kamene tigrie oko, topás a achát."},
                new NameEntity {Value = "Stanislav", NameDay = "13. november", Description = "Mužské meno Stanislav má slovanský pôvod a vo voľnom preklade znamená \"upevni slávu, staň sa slávnym\". Je rodený optimista a jeho plány sú vždy veľkolepé. Vysníva si také nádherné vzdušné zámky, že keď sa mu zrútia, oplatí sa mu žiť hoci aj pre zrúcaniny svojich snov. Má veľa ideálov, vzorov a koná vždy so vznešeným cieľom. Je minimálne ziskuchtivý. Získa skoro všetko, o čom kedy sníval. Nemsie sa však nechať zviesť zo svojej cesty, pretože ak začne myslieť len na seba, potom mu jeho šťastie unikne. Ovplyvňuje ho planéta Mars. Jeho ochranné kamene sú diamant a ametyst."},
                new NameEntity {Value = "Štefan", NameDay = "26. december", Description = "Mužské meno Štefan je gréckeho pôvodu a v preklade znamená \"víťaz\". Najradšej si všetko zjednodušuje a rozhoduje sa veľmi pomaly. Často je podráždený a neznáša, ak mu niekto dáva pocítiť jeho neznalosť veci. Je vždy informovaný o všetkom, čo sa deje okolo neho. Hľadá skrytý zmysel v slovách svojich blízkych, aj keď mu nikto neoponuje, či neničí jeho povesť. Je od malička podozrievavý a ťažko dokáže znášať odporovanie a kritiku. V láske je to silný milovník a nevie odolať zvádzaniu žien, preto sa na jeho vernosť nedá priveľmi spoľahnúť. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú diamant a ametyst."},
                new NameEntity {Value = "Svätopluk", NameDay = "12. november", Description = "Mužské meno Svätopluk má slovanský pôvod a jeho význam je \"silný v plukoch\", \"veľký medzi bojovníkmi\". Je symbolom silného a pôsobivého muža, o ktorého sa môže jeho okolie oprieť. Je nielen silný, ale aj prefíkaný. Boj má veľmi rád, potrebuje stálu aktivitu, rýchlosť a zmenu. Nielen v kariére, ale aj v súkromí, lebo inak sa začne nudiť a hľadať zábavu inde. Jeho snahy sú vždy korunované úspechom. Je teda veľmi výhodné s ním žiť v zhode a neodporovať mu. Jedinou jeho slabou stránkou je totiž neznášanlivosť voči akejkoľvek kritike. Ovplyvňujú ho planéty Mars a Pluto. Jeho ochranné kamene sú achát a topás."},
                new NameEntity {Value = "Svetozár", NameDay = "16. máj", Description = "Mužské meno Svetozár má slovanský pôvod a význam mena je \"žiariaci\". Ovplyvňuje ich planéta Mars. Ich šťastné farby sú červená, fialová. Ochranné rastliny púpava, cesnak, rozmarín, ochranné kamene diamant a ametyst. Jeho osobným číslom je 7 – Tajomnosť. Svetozárovia sú vzdorovití ľudia. Niekedy sú títo muži nesnaživí, ale keď o niečo bojujú, nevzdávajú sa. Najprv premýšľajú ako a čo budú konať a až potom využijú všetky svoje prednosti. Občas bývajú sebeckí a málokomu povedia, čo majú na pláne. Svetozárovia sú intuitívni a v živote majú šťastie, darí sa im všade. Niekedy nevedia sami, čo v danej chvíli cítia, sú raz milí a raz náladoví. Títo ľudia veľmi nevedia pochopiť druhých, ale dokážu sa zmeniť a inač konať, aby viac súcitili. Životaschopnosť majú celkom dobrú. K svojmu zdraviu potrebujú hlavne spánok. Aby sa cítili čo najlepšie, mali by obmedziť z svojho jedálnička kávu. Toto meno je vhodné pre znamenia Baran, Lev, Vodnár a Strelec."},
                new NameEntity {Value = "Tadeáš", NameDay = "25. jún", Description = "Mužské meno Tadeáš má hebrejský pôvod a jeho význam je zrejme \"šikovný\", \"rozumný\" alebo \"smelý\", \"odvážny\". Jeho osobným číslom je 6 – Nežnosť. Ich obľúbené farby sú svetlozelená, oranžová a modrá, ochranné rastliny skorocel a pľúcnik, ochranné kamene akvamarín a smaragd. Tadeášovia pôsobia ako rozumní ľudia a väčšinou sa dokážu správať pokojne. Vďaka ich dobrej pamäti môžu dosiahnuť úspechy. Práca im veľmi nevyhovuje, najradšej by nič nerobili a preto občas potrebujú od niekoho oporu, aby mohli bojovať s možnými problémami. Tadeášovia sú prelietavými mužmi. Radi využívajú úspechy iných a na tom sa priživujú. Nevyhovuje im, ak majú brať na seba zodpovednosť. Ich zámerom je nájsť si pohodlné zázemie, kde sa veľmi nenarobia. Aj keď sú to zruční a inteligentní ľudia, ktorí vedia snívať, ale často sa pred reálnym svetom skrýva. Čo sa týka ich zdravia a vitality majú slabý nervový a endokrinný systém. Toto meno je vhodné pre znamenia Váhy, Panna, Kozorožec a Býk."},
                new NameEntity {Value = "Teodor", NameDay = "9. november", Description = "Mužské meno Teodor má grécky pôvod a v preklade znamená \"boží dar, bohom darovaný\". Vie sa obracať a konať v pravý čas aj bez pomoci iných. Nebojí sa neúspechu a prípadné porážky berie ako niečo normálne. Jeho postup je vždy originálny a jeho nápady sú z veľkej časti úspešné. Neobáva sa žiadneho nebezpečenstva, vždy sa spolieha na vlastné sily, preto je obľúbeným spoločníkom nielen v mužskej, no i ženskej spoločnosti. V láske sa drží svojich morálnych zásad a ak mu jeho vyvolená nevyhovie vo všetkých jeho požiadavkách, nevie popustiť a radšej sa jej vzdá. Koná vždy otvorene a priamo. Neznáša klebety a ohováranie. Ovplyvňuje ho planéta Mars. Jeho ochranné kamene sú diamant, ametyst a jaspis."},
                new NameEntity {Value = "Tibor", NameDay = "10. november", Description = "Mužské meno Tibor má latinský pôvod a význam mena je \"muž pochádzajúci z latinského mesta Tibur\". Jeho ideály sú veľké a snaží sa pôsobiť ako silný muž. Väčšinou koná pod vplyvom súcitu a nevie povedať nie. Je často zneužívaný, klamaný a stáva sa ľahkou obeťou iných. Nevie odporovať a len v máločom si vie stáť za svojím názorom. Jediné, na čom mu skutočne záleží, je rodina a jej bezpečnosť. Kvôli svojim blízkym obetuje všetko a všetkých, pre neho je vždy rodina základ a bez podpory svojich blízkych by možno ani nič nedokázal. Nejde mu ani tak o úspech v kariére, ako o citovú spokojnosť a harmonické manželstvo. Tomuto podriaďuje svoju osobnosť a snaží sa prekonať aj veľké názorové rozdiely, len aby boli všetci rodinní príslušníci spokojní. Ovplyvňuje ho planéta Slnko. Jeho ochranné kamene sú jantár, topás a olivín."},
                new NameEntity {Value = "Tichomír", NameDay = "22. august", Description = "Mužské meno Tichomír má slovanský pôvod a v preklade znamená \"tichý mier (svet)\". Ovplyvňuje ich planéta Mesiac. Ich šťastné farby sú biela, modrá a svetlozelená. Ochranné rastliny rumanček, jazmín a medovka, ochranné kamene nefrit, perla a rubín. Ich osobným číslom je 5 - Riziko Títo muži sú veľmi zvláštni a tajomní. Veľa ľudí im nerozumie a nevyznajú sa v nich. Nevedia riešiť nejaké zložitejšie situácie, radšej sa uzavrú. Tichomírovia sa nenechajú ovplyvňovať od druhých a rýchlo si získajú priateľov. Títo muži sú nadaní a citliví. Potrebujú si ujasniť svoje túžby a ciele, ktoré chcú dosiahnuť v živote a mali by sa im naplno venovať. Muži s týmto menom sú kreatívny a preto sa neradi zdôverujú iným a svoje nápady a myšlienky si nechávajú iba pre seba. Tichomírom veľa ľudí závidí ich schopnosť presadiť sa, preto bývajú často obklopení závistlivými ľuďmi, ktorí ich využívajú. Títo muži sa zameriavajú na oblasť lekárstva, techniky a výskumníctva. Tichomírovia sú vytrvalí, preto aj dobre vedia odolať únave a dokážu dlho pracovať. Preto by si mali chrániť a svoje zdravie, ktoré je striedavé. V strave by mali vynechávať mastné a nezdravé jedlá, cukor. Toto meno je vhodné pre znamenia : Vodnár, Baran, Býk, Kozorožec a Panna."},
                new NameEntity {Value = "Timotej", NameDay = "24. január", Description = "Mužské meno Timotej má grécky pôvod a v preklade znamená \"uctievajúci Boha\". Obľúbené farby týchto mužov sú tmavomodrá, žltá a zelená. Ochranná rastlina je harmanček, ochranné kamene sú sokolie oko a obsidián. Najviac ich ovplyvňujú planéty Saturn a Urán. Osobným číslom tohto mena je 3 - Schopnosť. Títo muži sú veľmi spoločenskí ale občas leniví, čo im neprospieva, aby dosiahli úspech. Majú vždy prehľad, vedia čo chcú. Timotejovia sú extrovertmi a vedia dobre zapôsobiť na ľudí. Vždy musia vedieť, čo sa deje, sú zvedaví. Radi hovoria o svoj ich vedomostiach a zážitkoch. Občas majú problém niekomu veriť a niekedy pochybujú o svojich plánoch. Títo muži sú šetrní, zbytočne svoje financie nerozhadzujú. Keďže radi skúmajú, hodia sa k nim povolania ako je práca redaktora, vyšetrovateľa, právnika, alebo prokurátora. Čo sa týka ich zdravia, nie je vyhovujúce. Mali by si dávať pozor na ich slabšie kosti. Toto meno je vhodné pre znamenia : Rak, Lev, Panna a Váhy."},
                new NameEntity {Value = "Tomáš", NameDay = "7. marec", Description = "Mužské meno Tomáš má hebrejský pôvod a v preklade znamená \"dvojča\". Ovplyvňujú ich planéty Saturn a Urán. Ich šťastná farba je modrá a zelená, ochranné rastliny medovka a ružový klinček, ochranný kameň jaspis. Jeho osobným číslom je 3 - Schopnosť. Títo ľudia sú veľmi úprimní, ale majú komplikovaný charakter. Tomášovia sa nikdy nevedia pretvarovať a nikdy prepáčiť zradu. Väčšinou hladia na veci materialisticky, ale zato sú dosť inteligentní. Tomášovia sú šikovní, vždy získajú úspech a s ním aj veľké ohodnotenie. Ak sa budú spoliehať vždy sami na seba, dokážu získať všetko, po čom túžia. Ak sa majú stať vplyvnými ľuďmi, mali by sme ich podporovať. Nemôžu sa náhliť a riskovať pri svojich povinnostiach, aby všetko dosiahli. V ich zdraví im prospeje, ak sa budú venovať vodným športom a budú jesť veľa mliečnych výrobkov. Toto meno je vhodné pre znamenia : Vodnár, Strelec, Baran, Lev a vhodnými farbami."},
                new NameEntity {Value = "Urban", NameDay = "25. máj", Description = "Význam mužského mena Urban  je \"mestský, mešťan\" a má latinský pôvod. Osobným číslom tohto mena je 2 - Dobrota. Ovplyvňuje ich planéta Merkúr. Ich šťastné farby sú červená, biela, modrá a hnedozelená. Ochranné rastliny podbeľ, levanduľa, ochranné kamene tigrie oko a topás. Urban je muž plný rozporov a málokto sa dokáže v ňom orientovať. Rád hrá divadlo a pôsobí ako tvrdý chlapík, no stačí dlhšia snaha a zistíme, že je to citlivý romantik. Snaží sa učiť z chýb iných a tak je ťažké nájsť v ňom toho pravého a skutočného muža, je skrytý pod maskou a nerád dáva možnosť iným, aby ho zranili. V láske je opatrný a stály, keď už sa rozhodne, tak to platí na dlhý čas. Takisto to platí aj o jeho vzťahoch k priateľom, správa sa akoby otvorene a veselo, no pritom len skúma reakcie ľudí okolo seba a postupne si vyberá z nich tých, ktorí mu vyhovujú. Zdravie Urbanov je priemerné. Majú dobrú životaschopnosť, ale mali by pravidelne športovať a jesť jedlá doplnené o vitamín C. Toto meno je vhodné pre Blížencov, Panny a Váhy."},
                new NameEntity {Value = "Václav", NameDay = "28. september", Description = "Mužské meno Václav má slovanský pôvod a jeho význam je \"viac slávny\". Málokedy sa mu niečo nevydarí. Ak už v mladosti pracoval a mal možnosť investovať svoje peniaze, rýchlo zbohatne. Václavovi šťastie praje a vždy sa ho ujde aj iným. Rád sa podelí so svojimi ziskami, ale záleží mu na verejnom uznaní. Pre slávu a popularitu sa dokáže zrieknuť čohokoľvek. Je ideálnym politikom a diplomatom. Rád sa predvádza a snaží sa na každého zapôsobiť čo najlepšie. Má rád ženy a rýchlo strieda partnerky. No keď usúdi, že prišiel čas na založenie rodiny, potom si vyberá naozaj zodpovedne a snaží sa byť dobrým manželom a otcom pre svoje deti. Ovplyvňuje ho planéta Mars. Jeho ochranné kamene sú rubín a diamant."},
                new NameEntity {Value = "Valentín", NameDay = "14. február", Description = "Mužské meno Valentín má latinský pôvod a v preklade znamená \"silný\", \"zdravý\". Meniny oslavujú 14. februára, ktorý je súčasne aj dňom svätého Valentína, mučeníka a patróna zamilovaných. Osobným číslom tohto mena je 7 – Tajomnosť. Obľúbené farby Valentínov sú červená a fialová. Ochranné rastliny sú púpava a šafran, ochranné kamene diamant a ametyst. Celý život títo muži stále niečo hľadajú. Valentín sám nevie, čo všetko sa skrýva v jeho duši, býva často náladový, nestály, roztržitý a nervózny a dosť panovačný. Valintínovia sú aktívni a potrebujú sa realizovať. Nikdy nedokážu robiť iba jednu aktivitu, má veľa záľub a aj veľa vedomostí. Majú záujem o veľa vecí naraz a len niekedy ich dokážu dokončiť. Valentín je typ človeka, ktorý rád riadi a diriguje ľudí vo svojom okolí. V citovej oblasti potrebuje pomoc od druhých a dosť veci požaduje od partnerky. Sú odjakživa žiarliví. Láska a nenávisť sa v nich rodia pomaly, ale sú trvalé. Zdravie majú stále, mali by si akurát dávať pozor na prechladnutia. Nerobia im dobre zmeny v stravovaní. Toto meno je vhodné pre Rakov, Býkov, Levov a Blížencov. Vhodnou farbou je modrá."},
                new NameEntity {Value = "Valér", NameDay = "18. apríl", Description = "Mužské meno Valér má latinský pôvod a význam mena je \"silný, zdravý\". Ich osobným číslom je 7 – Tajomnosť. Valérovia sú ako schizofrenici – niekedy sú šťastní a niekedy výbušní. Svoje vedomosti vedia dobre využiť a uplatniť. Za svojim cieľom si idú pevne, až kým nedosiahnu to, na čom im záleží. No niekedy to nie je možné, čo si Valérovia neuvedomujú. Ak sú nespokojní so svojou prácou, rýchlo menia názor a opúšťajú ju a takto budú robiť dokonca života. Ich problém je v tom, že sú impulzívni. Všetko chcú rýchlo získať, ale to im neprinesie úspech. Čo sa týka ich zdravia, mali by si dávať pozor na krvný tlak a pravidelnú stravu, aby nepriberali. Ich meno je vhodné pre znamenia : Rak, Panna, Kozorožec a Ryby. Ich šťastné farby sú strieborná, modrá, krémová a ochrannými rastlinami sú rumanček, jazmín, kamene nefrit, karneol."},
                new NameEntity {Value = "Vasil", NameDay = "14. jún", Description = "Mužské ruské a ukrajinské meno Vasil má grécky pôvod a jeho význam je \"kráľovský\". Osobným číslom tohto mena je 3 – Schopnosť. Ich obľúbené farby sú modrá, žltá, biela a zelená. Ochranné rastliny rumanček, medovka, repík, ochranné kamene perla a rubín. Vasilovia sú záhadní muži. Majú rýchle reakcie aj keď sa im niekedy nechce konať. Jediné čo im prekáža v ich úspechu je, že sú zahaľčiví a netrpezliví. Títo muži sú často zvedaví a radi skúmajú všetko okolo seba. Stávajú sa často politikmi, vyšetrovateľmi alebo právnikmi. Majú radi, keď môžu ľudí riadiť a hlavne nové dobrodružstvá, ale neradi sa vzdávajú svojich veci. Vasilovia sú mužmi, ktorí šetria, aby mali zaistia svoju finančnú budúcnosť. Títo muži sú zdravými ľuďmi a neboja sa viac pracovať. Mali by si ale dávať pozor na úrazy. Nemali by konať tvrdohlavo, ani zlostne. Prospeje im, ak obmedzia cukor a mastné jedlá. Toto meno je vhodné pre znamenia : Rak, Lev, Panna a Váhy."},
                new NameEntity {Value = "Vavrinec", NameDay = "10. august", Description = "Vavrinec je západoslovanské meno, prevzaté z latinčiny a jeho význam je \"pochádzajúci z mesta Lorenta\". Je mužom činu. Nikdy dlho nepremýšľa a neváha. Má v sebe veľa intuície, dokáže sa vždy vynájsť, vybrať si tú správnu cestu a dosiahnuť to, po čom túži. Nemá problém s presadením vlastných nápadov a myšlienok. Ak chce, dosiahne aj vysoké ciele. Vie využívať nadanie iných a spolupracovať aj s veľmi konfliktnými typmi. V láske sa mu nadmieru darí. Nevie oceniť úprimné a vášnivé city, pretože vždy mu stačí len málo času, aby dosiahol svoje. Keď sa však ožení, je verný a vždy sa o svojich postará. Ovplyvňuje ho planéta Merkúr. Jeho ochranné kamene sú zafír a citrín."},
                new NameEntity {Value = "Vendelín", NameDay = "20. október", Description = "Mužské meno Vendelín je odvodené z nemeckého mena Wendelmar a znamená \"polabský Slovan\". Nemá problém nájsť si cestičku k peniazom a blahobytu. Dobre sa orientuje aj v zložitej finančnej situácii a využíva všetky možnosti na to, aby sa zabezpečil. Na prvom mieste je pre neho osobná stabilita a dostatočné pohodlie. Má rád okolo seba schopných a výkonných ľudí a ak je v jeho rodine dostatok takých, vybuduje rodinný podnik, ktorý bude prosperovať dlhé roky. Všeobecne rád buduje, stavia, plánuje a žiadnu prácu nepovažuje za zbytočnú, alebo nepotrebnú. Využíva sám seba a aj iných, ale vždy pre dobro spoločenstva, nie iba osobne. Ovplyvňujú ho planéty Saturn a Urán. Jeho ochranné kamene sú sokolie oko a jaspis."},
                new NameEntity {Value = "Viktor", NameDay = "26. február", Description = "Mužské meno Viktor má latinský pôvod a jeho význam v preklade je \"víťaz\". Ich osobným číslom je 3 – Schopnosť. Ich šťastné farby sú zelená, hnedá, oranžová. Ochranné rastliny čakanka, nevädza a konvalinka, ochranné kamene jaspis a citrinAby Viktorovia niečo dosiahli, musia silno bojovať a snažiť sa o úspech, čo ich dokáže dosť vyčerpať. Ale majú dostatok šťastia, preto by sa boja nemali zrieknuť. Viktorovia sú pokojní ľudia a charakteroví, ale niekedy dokážu byť dosť aktívni. Preto usilovne a svedomito dokážu konať, i keď nemajú vo veciach poriadok. Na pomoc od druhých si celkom počkajú, aj keď sú v kolektíve obľúbení a populárni. Aj keď sa ťažko prepracujú k úspechu, ich námaha bude ocenená, len musia byť trpezliví. Viktorovia ako muži sú v láske veľmi citliví, vnímavý a dostatočne oddaní. Radi študujú a aj keď radi na seba upozorňujú, učitelia ich majú v láske. Najlepšie im ide klasické štúdium a hlavne cudzie jazyky. Môžu byť dobrými profesormi, ale aj vysokými úradníkmi. Čo sa týka ich zdravia, mali by sa zamerať na potraviny, v ktorých je dostatok draslíka. Toto meno je vhodné pre znamenia : Panna, Kozorožec, Ryby, Rak, Škorpión."},
                new NameEntity {Value = "Viliam", NameDay = "28. máj", Description = "Mužské meno Viliam má nemecký pôvod a jeho význam je \"muž s pevnou vôľou\". Ovplyvňuje ich planéta Saturn. Ich obľúbené farby sú hnedá, zelená a čierna, ochranná rastlina kostihoj, ochranné kamene zafír a achát. Osobné číslo tohto mena je 9 – génius a predurčuje mu úspech. Viliamom vyjde všetko, čo si zaumienia aj keď je to niekedy na poslednú chvíľu. Tkvie v ňom akési  čaro osobnosti  a schopnosť plniť všetky úlohy, ktoré ho čo len trošku tešia. Vyžaruje neodolateľnou príťažlivosťou  Jeho nápady sú ľahko uskutočniteľné a výnosné  a keď nájde podporu vo svojej rodine, čoskoro zbohatne. Mal by sa učiť čím viac rečí, snažiť sa o udržiavanie svojej fyzickej kondičky a byť vždy pripravený na rýchle zmeny. Len ak bude ku každému príjemný, dosiahne vysoké postavenie aj bohatstvo, preto by mal tiež udržiavať kontakty aj so staršími mužmi či ženami. Nesmie sa správať odmietavo ani ku obyčajným ľuďom. V láske je prelietavý a kým sa rozhodne usadiť sa, môže stratiť zopár šancí na spolužitie s tou pravou. Toto meno je vhodné pre znamenie Kozorožca, Vodnára, Býka."},
                new NameEntity {Value = "Vincent", NameDay = "21. január", Description = "Mužské meno Vincent má latinský pôvod a v preklade znamená \"víťaziaci\". Osobným číslom tohto mena je 2 – Dobrota. Ich obľúbené farby sú zelená, biela a modrá, ochranné rastliny nechtík a šalvia, ochranné kamene smaragd, ruženín a avanturín. Títo muži sú nenápadní ľudia, o ktorých sa veľa nehovorí. Iba občas dokážu vybuchnúť a často menia svoju náladu. Niekedy nevedia sami, akí vlastne sú. Občas sú plní sily a pohybu a na druhej strane niekedy sú smutní a mrzutí. Ľudia sa nimi riadia a Vincentovia dokážu zmeniť názory iných. Vincentovia dokážu dosiahnuť všetko, po čom túžia. No niekedy sú pomalší a nepúšťa sa radšej do ťažších úloh. V niektorých veciach sú pesimistickí, všetko im je náročné a len málokedy sa vedia uvoľniť. Niektorí ľudia nevedia nájsť k nemu cestu a nerozumejú im. Keďže títo muži sú plní energie, už aj od malička radi študujú a hlavne niečo výnimočné, Radi sa realizujú v aktivitách, kde môžu využiť svoje tušenie. Ich vysoká inteligencia robí z nich vodcov, ktorí všetko riadia zo zákulisia. Aj keď nemajú pravidelnú stravu, ich zdravie je v celku dobré. Mali by niekedy viac pridávať k jedlu vitamíny. Je to prijateľné meno pre každé znamenie, len jeho nositeľ musí odolať alkoholu a všetko bude v poriadku."},
                new NameEntity {Value = "Vít", NameDay = "15. jún", Description = "Osobným číslom tohto mena je 6 - Nežnosť. Farba, ktorá sa ku nim hodí je zelená. Títo muži sú veľmi citliví a jemní. Vždy sa správajú ako čestní ľudia a nikomu by nepriali nič zlé. Ale vždy bude za pomoc požadovať odmenu a nič neurobí zadarmo. Nemajú radi ak ich ľudia ohovárajú a klamú, preto si aj precízne vyberá priateľov a blízkych. Vždy si pevne premyslia ako budú konať. Vitovia sú vytrvalí a jednajú pomaly a isto, aby sa dopracovali k svojmu vytúženému úspechu. Ak budete priateľom Vita, nemusíte sa báť, že vás zradí alebo opustí alebo že vám niekto ublíži, ak sa budete samozrejme správať podľa toho ako si to Vítovia predstavujú. Toto meno je vhodné pre Býkov, Strelcov, Baranov a Vodnárov."},
                new NameEntity {Value = "Víťazoslav", NameDay = "20. marec", Description = "Mužské meno Víťazoslav je novšie slovenské meno a jeho význam je \"slávny víťaz\". Ovplyvňuje ich planéta Jupiter. Obľúbené farby majú svetlomodrú, zelenú a bielu. Ochranné rastliny rimbaba a slez, kamene tyrkys, chalcedón. Osobným číslom pre toto meno je 6 - Nežnosť. Títo muži sú musia vždy dokazovať, ako si veria. Víťazoslavovia pôsobia ako pokojní a optimistickí ľudia. Nikdy nie sú zameraní iba na jednu oblasť svojich záujmov, ale vďaka tomu, že sú inteligentní, budú dosahovať dobré výsledky. Priťahujú ich povolania, ktoré im umožňujú cestovať, ako hudobník či obchodný zástupca. Ak budú využívať svoju intuíciu, tak určite budú úspešní v každej oblasti zamestnania. No ak budú v kontakte s nesprávnymi ľuďmi, ktorí ich budú využívať, tak sa úspechu určite nedočkajú. Toto meno je vhodné pre znamenia, ktoré vedia konať rýchlo : Blíženci, Baran, Škorpión."},
                new NameEntity {Value = "Vladimír", NameDay = "24. júl", Description = "Mužské meno Vladimír má slovanský pôvod a jeho význam je \"vládni svetu\", \"veľký vládca\". Osobným číslom tohto mena je 2 – Dobrota. Ovplyvňuje ich planéta Jupiter. Šťastné farby sú modrá, biela a zelená. Ochranné rastliny žihľava, skalnica a šalvia, kamene ametyst a smaragd. Títo muži sú dobrí, oddaní, pohotoví, súcitní a jemní. Majú v sebe veľa citu. Lenže nevedia konať premyslene. Vtedy sú impulzívni a dokážu povedať všetko, čo majú na srdci. Kvôli tomu sú v mladosti nenávidení. Majú sklon kritizovať a nariaďovať druhým, čo musia urobiť a ako sa správať. Ich túžba po dokonalosti je povestná. Preto by mali mať toto meno len silné znamenia, ktoré ich dokážu zvládnuť. V spoločnosti nie sú veľmi obľúbení, lebo väčšina ľudí v nich vidí konkurenciu. Zdravie majú celkom dobré, ale veľmi podliehajú psychike. Rýchlo sa dokážu unaviť.Toto meno je vhodné pre Baranov, Býkov, Kozorožcov a Panny."},
                new NameEntity {Value = "Vladislav", NameDay = "25. september", Description = "Mužské meno Vladislav má slovanský pôvod a v preklade znamená \"sláviaci vládu\". Je tvrdohlavý, nikdy nedá pokoj svojmu osudu, snaží sa ho zmeniť a upraviť. Bojuje a nevzdáva sa, nikdy neviete, čo spraví skôr a ako sa zachová. Je veľmi sebecký a všetko si dvakrát premyslí, len aby sa vyhol zbytočnému plytvaniu svojím časom. Býva náladový a aj v citoch sa mení ako aprílové počasie. Chvíľu ste jeho obľúbencom, potom nie, raz vám podá pomocnú ruku a o chvíľu vás nechá potopiť sa. Aj keď býva málo chápavý, niekedy v strede jeho života ho stretne udalosť, ktorá zmení jeho názory na svet a začne konať inak, ľudskejšie a súcitnejšie. Ovplyvňuje ho planéta Mars. Jeho ochranné kamene sú diamant a jaspis."},
                new NameEntity {Value = "Vlastimil", NameDay = "13. marec", Description = "Mužské meno Vlastimil má slovanský pôvod a znamená \"milujúci moc, vládu\". Obľúbené farby týchto mužov sú žltá, tmavomodrá, ochranné rastliny figovník, puškvorec, ochranný kameň granát. Toto meno má osobné číslo 5 - Riziko. Títo muži pôsobia ako rozumní ľudia. Radi riskujú a bojujú o svoje ciele. Nikdy sa nevzdajú bez toho, aby bojovali, po čom túžia. Vždy vedia, ako majú konať v danej situácií. V živote majú šťastie, preto žijú pokojný a úspešný život. Aj keď sa v iných sklamú, iba ich to posilní v ďalších krokoch v živote. No občas sa uzatvárajú do svojho sveta, ale nie na dlhý čas. Stávajú sa z nich profesori alebo odborní psychológovia. Vhodnými znameniami sú : Rak, Panna, Kozorožec, Býk, Lev."},
                new NameEntity {Value = "Vojtech", NameDay = "23. apríl", Description = "Mužské meno Vojtech má slovanský pôvod a význam mena je \"tešiteľ vojska\". Osobným číslom tohto mena je 7 - Tajomnosť. Vojtechovia sú aktívni a rozvážni ľudia. Majú dvojitú osobnosť a zakaždým sa ináč správajú. Na jednej strane je to osobná stránka a na druhej verejná. V každom prípade je však tvrdohlavý a neústupčivý, nedokáže prijať kritiku a nezabúda na urážky. Títo muži si vedia dobre naplánovať a uskutočniť svoje povinnosti. V zamestnaní Vojtechovia sú veľmi pracovití a úspešní. Majú dobré nápady a rýchlo sa prispôsobia. Často sa z nich stávajú bádatelia alebo projektanti. Životaschopnosť majú dobrú, odolávajú chorobám, ale mali by si dávať pozor na tráviaci systém. V strave by mali obmedziť cukor a živočíšne tuky, jesť veľa rýb a cesnak. Toto meno je vhodné pre znamenia : Škorpión, Kozorožec, Váhy, Ryby a Vodnár. Ich obľúbená farba je tmavomodrá, ochranné rastliny puškvorec, bazalka, ochranné kamene rubín, diamant a topás."},
                new NameEntity {Value = "Vratislav", NameDay = "18. jún", Description = "Mužské meno Vratislav má slovanský pôvod a význam mena je \"oslavujúci návrat\" alebo \"ten, ktorý vráti slávu\". Jeho osobným číslom je 3 – Schopnosť. Ich šťastné farby sú červená a fialová. Ochranné rastliny púpava, chmeľ a horčica, ochranné kamene diamant a chalcedón. Vratislavovia sú dvojtvárni. Vedia dobre posúdiť postoj druhých a majú zmysel pre porozumenie chýb iných. Medzi ľuďmi sú známi ako necitliví muži, ktorí ovládajú situáciu. V skutočnosti sú opakom – sú citliví a emotívni. Vratislavovia nie sú typmi, ktorí by ovplyvňovali druhých, nechá ľudí voľne sa rozhodovať. Sú dosť obetaví a idú si vždy za svojím cieľom. Ochotne vždy pomôžu ak to niekto bude potrebovať. Vitalita týchto mužov je výborná, ale majú horší zrak. V jedle by mali obmedzovať masť, maslo, vajíčka a cukor. Práve kvôli flexibilite je toto meno vhodné pre každé znamenie, ak chceme jeho energiu ešte viac zdôrazniť."},
                new NameEntity {Value = "Vratko", NameDay = "29. november", Description = "Mužské meno Vratko je slovenské a je pôvodnou domácou podobou slovanského mena Vratislav. V preklade znamená \"vráti slávu\". Jeho budúcnosť je plná prekvapení a šancí, ktoré by ho mohli vyniesť až do tých najvyšších sfér. Je nerozhodný a pomalý. Ak by sa viac snažil a nebál sa rizika, mohol by uskutočniť svoje plány oveľa skôr. On by rád postupoval krok za krokom a nechápe sám seba, svoje možnosti. Veľmi sa podceňuje a sám si škodí svojimi komplexmi. Preto by si vždy mal nájsť priateľov, v ktorých by našiel pomoc a podporu a pochopenie. Pokiaľ ide o ženy, tu by mal byť veľmi opatrný a snažiť sa čím menej zaväzovať v mladosti. Ovplyvňuje ho planéta Mars. Jeho ochranné kamene sú diamant a chalcedón."},
                new NameEntity {Value = "Zdenko", NameDay = "9. február", Description = "Mužské meno Zdenko má český pôvod a vo voľnom preklade znie ako \"tu slávny\", \"oslavujúci dielo\". Jeho osobným číslom je 3 - Schopnosť. Vhodná farba je oranžová a modrá. Ochranné rastliny čakanka, fenikel, ochranné kamene jaspis a nefrit. Zdenkovia sú zodpovední a presní. Veci, do ktorých sa pustia sa snažia spraviť správne, bezchybne a dotiahnuť ich do konca.Keď mu chce niekto pomôcť, neodolá ponuke.Títo muži sa dajú rýchlo ovplyvniť a sú dosť citliví. Ale o svojich citoch a problémoch nepovie iným. Ich nálady sú striedavé, niekedy chcú žiť v samote, a inokedy by chceli byť obklopení blízkymi. Ich myšlienky záležia od okolností.V živote majú dosť príležitostí, ale ich obozretnosťou to dokážu rýchlo stratiť, preto často dokážu byť nestabilní a podliehajú k depresiám.Priateľov si rýchlo získajú, ale najbližšie mu je rodina. Ľuďom vo svojej blízkosti nedôveruje, ale pre rodinu spraví všetko a hlavne zabezpečiť ich finančnú stabilitu.Vyhýba sa ľudom, ktorí dokážu ovplyvniť ostatných. Lásku nedáva najavo ani svoju nežnosť ale milovať dokáže verne a horlivo. Ovplyvňuje ich planéta Merkúr. Toto meno je vhodné pre znamenia Kozorožec, Býk, Rak, Panna."},
                new NameEntity {Value = "Žigmund", NameDay = "2. máj", Description = "Mužské meno Žigmund má nemecký pôvod a v preklade znamená \"ochranca víťazstva\". Osobným číslom tohto mena je 7 - Tajomnosť. Ich obľúbené farby sú fialová, červená, žltá, modrá a biela, ochranné rastliny žihľava, rimbaba, skalnica, ochranné kamene ametyst, smaragd. Muži s týmto menom sú spoločenskí, ktorí si vždy vedia nájsť priateľov. No občas ľudia o nich dlho nepočujú, lebo potrebujú pokoj a byť osamote, ale neradi sa nudia. Sú to ľudia, ktorí prajú ostatným len dobre a vždy sa snažia pomôcť. Žigmundovia majú problém niekomu dôverovať, často ľudí podozrievajú. V živote nie sú stabilní, ale zato vedia čo chcú a postupne si zodpovedne vybudujú svoj život. Ich inteligencia je dobrá, preto sa vedia aj vysoko vypracovať. V zamestnaní sú presný, trpezliví a úspešní. Vždy pracujú tak, aby dosiahli to, po čom túžia. Toto meno je vhodné pre znamenia : Strelec, Baran, Býk, Kozorožec, Váhy a Vodnár."},
                new NameEntity {Value = "Zlatko", NameDay = "12. jún", Description = "S nositeľmi mužského mena Zlatko sa na Slovensku stretávame len výnimočne. Jeho pôvod je slovanský a význam tohto mena je \"zlatý\". Toto meno má osobné číslo 5 - Riziko. Ich obľúbené farby sú prírodná zelená, biela a žltá. Ochranné rastliny nechtík, mäta, podbeľ, ochranné kamene avanturín, zafír. Toto meno na mužov vplýva pozitívne a charakterizuje bezpečie a optimizmus. Sú to väčšinou šťastní ľudia a majú v sebe veľa dobrých síl. Zlatkovia sú skôr pokojnými ale dokážu byť aj temperamentní. U týchto mužov neuvidíme výraznú citlivosť. Zlatkovia vedia, čo v živote chcú dosiahnuť. Ak sa budú spoliehať iba na seba a svoje pocity, tak budú mať úspechy. Ak sa u nich vyskytnú nejaké ťažkosti, vždy majú pri sebe nejakého človeka, ktorý ho podporia. Ich zdravie je celkom v poriadku, ale rýchlo sa unavia a často trpia prepracovanosťou, čo im neprospieva. Ide o labužníkov, radi si dobre zajedia. Mali by sa vystríhať odmeranosti a pohodlnosti. Toto meno je vhodné pre znamenia Lev, Váhy, Baran a Strelec."},
                new NameEntity {Value = "Zoltán", NameDay = "7. apríl", Description = "Mužské meno Zoltán má turecký pôvod a význam mena je \"moc\". Osobným číslom tohto mena je 9 – Génius. Zoltán je celkom nadaný a talentovaný človek, ktorý ale niekedy nevie využiť svoje správne prednosti. Často zabudne na podstatné udalosti a niekedy má problém sa nevie dobre sústrediť. Napriek tomu je úrpimnou a priamou osobnosťou. Zoltán sa nevie hnevať, ale ak k tomu príde, tak to stojí za to. Ak sa chce pre niečo rozhodnúť, urobí pre to všetko, aby dosiahol svoje ciele a sny. Ale ak mu v tom stojí jeho hrdosť, dokáže to zničiť jeho snahu. Toto meno je vhodné pre znamenia : Lev, Baran, Vodnár, Strelec a vhodnou farbou je modrá, žltá a medená. Ochranné rastliny mak, nechtík a rumanček, ochranné kamene kryštál, granát a ónyx."},
